import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Zap, Target, Clock, TrendingUp } from 'lucide-react';

const TaskStats = ({ tasks, taskLists }) => {
  const [stats, setStats] = useState({
    completionRate: 0,
    streakDays: 0,
    averageCompletionTime: 0,
    powerLevel: 0
  });

  useEffect(() => {
    calculateStats();
  }, [tasks, taskLists]);

  const calculateStats = () => {
    if (!tasks || tasks.length === 0) {
      setStats({
        completionRate: 0,
        streakDays: 0,
        averageCompletionTime: 0,
        powerLevel: 0
      });
      return;
    }

    // Completion Rate
    const totalTasks = tasks.length;
    const completedTasks = tasks.filter(t => t.completed).length;
    const completionRate = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

    // Streak Calculation - more realistic
    const streakDays = calculateStreak(completedTasks);

    // Average Completion Time (mock calculation - more reasonable)
    const averageCompletionTime = completedTasks > 0 ? Math.round(1 + Math.random() * 3) : 0;

    // Power Level Calculation - Much more conservative
    const powerLevel = calculatePowerLevel(completedTasks, completionRate, streakDays, tasks);

    setStats({
      completionRate,
      streakDays,
      averageCompletionTime,
      powerLevel
    });
  };

  const calculateStreak = (completedCount) => {
    // More realistic streak calculation
    if (completedCount === 0) return 0;
    if (completedCount === 1) return 1;
    if (completedCount <= 3) return 2;
    if (completedCount <= 7) return 3;
    if (completedCount <= 15) return Math.floor(completedCount / 3);
    if (completedCount <= 30) return Math.floor(completedCount / 2);
    return Math.min(Math.floor(completedCount / 1.5), 30); // Cap at 30 days
  };

  const calculatePowerLevel = (completed, completionRate, streak, allTasks) => {
    let power = 0;

    // Base power from completed tasks - primary source of XP
    power += completed * 10; // 10 XP per completed task

    // Completion rate bonus - only with significant volume (10+ tasks)
    if (allTasks.length >= 10) {
      power += Math.round(completionRate * 0.8); // Small bonus for high completion rate
    }

    // Streak bonus - meaningful streaks only (7+ days)
    if (streak >= 7) {
      power += (streak - 6) * 8; // Decent bonus for real streaks
    }

    // High priority completion bonus - only with volume (10+ completed)
    if (completed >= 10) {
      const highPriorityCompleted = allTasks.filter(t => t.completed && t.priority === 'high').length;
      power += highPriorityCompleted * 12; // Bonus for tackling hard tasks
    }

    // Project diversity bonus - only with significant usage (20+ tasks)
    if (allTasks.length >= 20) {
      const projectsUsed = new Set(allTasks.filter(t => t.taskListId).map(t => t.taskListId)).size;
      power += projectsUsed * 15; // Good bonus for organization
    }

    // Tag usage bonus - only with consistent tagging (15+ completed)
    if (completed >= 15) {
      const tasksWithTags = allTasks.filter(t => t.completed && t.tags && t.tags.trim()).length;
      power += tasksWithTags * 5; // Organization bonus
    }

    // Volume milestone bonuses
    if (completed >= 100) power += 100; // Century bonus
    if (completed >= 50) power += 50;   // Half-century bonus
    if (completed >= 25) power += 25;   // Quarter-century bonus

    return Math.max(0, Math.round(power));
  };

  const getPowerLevelRank = (power) => {
    if (power >= 1200) return { rank: 'LEGENDARY', color: '#FFD700', glow: 'rgba(255, 215, 0, 0.4)' };
    if (power >= 800) return { rank: 'MASTER', color: '#8B5CF6', glow: 'rgba(139, 92, 246, 0.4)' };
    if (power >= 500) return { rank: 'EXPERT', color: '#10B981', glow: 'rgba(16, 185, 129, 0.4)' };
    if (power >= 250) return { rank: 'SKILLED', color: '#3B82F6', glow: 'rgba(59, 130, 246, 0.4)' };
    if (power >= 150) return { rank: 'TRAINED', color: '#F59E0B', glow: 'rgba(245, 158, 11, 0.4)' };
    if (power >= 75) return { rank: 'NOVICE', color: '#06B6D4', glow: 'rgba(6, 182, 212, 0.4)' };
    return { rank: 'ROOKIE', color: '#6B7280', glow: 'rgba(107, 114, 128, 0.4)' };
  };

  const getStreakRank = (days) => {
    if (days >= 20) return { rank: 'UNSTOPPABLE', color: '#FFD700' };
    if (days >= 14) return { rank: 'LEGENDARY', color: '#8B5CF6' };
    if (days >= 10) return { rank: 'MASTER', color: '#10B981' };
    if (days >= 7) return { rank: 'SOLID', color: '#3B82F6' };
    if (days >= 3) return { rank: 'BUILDING', color: '#F59E0B' };
    if (days >= 1) return { rank: 'STARTING', color: '#06B6D4' };
    return { rank: 'INACTIVE', color: '#6B7280' };
  };

  const getCompletionRank = (rate) => {
    if (rate >= 95) return { rank: 'PERFECT', color: '#FFD700' };
    if (rate >= 85) return { rank: 'EXCELLENT', color: '#10B981' };
    if (rate >= 75) return { rank: 'GOOD', color: '#3B82F6' };
    if (rate >= 60) return { rank: 'DECENT', color: '#F59E0B' };
    if (rate >= 40) return { rank: 'IMPROVING', color: '#EF4444' };
    return { rank: 'LEARNING', color: '#6B7280' };
  };

  const getSpeedRank = (days) => {
    if (days === 0) return { rank: 'UNKNOWN', color: '#6B7280' };
    if (days === 1) return { rank: 'LIGHTNING', color: '#FFD700' };
    if (days === 2) return { rank: 'FAST', color: '#10B981' };
    if (days === 3) return { rank: 'STEADY', color: '#3B82F6' };
    if (days <= 5) return { rank: 'CAREFUL', color: '#F59E0B' };
    return { rank: 'METHODICAL', color: '#6B7280' };
  };

  const powerRank = getPowerLevelRank(stats.powerLevel);
  const streakRank = getStreakRank(stats.streakDays);
  const completionRank = getCompletionRank(stats.completionRate);
  const speedRank = getSpeedRank(stats.averageCompletionTime);

  // Calculate next rank threshold
  const getNextRankThreshold = (power) => {
    if (power < 75) return 75;
    if (power < 150) return 150;
    if (power < 250) return 250;
    if (power < 500) return 500;
    if (power < 800) return 800;
    if (power < 1200) return 1200;
    return 1200; // Max
  };

  const nextThreshold = getNextRankThreshold(stats.powerLevel);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.2 }}
      className="bg-gray-800 border-2 border-cyan-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 relative"
      style={{
        boxShadow: '0 0 20px rgba(34, 211, 238, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
      }}
    >
      <div className="absolute inset-0 border-2 border-cyan-400 opacity-50 animate-pulse pointer-events-none" />

      <h3 className="text-lg font-mono font-bold text-white flex items-center mb-6">
        <div className="w-4 h-4 bg-purple-400 mr-2" />
        BATTLE STATISTICS
      </h3>

      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
        {/* Power Level */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between mb-2">
            <div>
              <div className="text-xs font-mono text-gray-400">POWER LEVEL</div>
              <div className="text-2xl font-mono font-bold text-white">
                {stats.powerLevel}
              </div>
            </div>
            <Zap className="text-cyan-400" size={24} />
          </div>
          <div 
            className="text-xs font-mono font-bold px-2 py-1 border border-cyan-400 bg-cyan-400 bg-opacity-20 text-cyan-400"
          >
            {powerRank.rank}
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* Completion Rate */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between mb-2">
            <div>
              <div className="text-xs font-mono text-gray-400">SUCCESS RATE</div>
              <div className="text-2xl font-mono font-bold text-white">
                {stats.completionRate}%
              </div>
            </div>
            <Target className="text-cyan-400" size={24} />
          </div>
          <div className="text-xs font-mono font-bold px-2 py-1 border border-cyan-400 bg-cyan-400 bg-opacity-20 text-cyan-400">
            {completionRank.rank}
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* Streak */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between mb-2">
            <div>
              <div className="text-xs font-mono text-gray-400">KILL STREAK</div>
              <div className="text-2xl font-mono font-bold text-white">
                {stats.streakDays}
              </div>
            </div>
            <TrendingUp className="text-cyan-400" size={24} />
          </div>
          <div className="text-xs font-mono font-bold px-2 py-1 border border-cyan-400 bg-cyan-400 bg-opacity-20 text-cyan-400">
            {streakRank.rank}
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* Average Time */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between mb-2">
            <div>
              <div className="text-xs font-mono text-gray-400">CLEAR SPEED</div>
              <div className="text-2xl font-mono font-bold text-white">
                {stats.averageCompletionTime > 0 ? `${stats.averageCompletionTime}d` : 'N/A'}
              </div>
            </div>
            <Clock className="text-cyan-400" size={24} />
          </div>
          <div className="text-xs font-mono font-bold px-2 py-1 border border-cyan-400 bg-cyan-400 bg-opacity-20 text-cyan-400">
            {speedRank.rank}
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>
      </div>

      {/* Power Level Progress Bar */}
      <div className="mt-6 pt-4 border-t border-gray-700">
        <div className="flex items-center justify-between mb-2">
          <span className="text-sm font-mono text-gray-400">NEXT RANK:</span>
          <span className="text-sm font-mono text-cyan-400">
            {stats.powerLevel < 1200 ? `${nextThreshold - stats.powerLevel} XP needed` : 'MAX LEVEL'}
          </span>
        </div>
        <div className="w-full bg-gray-700 h-2 border border-gray-600">
          <div 
            className="h-full transition-all duration-1000 bg-gradient-to-r from-cyan-400 to-purple-500"
            style={{ 
              width: `${Math.min((stats.powerLevel / nextThreshold) * 100, 100)}%`
            }}
          />
        </div>
      </div>

      {/* Achievement Indicators */}
      <div className="mt-4 flex flex-wrap gap-2">
        {stats.streakDays >= 7 && (
          <div className="flex items-center gap-1 px-2 py-1 bg-cyan-400 bg-opacity-20 border border-cyan-400">
            <TrendingUp size={12} className="text-cyan-400" />
            <span className="text-xs font-mono text-cyan-400">WEEK WARRIOR</span>
          </div>
        )}

        {stats.completionRate >= 90 && tasks.length >= 5 && (
          <div className="flex items-center gap-1 px-2 py-1 bg-cyan-400 bg-opacity-20 border border-cyan-400">
            <Target size={12} className="text-cyan-400" />
            <span className="text-xs font-mono text-cyan-400">PERFECTIONIST</span>
          </div>
        )}

        {stats.powerLevel >= 150 && (
          <div className="flex items-center gap-1 px-2 py-1 bg-cyan-400 bg-opacity-20 border border-cyan-400">
            <Zap size={12} className="text-cyan-400" />
            <span className="text-xs font-mono text-cyan-400">ELITE FORCE</span>
          </div>
        )}
      </div>
    </motion.div>
  );
};

export default TaskStats;
