import React from 'react';
import { motion } from 'framer-motion';
import { FileText, Archive, Trophy, User } from 'lucide-react';

const TabNavigation = ({ activeTab, onTabChange }) => {
  const tabs = [
    {
      id: 'notes',
      label: 'MISSIONS',
      icon: <FileText size={18} />,
      description: 'Mission Control Center'
    },
    {
      id: 'library',
      label: 'VAULT',
      icon: <Archive size={18} />,
      description: 'Data Archive System'
    },
    {
      id: 'achievements',
      label: 'ACHIEVEMENTS',
      icon: <Trophy size={18} />,
      description: 'Honor & Commendations'
    },
    {
      id: 'profile',
      label: 'PROFILE',
      icon: <User size={18} />,
      description: 'Operator Status'
    }
  ];

  return (
    <nav className="border-b-2 border-cyan-400 bg-gray-800 px-6 py-4">
      <div className="flex space-x-1">
        {tabs.map((tab) => {
          const isActive = activeTab === tab.id;

          return (
            <motion.button
              key={tab.id}
              onClick={() => onTabChange(tab.id)}
              className={`relative px-6 py-3 font-mono font-bold text-sm border-2 transition-all duration-300 ${
                isActive
                  ? 'bg-cyan-400 border-cyan-400 text-black shadow-[4px_4px_0px_0px_rgba(0,0,0,1)]'
                  : 'bg-gray-900 border-cyan-400 text-cyan-400 hover:bg-gray-700 hover:shadow-[2px_2px_0px_0px_rgba(0,0,0,1)]'
              }`}
              style={{
                boxShadow: isActive 
                  ? '0 0 15px rgba(34, 211, 238, 0.4), 4px 4px 0px 0px rgba(0,0,0,1)'
                  : '0 0 5px rgba(34, 211, 238, 0.2), 1px 1px 0px 0px rgba(0,0,0,1)'
              }}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              title={tab.description}
            >
              <div className="flex items-center gap-2">
                {tab.icon}
                <span>{tab.label}</span>
              </div>

              {isActive && (
                <motion.div
                  className="absolute inset-0 border-2 border-cyan-400 opacity-30 animate-pulse pointer-events-none"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 0.3 }}
                  transition={{ duration: 0.5 }}
                />
              )}
            </motion.button>
          );
        })}
      </div>
    </nav>
  );
};

export default TabNavigation;
