import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ArrowLeft, Save, Plus, Folder, Palette, X } from 'lucide-react';
import { useNotification } from '../../contexts/NotificationContext';

const FolderModal = ({ 
  isOpen, 
  onClose, 
  onSave, 
  existingFolder = null 
}) => {
  const [folderData, setFolderData] = useState({
    name: '',
    description: '',
    colorCode: '#FFD700'
  });

  const { showNotification } = useNotification();
  const isEditing = !!existingFolder;
  const modalTitle = isEditing ? 'MODIFY ARCHIVE SYSTEM' : 'INITIALIZE ARCHIVE SYSTEM';

  // Color options matching NotebookModal
  const colors = [
    '#FFD700', '#FF6B6B', '#4ECDC4', '#45B7D1', 
    '#96CEB4', '#FECA57', '#FF9FF3', '#54A0FF',
    '#5F27CD', '#00D2D3', '#FF9F43', '#EE5A24',
    '#009432', '#0652DD', '#9980FA', '#EA2027'
  ];

  useEffect(() => {
    if (existingFolder) {
      setFolderData({
        name: existingFolder.name || '',
        description: existingFolder.description || '',
        colorCode: existingFolder.colorCode || '#FFD700'
      });
    } else {
      setFolderData({
        name: '',
        description: '',
        colorCode: '#FFD700'
      });
    }
  }, [existingFolder, isOpen]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!folderData.name.trim()) {
      showNotification('Archive name is required', 'error');
      return;
    }

    try {
      if (isEditing) {
        await onSave(existingFolder.id, folderData);
        showNotification(`Archive "${folderData.name}" modified successfully!`, 'success');
      } else {
        await onSave(folderData);
        showNotification(`Archive "${folderData.name}" initialized successfully!`, 'success');
      }
      onClose();
    } catch (error) {
      console.error('Failed to save archive:', error);
      showNotification('Failed to save archive. Please try again.', 'error');
    }
  };

  // Handle keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!isOpen) return;

      if (e.key === 'Escape') {
        onClose();
      } else if ((e.ctrlKey || e.metaKey) && e.key === 'Enter') {
        e.preventDefault();
        document.getElementById('archive-form')?.requestSubmit();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isOpen, onClose]);

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-gray-900 z-50 flex flex-col"
        >
          {/* Header Bar - Matching NotebookModal */}
          <motion.div 
            initial={{ y: -50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            className="bg-gray-800 border-b-2 border-cyan-400 p-4 flex items-center justify-between flex-shrink-0"
            style={{
              boxShadow: '0 2px 20px rgba(34,211,238,0.3)'
            }}
          >
            <div className="flex items-center gap-4">
              <button
                onClick={onClose}
                className="bg-gray-900 border-2 border-cyan-400 p-2 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] text-cyan-400"
                style={{
                  boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
                }}
                title="Back to Library (ESC)"
              >
                <ArrowLeft size={20} className="text-cyan-400" />
                <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
              </button>

              <div className="flex items-center gap-3">
                <h1 className="font-mono text-2xl font-bold text-white">
                  {modalTitle}
                </h1>
              </div>
            </div>

            <div className="flex items-center gap-3">
              <div className="text-xs font-mono text-gray-400 hidden sm:flex gap-4">
                <span>ESC: Back</span>
                <span>CTRL+ENTER: Save</span>
              </div>

              <button
                type="submit"
                form="archive-form"
                className="bg-gray-900 border-2 border-cyan-400 px-6 py-2 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] font-mono font-bold text-cyan-400"
                style={{
                  boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
                }}
              >
                <div className="flex items-center gap-2">
                  {isEditing ? <Save size={18} className="text-cyan-400" /> : <Plus size={18} className="text-cyan-400" />}
                  <span className="text-cyan-400">{isEditing ? 'SAVE CHANGES' : 'INITIALIZE ARCHIVE'}</span>
                </div>
                <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
              </button>
            </div>
          </motion.div>

          {/* Content Area - Matching NotebookModal */}
          <div className="flex-1 overflow-hidden flex flex-col">
            <motion.div 
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.1 }}
              className="max-w-4xl mx-auto flex-1 p-6 overflow-hidden flex flex-col w-full"
            >
              <form id="archive-form" onSubmit={handleSubmit} className="h-full flex flex-col space-y-6">

                {/* Title Input */}
                <div className="flex-shrink-0">
                  <input
                    value={folderData.name}
                    onChange={(e) => setFolderData({ ...folderData, name: e.target.value })}
                    placeholder="Enter archive name..."
                    className="w-full text-4xl font-mono font-bold bg-transparent border-none outline-none text-white placeholder-gray-500 p-0"
                    style={{ 
                      color: '#ffffff !important',
                      WebkitTextFillColor: '#ffffff',
                      textFillColor: '#ffffff'
                    }}
                    required
                    autoFocus
                  />
                  <div className="h-1 bg-gradient-to-r from-cyan-400 to-purple-400 mt-3" />
                </div>

                {/* Main Content Grid */}
                <div className="flex-1 grid grid-cols-1 lg:grid-cols-4 gap-6 overflow-hidden">

                  {/* Description Area */}
                  <div className="lg:col-span-3 flex flex-col overflow-hidden">
                    <div className="bg-gray-800 border-2 border-cyan-400 shadow-[4px_4px_0px_0px_rgba(0,0,0,1)] relative flex-1 flex flex-col overflow-hidden">
                      <div className="absolute inset-0 border-2 border-cyan-400 opacity-20 animate-pulse pointer-events-none" />

                      {/* Header */}
                      <div className="flex items-center justify-between p-3 border-b-2 border-gray-700 relative z-10 flex-shrink-0">
                        <div className="flex items-center gap-2">
                          <Folder size={20} className="text-cyan-400" />
                          <span className="font-mono text-sm font-bold text-cyan-400">ARCHIVE DESCRIPTION</span>
                        </div>
                      </div>

                      {/* Description Input */}
                      <div className="flex-1 flex relative z-10 overflow-hidden">
                        <textarea
                          value={folderData.description}
                          onChange={(e) => setFolderData({ ...folderData, description: e.target.value })}
                          placeholder="Describe the purpose of this archive system...

This archive will contain:
- Collections of related topics
- Individual log entries
- Organized documentation
- Reference materials

Use this space to document the archive's intended use and organization structure."
                          className="w-full flex-1 bg-transparent border-none outline-none text-white placeholder-gray-400 font-mono text-lg resize-none leading-relaxed overflow-y-auto p-4"
                          style={{ 
                            color: '#ffffff !important',
                            WebkitTextFillColor: '#ffffff',
                            textFillColor: '#ffffff'
                          }}
                        />
                      </div>
                    </div>
                  </div>

                  {/* Sidebar */}
                  <div className="lg:col-span-1 overflow-y-auto">
                    <div className="space-y-4">

                      {/* Color Selection */}
                      <div className="bg-gray-800 border-2 border-gray-600 p-3">
                        <label className="block text-xs font-mono font-bold text-cyan-400 mb-2">
                          <Palette size={14} className="inline mr-2" />
                          ARCHIVE COLOR
                        </label>
                        <div className="grid grid-cols-4 gap-2">
                          {colors.map(color => (
                            <button
                              key={color}
                              type="button"
                              onClick={() => setFolderData({ ...folderData, colorCode: color })}
                              className={`w-8 h-8 border-2 transition-all duration-200 ${
                                folderData.colorCode === color 
                                  ? 'border-white scale-110' 
                                  : 'border-gray-600 hover:border-gray-400'
                              }`}
                              style={{ backgroundColor: color }}
                              title={color}
                            />
                          ))}
                        </div>

                        {/* Current Color Display */}
                        <div className="mt-3 p-2 bg-gray-900 border border-gray-600 font-mono text-xs text-center">
                          <div className="flex items-center justify-center gap-2">
                            <div 
                              className="w-4 h-4 border border-gray-400"
                              style={{ backgroundColor: folderData.colorCode }}
                            />
                            <span className="text-gray-300">{folderData.colorCode}</span>
                          </div>
                        </div>
                      </div>

                      {/* Archive Info */}
                      <div className="bg-gray-800 border-2 border-gray-600 p-3">
                        <h3 className="font-mono text-xs font-bold text-cyan-400 mb-2">ARCHIVE INFO</h3>
                        <div className="space-y-2 text-xs font-mono text-gray-400">
                          <div>Archives organize collections and individual logs into logical groups</div>
                          <div className="text-cyan-400">• Unlimited sub-collections</div>
                          <div className="text-cyan-400">• Hierarchical organization</div>
                          <div className="text-cyan-400">• Color-coded identification</div>
                        </div>
                      </div>

                    </div>
                  </div>
                </div>
              </form>
            </motion.div>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default FolderModal;
