import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { User, Edit3, Save, X, Heart, Star } from 'lucide-react';
import { useTheme } from '../../contexts/ThemeContext';
import { getPlayerInfo, savePlayerInfo, skillBranches } from '../../data/playerInfo';

const AboutPlayer = () => {
  const { currentTheme, getThemeColors } = useTheme();
  const themeColors = getThemeColors();

  const [playerInfo, setPlayerInfo] = useState(() => getPlayerInfo());
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState(playerInfo);

  const handleSave = () => {
    setPlayerInfo(editData);
    savePlayerInfo(editData);
    setIsEditing(false);
  };

  const handleCancel = () => {
    setEditData(playerInfo);
    setIsEditing(false);
  };

  const handleInputChange = (field, value) => {
    setEditData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="p-6 relative overflow-hidden"
      style={{
        backgroundColor: themeColors.backgroundColor,
        border: `2px solid ${themeColors.borderColor}`,
        borderRadius: '12px',
        boxShadow: currentTheme === 'default' 
          ? `0 0 15px rgba(255, 255, 255, 0.2), 4px 4px 0px 0px rgba(0,0,0,1)` 
          : `0 0 3px ${themeColors.primary}50, 4px 4px 0px 0px rgba(0,0,0,1)`
      }}
    >
      {/* Gradient overlay */}
      <div className="absolute inset-0 pointer-events-none"
           style={{
             background: currentTheme === 'default' 
               ? 'linear-gradient(135deg, rgba(255,255,255,0.05), rgba(6,182,212,0.1))'
               : `linear-gradient(to bottom right, ${themeColors.secondary}15, ${themeColors.secondary}20)`,
             borderRadius: '12px'
           }} />

      <div className="relative z-10">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-2xl font-mono font-bold text-white flex items-center">
            {currentTheme === 'pink' ? (
              <Heart 
                size={20} 
                className="mr-2" 
                style={{ color: themeColors.secondary }}
              />
            ) : (
              <User 
                size={20} 
                className="mr-2" 
                style={{ color: themeColors.secondary }}
              />
            )}
            ABOUT PLAYER
          </h3>

          {/* Edit/Save Button */}
          <button
            onClick={isEditing ? handleSave : () => setIsEditing(true)}
            className="px-3 py-2 relative group cursor-pointer transition-all duration-300 font-mono font-bold overflow-hidden flex items-center gap-2"
            style={{
              backgroundColor: themeColors.backgroundColor,
              border: `2px solid ${themeColors.controlColor}`,
              borderRadius: '12px',
              color: themeColors.controlColor,
              boxShadow: currentTheme === 'default' 
                ? '0 0 10px rgba(255, 255, 255, 0.4), 2px 2px 0px 0px rgba(0,0,0,1)' 
                : `0 0 6px ${themeColors.controlColor}50, 2px 2px 0px 0px rgba(0,0,0,1)`
            }}
          >
            <div className="relative z-10 flex items-center gap-2">
              {isEditing ? (
                <>
                  <Save size={14} style={{ color: themeColors.controlColor }} />
                  <span className="text-xs">SAVE</span>
                </>
              ) : (
                <>
                  <Edit3 size={14} style={{ color: themeColors.controlColor }} />
                  <span className="text-xs">EDIT</span>
                </>
              )}
            </div>
          </button>

          {/* Cancel Button (only shown when editing) */}
          {isEditing && (
            <button
              onClick={handleCancel}
              className="ml-2 px-3 py-2 relative group cursor-pointer transition-all duration-300 font-mono font-bold overflow-hidden flex items-center gap-2"
              style={{
                backgroundColor: themeColors.backgroundColor,
                border: `2px solid #EF4444`,
                borderRadius: '12px',
                color: '#EF4444',
                boxShadow: '0 0 6px rgba(239, 68, 68, 0.5), 2px 2px 0px 0px rgba(0,0,0,1)'
              }}
            >
              <div className="relative z-10 flex items-center gap-2">
                <X size={14} style={{ color: '#EF4444' }} />
                <span className="text-xs">CANCEL</span>
              </div>
            </button>
          )}
        </div>

        {/* Content Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {/* Name */}
          <div className="p-4 relative overflow-hidden"
               style={{
                 backgroundColor: themeColors.backgroundColor,
                 border: `1px solid ${themeColors.borderColor}`,
                 borderRadius: '12px'
               }}>
            <div className="absolute inset-0 pointer-events-none"
                 style={{
                   background: currentTheme === 'default' 
                     ? 'linear-gradient(135deg, rgba(255,255,255,0.05), rgba(6,182,212,0.05))'
                     : `linear-gradient(to bottom right, ${themeColors.secondary}10, ${themeColors.secondary}15)`,
                   borderRadius: '12px'
                 }} />
            <div className="relative z-10">
              <div className="text-xs font-mono text-gray-400 mb-2">REAL NAME</div>
              {isEditing ? (
                <input
                  type="text"
                  value={editData.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
