import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Trophy, Star, Award, Shield, Crown, Lock } from 'lucide-react';

const AchievementBadge = React.memo(({ achievement, isUnlocked, showProgress, onClick }) => {
  const [isHovered, setIsHovered] = useState(false);

  // Rarity styles
  const rarityStyles = {
    common: {
      color: '#06B6D4',
      bgColor: 'rgba(6, 182, 212, 0.15)',
      shadowColor: 'rgba(6, 182, 212, 0.6)',
      borderColor: '#06B6D4'
    },
    uncommon: {
      color: '#EC4899',
      bgColor: 'rgba(236, 72, 153, 0.15)',
      shadowColor: 'rgba(236, 72, 153, 0.6)',
      borderColor: '#EC4899'
    },
    rare: {
      color: '#8B5CF6',
      bgColor: 'rgba(139, 92, 246, 0.15)',
      shadowColor: 'rgba(139, 92, 246, 0.6)',
      borderColor: '#8B5CF6'
    },
    legendary: {
      color: '#F59E0B',
      bgColor: 'rgba(245, 158, 11, 0.15)',
      shadowColor: 'rgba(245, 158, 11, 0.6)',
      borderColor: '#F59E0B'
    }
  };

  const rarityIcons = {
    common: Star,
    uncommon: Award,
    rare: Shield,
    legendary: Crown
  };

  const style = rarityStyles[achievement.tier?.toLowerCase()] || rarityStyles.common;
  const IconComponent = rarityIcons[achievement.tier?.toLowerCase()] || Trophy;

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
      className="relative cursor-pointer"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={onClick}
    >
      <div 
        className={`bg-black border-2 p-6 relative overflow-hidden transition-all duration-300 ${
          isUnlocked ? '' : 'opacity-60'
        }`}
        style={{
          borderColor: isUnlocked ? style.borderColor : '#4B5563',
          boxShadow: isUnlocked 
            ? `0 0 20px ${style.shadowColor}, 2px 2px 0px 0px rgba(0,0,0,1)`
            : '0 0 5px rgba(75, 85, 99, 0.3), 2px 2px 0px 0px rgba(0,0,0,1)'
        }}
      >
        {/* Background gradient */}
        <div 
          className="absolute inset-0 pointer-events-none opacity-20"
          style={{ 
            background: isUnlocked 
              ? `linear-gradient(135deg, ${style.color}20, ${style.color}10, ${style.color}30)`
              : 'linear-gradient(135deg, rgba(75, 85, 99, 0.2), rgba(75, 85, 99, 0.1))' 
          }} 
        />

        {/* Initial shimmer on load - only for unlocked badges */}
        {isUnlocked && (
          <motion.div
            initial={{ x: '-100%' }}
            animate={{ x: '200%' }}
            transition={{ 
              duration: 1.5, 
              ease: "easeInOut",
              delay: Math.random() * 0.5 // Random delay for staggered effect
            }}
            className="absolute inset-0 pointer-events-none"
            style={{
              background: `linear-gradient(90deg, transparent, ${style.color}40, transparent)`,
              width: '100%'
            }}
          />
        )}

        {/* Hover shimmer effect */}
        {isHovered && isUnlocked && (
          <motion.div
            initial={{ x: '-100%' }}
            animate={{ x: '200%' }}
            transition={{ 
              duration: 0.8, 
              ease: "easeInOut",
              repeat: Infinity,
              repeatDelay: 0.5
            }}
            className="absolute inset-0 pointer-events-none"
            style={{
              background: `linear-gradient(90deg, transparent, ${style.color}60, transparent)`,
              width: '100%'
            }}
          />
        )}

        {/* Badge Icon */}
        <div className="flex items-center justify-center mb-4">
          <div 
            className={`w-16 h-16 rounded-full border-2 flex items-center justify-center relative ${
              !isUnlocked ? 'grayscale' : ''
            }`}
            style={{
              borderColor: isUnlocked ? style.color : '#6B7280',
              background: isUnlocked 
                ? `radial-gradient(circle, ${style.color}30, ${style.color}15)`
                : 'radial-gradient(circle, rgba(107, 114, 128, 0.3), rgba(107, 114, 128, 0.15))',
              boxShadow: isUnlocked 
                ? `0 0 25px ${style.shadowColor}, inset 0 0 15px ${style.color}20`
                : '0 0 10px rgba(107, 114, 128, 0.3)'
            }}
          >
            {isUnlocked ? (
              <IconComponent 
                size={24} 
                className="text-white"
                style={{ 
                  color: '#ffffff',
                  filter: `drop-shadow(0 0 8px ${style.color})`
                }}
              />
            ) : (
              <Lock size={24} className="text-gray-400" />
            )}
          </div>
        </div>

        {/* Badge Content */}
        <div className="text-center relative z-10">
          <h3 
            className={`font-mono font-bold text-lg mb-2 ${
              isUnlocked ? 'text-white' : 'text-gray-400'
            }`}
          >
            {achievement.name}
          </h3>

          <p className="text-gray-400 text-sm mb-3 font-mono leading-tight">
            {achievement.description}
          </p>

          {/* Rarity Badge */}
          <div 
            className="inline-block px-3 py-1 text-xs font-mono font-bold border"
            style={{
              color: isUnlocked ? style.color : '#6B7280',
              borderColor: isUnlocked ? style.color : '#6B7280',
              backgroundColor: isUnlocked ? style.bgColor : 'rgba(107, 114, 128, 0.1)'
            }}
          >
            {achievement.tier?.toUpperCase() || 'COMMON'}
          </div>

          {/* Progress Bar for locked achievements */}
          {!isUnlocked && showProgress && achievement.progress > 0 && (
            <div className="mt-3">
              <div className="text-xs font-mono text-gray-400 mb-1">
                PROGRESS: {achievement.progress}%
              </div>
              <div className="w-full bg-gray-700 h-2 border">
                <motion.div
                  className="h-full"
                  style={{ 
                    backgroundColor: style.color,
                    width: `${achievement.progress}%`
                  }}
                  initial={{ width: 0 }}
                  animate={{ width: `${achievement.progress}%` }}
                  transition={{ duration: 1, ease: "easeOut" }}
                />
              </div>
            </div>
          )}

          {/* XP Reward for unlocked achievements */}
          {isUnlocked && achievement.xpReward && (
            <div className="mt-3 flex items-center justify-center gap-1">
              <Star size={12} className="text-yellow-400" />
              <span className="text-xs font-mono text-yellow-400">
                +{achievement.xpReward} XP
              </span>
              <Star size={12} className="text-yellow-400" />
            </div>
          )}
        </div>

        {/* Legendary star effect */}
        {isUnlocked && achievement.tier === 'legendary' && (
          <div className="absolute top-2 right-2 text-yellow-400 text-lg">⭐</div>
        )}
      </div>
    </motion.div>
  );
});

export default AchievementBadge;
