import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, Trophy } from 'lucide-react';
import AchievementBadge from '../ui/AchievementBadge';

/**
 * Badge Selector Modal Component
 * Allows users to select achievements to display as profile badges
 */
const BadgeSelectorModal = ({
  isOpen,
  onClose,
  availableAchievements,
  selectedBadges,
  onSelectBadge,
  onRemoveBadge,
  canAddMoreBadges
}) => {
  return (
    <AnimatePresence>
      {isOpen && (
        <>
          {/* Backdrop */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50"
            onClick={onClose}
          />

          {/* Modal */}
          <motion.div
            initial={{ opacity: 0, scale: 0.9, y: -20 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.9, y: -20 }}
            transition={{ type: "spring", damping: 20, stiffness: 300 }}
            className="fixed top-10 left-1/2 transform -translate-x-1/2 bg-gray-900/95 backdrop-blur-md rounded-xl border border-purple-500/30 shadow-2xl z-50 max-w-4xl w-full mx-4 max-h-[80vh] overflow-hidden"
          >
            {/* Header */}
            <div className="flex items-center justify-between p-6 border-b border-gray-700/50">
              <div className="flex items-center gap-3">
                <Trophy className="text-yellow-400" size={24} />
                <h2 className="text-xl font-bold text-white">Select Profile Badges</h2>
                <span className="text-sm text-gray-400">
                  ({selectedBadges.length}/3 selected)
                </span>
              </div>
              <button
                onClick={onClose}
                className="p-2 rounded-lg bg-gray-800/50 hover:bg-gray-700/50 text-gray-400 hover:text-white transition-colors"
              >
                <X size={20} />
              </button>
            </div>

            {/* Content */}
            <div className="p-6 overflow-y-auto max-h-[60vh]">
              {/* Instructions */}
              <div className="mb-6 p-4 bg-blue-900/30 border border-blue-500/30 rounded-lg">
                <p className="text-blue-200 text-sm">
                  Choose up to 3 achievements to display as badges on your profile. 
                  Only unlocked achievements can be selected.
                </p>
              </div>

              {/* Currently Selected Badges */}
              {selectedBadges.length > 0 && (
                <div className="mb-8">
                  <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
                    <Trophy size={18} className="text-yellow-400" />
                    Selected Badges
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {selectedBadges.map((badge) => (
                      <div key={badge.id} className="relative group">
                        <AchievementBadge 
                          achievement={badge}
                          variant="horizontal"
                          showProgress={false}
                          className="h-16"
                        />
                        <button
                          onClick={() => onRemoveBadge(badge.id)}
                          className="absolute -top-2 -right-2 bg-red-500 hover:bg-red-600 text-white rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity shadow-lg"
                        >
                          <X size={14} />
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Available Achievements */}
              <div>
                <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
                  <Trophy size={18} className="text-green-400" />
                  Available Achievements
                  <span className="text-sm text-gray-400 font-normal">
                    ({availableAchievements.length} unlocked)
                  </span>
                </h3>

                {availableAchievements.length === 0 ? (
                  <div className="text-center py-8">
                    <Trophy className="mx-auto text-gray-600 mb-4" size={48} />
                    <p className="text-gray-400">
                      {selectedBadges.length >= 3 
                        ? "You've selected the maximum number of badges. Remove a badge to add a different one."
                        : "No more achievements available to add as badges."
                      }
                    </p>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {availableAchievements.map((achievement) => (
                      <button
                        key={achievement.id}
                        onClick={() => onSelectBadge(achievement)}
                        disabled={!canAddMoreBadges}
                        className={`text-left transition-all duration-200 ${
                          canAddMoreBadges
                            ? 'hover:scale-105 hover:shadow-lg cursor-pointer'
                            : 'opacity-50 cursor-not-allowed'
                        }`}
                      >
                        <AchievementBadge 
                          achievement={achievement}
                          variant="horizontal"
                          showProgress={false}
                          className="h-16"
                        />
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Footer */}
            <div className="flex justify-end gap-3 p-6 border-t border-gray-700/50 bg-gray-900/50">
              <button
                onClick={onClose}
                className="px-6 py-2 bg-gray-700/50 hover:bg-gray-600/50 text-white rounded-lg transition-colors"
              >
                Done
              </button>
            </div>
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
};

export default BadgeSelectorModal;
