import React from 'react';
import { motion } from 'framer-motion';
import { tierInfo } from '../../data/achievements';

const AchievementBadge = ({ achievement, size = 'medium', showGlow = true, onClick, isUnlocked = false }) => {
  const tier = tierInfo[achievement.tier];
  const unlocked = isUnlocked || achievement.unlockedAt;

  const sizes = {
    small: { badge: 48, icon: 16, text: 'text-xs', glow: 8 },
    medium: { badge: 72, icon: 24, text: 'text-sm', glow: 12 },
    large: { badge: 96, icon: 32, text: 'text-base', glow: 16 },
    xlarge: { badge: 128, icon: 40, text: 'text-lg', glow: 20 }
  };

  const currentSize = sizes[size];

  return (
    <motion.div
      className={`relative cursor-pointer ${currentSize.text}`}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      onClick={onClick}
    >
      {/* Outer glow effect for unlocked badges */}
      {unlocked && showGlow && (
        <motion.div 
          className="absolute inset-0 rounded-full blur-lg animate-pulse"
          style={{
            background: `radial-gradient(circle, ${tier.color}60 0%, ${tier.color}20 50%, transparent 70%)`,
            width: currentSize.badge + currentSize.glow * 2,
            height: currentSize.badge + currentSize.glow * 2,
            left: -currentSize.glow,
            top: -currentSize.glow
          }}
          animate={{
            opacity: [0.6, 1, 0.6],
            scale: [0.95, 1.05, 0.95]
          }}
          transition={{
            duration: 2,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
      )}

      {/* Secondary glow for legendary badges */}
      {unlocked && showGlow && achievement.tier === 'legendary' && (
        <motion.div 
          className="absolute inset-0 rounded-full blur-xl"
          style={{
            background: `radial-gradient(circle, ${tier.color}40 0%, transparent 70%)`,
            width: currentSize.badge + currentSize.glow * 3,
            height: currentSize.badge + currentSize.glow * 3,
            left: -currentSize.glow * 1.5,
            top: -currentSize.glow * 1.5
          }}
          animate={{
            opacity: [0.3, 0.7, 0.3],
            rotate: [0, 360]
          }}
          transition={{
            duration: 4,
            repeat: Infinity,
            ease: "linear"
          }}
        />
      )}

      {/* Main badge */}
      <div
        className="relative border-4 rounded-full flex items-center justify-center overflow-hidden"
        style={{
          width: currentSize.badge,
          height: currentSize.badge,
          borderColor: unlocked ? tier.color : '#4B5563',
          backgroundColor: unlocked ? `${tier.color}25` : '#1F2937',
          boxShadow: unlocked 
            ? `0 0 30px ${tier.color}80, inset 0 0 20px ${tier.color}30, inset 0 2px 4px rgba(255,255,255,0.3)`
            : 'inset 0 0 10px rgba(0,0,0,0.5), inset 0 2px 4px rgba(255,255,255,0.1)'
        }}
      >
        {/* Multi-layer gradient overlay for premium shine effect */}
        {unlocked && (
          <>
            {/* Base gradient */}
            <div 
              className="absolute inset-0 rounded-full opacity-60"
              style={{
                background: `linear-gradient(135deg, 
                  ${tier.color}40 0%, 
                  transparent 30%, 
                  transparent 70%, 
                  ${tier.color}20 100%)`
              }}
            />

            {/* Highlight shine */}
            <motion.div 
              className="absolute top-2 left-2 w-6 h-6 rounded-full opacity-80 blur-sm"
              style={{
                background: `radial-gradient(circle, rgba(255,255,255,0.8) 0%, transparent 70%)`
              }}
              animate={{
                opacity: [0.5, 0.9, 0.5],
                scale: [0.8, 1.1, 0.8]
              }}
              transition={{
                duration: 2.5,
                repeat: Infinity,
                ease: "easeInOut"
              }}
            />

            {/* Animated light sweep for rare+ badges */}
            {(achievement.tier === 'rare' || achievement.tier === 'legendary') && (
              <motion.div
                className="absolute inset-0 rounded-full"
                style={{
                  background: `linear-gradient(45deg, transparent 30%, rgba(255,255,255,0.4) 50%, transparent 70%)`
                }}
                animate={{
                  rotate: [0, 360]
                }}
                transition={{
                  duration: 3,
                  repeat: Infinity,
                  ease: "linear"
                }}
              />
            )}
          </>
        )}

        {/* Badge content */}
        <div className="relative z-10 text-center">
          {unlocked ? (
            <motion.div 
              className="font-mono font-bold flex items-center justify-center"
              style={{ 
                color: tier.color,
                fontSize: currentSize.icon,
                textShadow: `0 0 15px ${tier.color}cc, 0 0 30px ${tier.color}66`,
                filter: 'drop-shadow(0 2px 4px rgba(0,0,0,0.8))'
              }}
              animate={{
                scale: achievement.tier === 'legendary' ? [1, 1.1, 1] : [1],
                rotate: achievement.tier === 'legendary' ? [0, 5, -5, 0] : [0]
              }}
              transition={{
                duration: achievement.tier === 'legendary' ? 2 : 0,
                repeat: achievement.tier === 'legendary' ? Infinity : 0,
                ease: "easeInOut"
              }}
            >
              {tier.emoji}
            </motion.div>
          ) : (
            <div 
              className="text-gray-600 font-mono font-bold flex items-center justify-center"
              style={{ fontSize: currentSize.icon }}
            >
              🔒
            </div>
          )}
        </div>

        {/* Rotating border animation for legendary badges */}
        {unlocked && achievement.tier === 'legendary' && (
          <motion.div
            className="absolute inset-0 rounded-full border-2 opacity-70"
            style={{ borderColor: `${tier.color}cc` }}
            animate={{ rotate: 360 }}
            transition={{ duration: 4, repeat: Infinity, ease: "linear" }}
          />
        )}

        {/* Pulsing border for rare badges */}
        {unlocked && achievement.tier === 'rare' && (
          <motion.div
            className="absolute inset-0 rounded-full border-2"
            style={{ borderColor: `${tier.color}80` }}
            animate={{ 
              opacity: [0.3, 0.8, 0.3],
              scale: [0.95, 1.02, 0.95]
            }}
            transition={{ 
              duration: 2, 
              repeat: Infinity, 
              ease: "easeInOut" 
            }}
          />
        )}
      </div>

      {/* Enhanced badge tier indicator */}
      <motion.div 
        className="absolute -bottom-2 left-1/2 transform -translate-x-1/2 px-2 py-0.5 rounded text-xs font-mono font-bold border shadow-lg"
        style={{
          backgroundColor: unlocked ? tier.color : '#4B5563',
          color: unlocked ? (tier.color === '#F59E0B' ? '#000' : '#FFF') : '#9CA3AF',
          borderColor: unlocked ? tier.color : '#4B5563',
          boxShadow: unlocked ? `0 2px 8px ${tier.color}60` : '0 2px 4px rgba(0,0,0,0.3)'
        }}
        whileHover={{ scale: 1.05 }}
      >
        {tier.name.charAt(0)}
      </motion.div>

      {/* Particle effects for legendary unlocked badges */}
      {unlocked && achievement.tier === 'legendary' && showGlow && (
        <div className="absolute inset-0 pointer-events-none">
          {[...Array(6)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute w-1 h-1 rounded-full"
              style={{
                backgroundColor: tier.color,
                left: '50%',
                top: '50%'
              }}
              animate={{
                x: [0, Math.cos(i * 60 * Math.PI / 180) * 40],
                y: [0, Math.sin(i * 60 * Math.PI / 180) * 40],
                opacity: [1, 0],
                scale: [0.5, 1, 0]
              }}
              transition={{
                duration: 2,
                repeat: Infinity,
                delay: i * 0.3,
                ease: "easeOut"
              }}
            />
          ))}
        </div>
      )}
    </motion.div>
  );
};

export default AchievementBadge;
