// components/ui/HeartProgressBar.jsx
import React from 'react';
import { motion } from 'framer-motion';
import { Heart } from 'lucide-react';
import { useTheme } from '../../contexts/ThemeContext';

const HeartProgressBar = ({ progress, maxHearts = 10, size = 24, className = "" }) => {
  const { currentTheme } = useTheme();

  if (currentTheme !== 'pink') {
    // Fallback to regular progress bar for other themes
    return (
      <div className={`w-full h-4 bg-gray-700 rounded-full overflow-hidden ${className}`}>
        <motion.div
          initial={{ width: 0 }}
          animate={{ width: `${progress}%` }}
          className="h-full bg-gradient-to-r from-cyan-400 to-cyan-600"
          transition={{ duration: 1, ease: "easeOut" }}
        />
      </div>
    );
  }

  // Calculate how many hearts should be filled
  const filledHearts = Math.floor((progress / 100) * maxHearts);
  const partialHeart = ((progress / 100) * maxHearts) % 1;

  return (
    <div className={`flex items-center gap-1 ${className}`}>
      {Array.from({ length: maxHearts }, (_, index) => {
        let heartState = 'empty';

        if (index < filledHearts) {
          heartState = 'filled';
        } else if (index === filledHearts && partialHeart > 0) {
          heartState = 'partial';
        }

        return (
          <HeartIcon
            key={index}
            state={heartState}
            size={size}
            partialProgress={heartState === 'partial' ? partialHeart : 0}
            delay={index * 0.1}
          />
        );
      })}
    </div>
  );
};

const HeartIcon = ({ state, size, partialProgress, delay }) => {
  return (
    <motion.div
      className="relative"
      initial={{ scale: 0 }}
      animate={{ scale: 1 }}
      transition={{ delay, duration: 0.3, type: "spring" }}
      style={{ width: size, height: size }}
    >
      {/* Background heart (empty) */}
      <Heart
        size={size}
        className="absolute inset-0 text-gray-600"
        fill="rgba(75, 85, 99, 0.3)"
        stroke="rgba(244, 114, 182, 0.5)"
        strokeWidth="1"
      />

      {/* Filled heart */}
      {state !== 'empty' && (
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: state === 'filled' ? 1 : partialProgress }}
          transition={{ delay: delay + 0.2, duration: 0.4, type: "spring" }}
          className="absolute inset-0"
        >
          <Heart
            size={size}
            className="text-pink-400"
            fill="url(#heartGradient)"
            stroke="#f472b6"
            strokeWidth="1"
          />
        </motion.div>
      )}

      {/* Sparkle effect for filled hearts */}
      {state === 'filled' && (
        <motion.div
          animate={{
            opacity: [0.5, 1, 0.5],
            scale: [0.8, 1.1, 0.8]
          }}
          transition={{
            duration: 2,
            repeat: Infinity,
            ease: "easeInOut",
            delay: delay + 0.5
          }}
          className="absolute inset-0 pointer-events-none"
        >
          <Heart
            size={size}
            className="text-white"
            fill="none"
            stroke="rgba(248, 250, 252, 0.6)"
            strokeWidth="0.5"
            filter="blur(1px)"
          />
        </motion.div>
      )}

      {/* SVG gradient definition */}
      <svg width="0" height="0">
        <defs>
          <linearGradient id="heartGradient" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" stopColor="#f472b6" />
            <stop offset="50%" stopColor="#e879f9" />
            <stop offset="100%" stopColor="#f472b6" />
          </linearGradient>
        </defs>
      </svg>
    </motion.div>
  );
};

export default HeartProgressBar;
