import React, { useState } from 'react';
import { Camera, User } from 'lucide-react';

/**
 * Profile Picture Section Component
 * Handles profile picture display and upload
 */
const ProfilePictureSection = ({ 
  username, 
  profilePic, 
  onProfilePicChange,
  className = "" 
}) => {
  const [isHovering, setIsHovering] = useState(false);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const base64String = e.target.result;
        onProfilePicChange(base64String);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className={`relative ${className}`}>
      {/* Profile Picture Container */}
      <div 
        className="relative group cursor-pointer"
        onMouseEnter={() => setIsHovering(true)}
        onMouseLeave={() => setIsHovering(false)}
      >
        {/* Profile Picture */}
        <div className="w-24 h-24 mx-auto rounded-full border-4 border-purple-500/50 overflow-hidden shadow-2xl">
          {profilePic ? (
            <img 
              src={profilePic} 
              alt="Profile" 
              className="w-full h-full object-cover"
            />
          ) : (
            <div className="w-full h-full bg-gradient-to-br from-purple-600 to-blue-600 flex items-center justify-center">
              <User className="text-white/80" size={32} />
            </div>
          )}
        </div>

        {/* Upload Overlay */}
        {isHovering && (
          <div className="absolute inset-0 bg-black/50 rounded-full flex items-center justify-center">
            <Camera className="text-white" size={24} />
          </div>
        )}

        {/* Hidden File Input */}
        <input
          type="file"
          accept="image/*"
          onChange={handleFileUpload}
          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
          title="Click to upload profile picture"
        />
      </div>

      {/* Username */}
      <h2 className="text-xl font-bold text-white text-center mt-3">
        {username}
      </h2>
    </div>
  );
};

export default ProfilePictureSection;
