        className="bg-gray-900 border border-purple-400 p-6 relative overflow-hidden"
        style={{
          boxShadow: '0 0 3px rgba(139, 92, 246, 0.3), 1px 1px 0px 0px rgba(0,0,0,1)'
        }}
      >
        {/* Gradient overlay with purple theme to match other sections */}
        <div className="absolute inset-0 bg-gradient-to-br from-purple-400/15 to-purple-600/20 pointer-events-none" />

        <div className="relative z-10">
          <h3 className="text-lg font-mono font-bold text-white flex items-center mb-4">
            <span className="text-pink-400 mr-2 text-xl">♥</span>
            ACHIEVEMENT STATS
          </h3>

          <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
            {/* Unlocked Achievements */}
            <div className="bg-gray-900 border border-gray-600 p-4 relative overflow-hidden">
              <div className="absolute inset-0 bg-gradient-to-br from-purple-500/10 to-purple-600/15 pointer-events-none" />
              <div className="relative z-10">
                <div className="text-xs font-mono text-gray-400">UNLOCKED</div>
                <div className="text-2xl font-mono font-bold text-white">{achievementStats.unlocked || 0}</div>
              </div>
            </div>

            {/* Completion Rate */}
            <div className="bg-gray-900 border border-gray-600 p-4 relative overflow-hidden">
              <div className="absolute inset-0 bg-gradient-to-br from-purple-400/10 to-purple-500/15 pointer-events-none" />
              <div className="relative z-10">
                <div className="text-xs font-mono text-gray-400">COMPLETION</div>
                <div className="text-2xl font-mono font-bold text-white">{achievementStats.percentage || 0}%</div>
              </div>
            </div>

            {/* Rare Achievements */}
            <div className="bg-gray-900 border border-gray-600 p-4 relative overflow-hidden">
              <div className="absolute inset-0 bg-gradient-to-br from-purple-600/10 to-purple-700/15 pointer-events-none" />
              <div className="relative z-10">
                <div className="text-xs font-mono text-gray-400">RARE</div>
                <div className="text-2xl font-mono font-bold text-white">{achievementStats.byTier?.rare || 0}</div>
              </div>
            </div>

            {/* Legendary Achievements */}
            <div className="bg-gray-900 border border-gray-600 p-4 relative overflow-hidden">
              <div className="absolute inset-0 bg-gradient-to-br from-purple-500/10 to-purple-800/15 pointer-events-none" />
              <div className="relative z-10">
                <div className="text-xs font-mono text-gray-400">LEGENDARY</div>
                <div className="text-2xl font-mono font-bold text-white">{achievementStats.byTier?.legendary || 0}</div>
              </div>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Easter Egg Modal - unchanged */}
      <AnimatePresence>
        {showEasterEgg && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
            onClick={() => setShowEasterEgg(false)}
          >
            <motion.div
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              className="bg-gray-800 border-2 border-cyan-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-8 max-w-md mx-4 relative"
              style={{
                boxShadow: '0 0 20px rgba(147, 51, 234, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
              }}
              onClick={(e) => e.stopPropagation()}
            >
              {/* Animated border */}
              <div className="absolute inset-0 border-2 border-cyan-400 opacity-50 animate-pulse pointer-events-none" />

              {/* Close button */}
              <button
                onClick={() => setShowEasterEgg(false)}
                className="absolute top-4 right-4 text-gray-400 hover:text-white transition-colors"
              >
                <X size={20} />
              </button>

              {/* Content - unchanged */}
              <div className="text-center">
                <motion.div
                  className="mb-6"
                  animate={{ rotate: 360 }}
                  transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
                >
                  <Trophy size={64} className="text-cyan-400 mx-auto" />
                </motion.div>

                <h2 className="text-2xl font-mono font-bold text-white mb-2">
                  EASTER EGG ACHIEVEMENTS
                </h2>

                <p className="text-cyan-400 font-mono text-sm mb-6">
                  You found the hidden easter egg level!
                </p>

                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.3 }}
                  className="bg-gray-900 border border-cyan-400 p-4 mb-6"
                >
                  <h3 className="text-xl font-mono font-bold text-cyan-400 mb-4">
                    COMING SOON
                  </h3>
                </motion.div>

                <div className="flex items-center justify-center gap-1">
                  <motion.div
                    className="w-2 h-2 bg-cyan-400 rounded-full"
                    animate={{ opacity: [0.3, 1, 0.3] }}
                    transition={{ duration: 1.5, repeat: Infinity, delay: 0 }}
                  />
                  <motion.div
                    className="w-2 h-2 bg-cyan-400 rounded-full"
                    animate={{ opacity: [0.3, 1, 0.3] }}
                    transition={{ duration: 1.5, repeat: Infinity, delay: 0.5 }}
                  />
                  <motion.div
                    className="w-2 h-2 bg-cyan-400 rounded-full"
                    animate={{ opacity: [0.3, 1, 0.3] }}
                    transition={{ duration: 1.5, repeat: Infinity, delay: 1 }}
                  />
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Achievement Badge Selector Modal */}
      <AnimatePresence>
        {showBadgeSelector && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
            onClick={() => setShowBadgeSelector(false)}
          >
            <motion.div
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 max-w-2xl mx-4 relative max-h-[80vh] overflow-y-auto"
              style={{
                borderColor: themeColors.primary,
                boxShadow: `0 0 20px ${themeColors.primary}30, 8px 8px 0px 0px rgba(0,0,0,1)`
              }}
              onClick={(e) => e.stopPropagation()}
            >
              {/* Animated border */}
              <div className="absolute inset-0 border-2 opacity-50 animate-pulse pointer-events-none" 
                   style={{ borderColor: themeColors.primary }} />

              {/* Close button */}
              <button
                onClick={() => setShowBadgeSelector(false)}
                className="absolute top-4 right-4 text-gray-400 hover:text-white transition-colors"
              >
                <X size={20} />
              </button>

              {/* Content */}
              <div>
                <h2 className="text-2xl font-mono font-bold text-white mb-2">
                  SELECT PROFILE BADGES
                </h2>

                <p className="font-mono text-sm mb-6" style={{ color: themeColors.primary }}>
                  Choose up to 3 achievement badges to display on your profile ({selectedBadges.length}/3 selected)
                </p>

                <div className="space-y-4">
                  {Object.entries(tierInfo).map(([tierKey, tier]) => {
                    const tierAchievements = getUnlockedAchievements().filter(a => a.tier === tierKey);
                    if (tierAchievements.length === 0) return null;

                    return (
                      <div key={tierKey}>
                        <h3 className="font-mono font-bold text-lg mb-3 flex items-center gap-2">
                          <span style={{ color: tier.color }}>{tier.emoji}</span>
                          <span style={{ color: tier.color }}>{tier.name}</span>
                          <span className="text-gray-400 text-sm">({tierAchievements.length})</span>
                        </h3>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                          {tierAchievements.map(achievement => (
                            <button
                              key={achievement.id}
                              onClick={() => toggleBadgeSelection(achievement.id)}
                              className={`p-3 border transition-all duration-200 text-left ${
                                selectedBadges.includes(achievement.id)
                                  ? 'bg-gray-700 border-opacity-100'
                                  : 'bg-gray-900 border-gray-600 hover:bg-gray-800'
                              }`}
                              style={{
                                borderColor: selectedBadges.includes(achievement.id) ? tier.color : '#4B5563'
                              }}
                            >
                              <div className="flex items-center gap-3">
                                <span className="text-2xl">{achievement.icon}</span>
                                <div className="flex-1">
                                  <div className="font-mono font-bold text-white text-sm">
                                    {achievement.name}
                                  </div>
                                  <div className="font-mono text-xs text-gray-400">
                                    {achievement.description}
                                  </div>
                                </div>
                                {selectedBadges.includes(achievement.id) && (
                                  <Star size={16} style={{ color: tier.color }} />
                                )}
                              </div>
                            </button>
                          ))}
                        </div>
                      </div>
                    );
                  })}

                  {getUnlockedAchievements().length === 0 && (
                    <div className="text-center py-8">
                      <Trophy size={48} className="text-gray-500 mx-auto mb-3" />
                      <div className="text-gray-400 font-mono text-lg font-bold mb-2">
                        NO ACHIEVEMENTS UNLOCKED
                      </div>
                      <div className="text-gray-500 font-mono text-sm">
                        Complete tasks and activities to unlock achievement badges!
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Add this style block to your component */}
      <style jsx>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes shimmer-slide {
          0% {
            background-position: -200% 0%;
          }
          100% {
            background-position: 200% 0%;
          }
        }
      `}</style>
    </div>
  );
};

export default HeroCard;
