import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Play, Pause, Square, RotateCcw, Timer, Coffee, Target, Settings, Plus } from 'lucide-react';
import useTimer from '../../hooks/useTimer';
import useFocusSessions from '../../hooks/useFocusSessions';
import SessionCreator from '../focus/SessionCreator';

const FocusTab = ({ tabColor = '#8B5CF6' }) => {
  const [currentSession, setCurrentSession] = useState(null);
  const [sessionConfig, setSessionConfig] = useState({
    workDuration: 25, // minutes
    breakDuration: 5,
    longBreakDuration: 15,
    cycles: 1,
    currentCycle: 0
  });
  const [isSessionCreatorOpen, setIsSessionCreatorOpen] = useState(false);
  const [sessionType, setSessionType] = useState('general'); // 'general' or 'tracked'
  const [activePhase, setActivePhase] = useState('work'); // 'work' or 'break'
  const [completedTime, setCompletedTime] = useState(0);
  const [showCompletionModal, setShowCompletionModal] = useState(false);

  const timer = useTimer();
  const { sessions, createSession, logFocusTime } = useFocusSessions();

  const hexToRgb = (hex) => {
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? 
      `${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}` :
      '139, 92, 246';
  };

  const tabColorRgb = hexToRgb(tabColor);

  // Handle timer completion
  useEffect(() => {
    if (timer.mode === 'completed') {
      if (activePhase === 'work') {
        // Work phase completed, add to completed time
        setCompletedTime(prev => prev + sessionConfig.workDuration);

        if (sessionConfig.currentCycle < sessionConfig.cycles - 1) {
          // Start break
          setActivePhase('break');
          timer.startTimer(sessionConfig.breakDuration, 'break');
        } else {
          // All cycles completed
          handleSessionComplete();
        }
      } else {
        // Break completed, start next work cycle
        setSessionConfig(prev => ({ ...prev, currentCycle: prev.currentCycle + 1 }));
        setActivePhase('work');
        timer.startTimer(sessionConfig.workDuration, 'focus');
      }
    }
  }, [timer.mode, activePhase, sessionConfig]);

  const handleStartSession = (config, session = null) => {
    setSessionConfig({
      ...config,
      currentCycle: 0
    });
    setCurrentSession(session);
    setSessionType(session ? 'tracked' : 'general');
    setActivePhase('work');
    setCompletedTime(0);
    timer.startTimer(config.workDuration, 'focus');
  };

  const handleSessionComplete = () => {
    if (sessionType === 'tracked' && currentSession) {
      // Show completion modal with options to log or discard
      setShowCompletionModal(true);
    } else {
      // General session - just reset
      resetSession();
    }
  };

  const handleLogSession = async () => {
    if (currentSession && completedTime > 0) {
      try {
        await logFocusTime(currentSession.id, completedTime);
        setShowCompletionModal(false);
        resetSession();
      } catch (error) {
        console.error('Failed to log session:', error);
        alert('Failed to log session. Please try again.');
      }
    }
  };

  const handleDiscardSession = () => {
    setShowCompletionModal(false);
    resetSession();
  };

  const resetSession = () => {
    setCurrentSession(null);
    setSessionConfig({
      workDuration: 25,
      breakDuration: 5,
      longBreakDuration: 15,
      cycles: 1,
      currentCycle: 0
    });
    setActivePhase('work');
    setCompletedTime(0);
    setSessionType('general');
    timer.stopTimer();
  };

  // Background style based on timer state
  const getBackgroundStyle = () => {
    if (timer.mode === 'focus') {
      return {
        background: `linear-gradient(135deg, rgba(${tabColorRgb}, 0.1), rgba(${tabColorRgb}, 0.2))`,
        borderColor: tabColor
      };
    } else if (timer.mode === 'break') {
      return {
        background: 'linear-gradient(135deg, rgba(34, 197, 94, 0.1), rgba(34, 197, 94, 0.2))',
        borderColor: '#22C55E'
      };
    }
    return {
      background: 'linear-gradient(135deg, rgba(75, 85, 99, 0.1), rgba(75, 85, 99, 0.2))',
      borderColor: '#6B7280'
    };
  };

  return (
    <div className="focus-tab-container p-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="font-mono text-3xl font-bold text-white mb-2 flex items-center gap-3">
          <div 
            className="w-6 h-6 border border-gray-600" 
            style={{ backgroundColor: tabColor }}
          />
          FOCUS STATION
        </h1>
        <p className="text-gray-400 font-mono text-sm">
          Productivity stations with time tracking and break management.
        </p>
      </div>

      {/* Main Timer Interface */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-8 relative mb-6"
        style={{
          ...getBackgroundStyle(),
          boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="absolute inset-0 border-2 opacity-30 animate-pulse pointer-events-none" 
             style={{ borderColor: getBackgroundStyle().borderColor }} />

        <div className="relative z-10 text-center">
          {/* Session Info */}
          {currentSession && (
            <div className="mb-6">
              <h3 className="font-mono text-xl font-bold text-white mb-2">
                {currentSession.name}
              </h3>
              <div className="flex items-center justify-center gap-4 text-sm font-mono text-gray-400">
                <span>Cycle {sessionConfig.currentCycle + 1} of {sessionConfig.cycles}</span>
                <span>Phase: {activePhase.toUpperCase()}</span>
                <span>Completed: {completedTime}m</span>
              </div>
              {currentSession.tag && (
                <div className="mt-2">
                  <span 
                    className="px-3 py-1 text-xs font-mono border"
                    style={{ 
                      borderColor: currentSession.colorCode,
                      color: currentSession.colorCode 
                    }}
                  >
                    {currentSession.tag}
                  </span>
                </div>
              )}
            </div>
          )}

          {/* Timer Display */}
          <div className="mb-8">
            <div className="text-6xl font-mono font-bold text-white mb-4">
              {timer.formatTime}
            </div>

            {/* Progress Bar */}
            <div className="w-full bg-gray-700 h-4 border border-gray-600 mb-4">
              <div 
                className="h-full transition-all duration-1000"
                style={{ 
                  width: `${timer.getProgress()}%`,
                  backgroundColor: activePhase === 'work' ? tabColor : '#22C55E'
                }}
              />
            </div>

            {/* Timer Status */}
            <div className="flex items-center justify-center gap-2 text-sm font-mono">
              {timer.mode === 'focus' && (
                <>
                  <Target size={16} style={{ color: tabColor }} />
                  <span style={{ color: tabColor }}>FOCUS MODE ACTIVE</span>
                </>
              )}
              {timer.mode === 'break' && (
                <>
                  <Coffee size={16} className="text-green-400" />
                  <span className="text-green-400">BREAK TIME</span>
                </>
              )}
              {timer.mode === 'idle' && (
                <>
                  <Timer size={16} className="text-gray-400" />
                  <span className="text-gray-400">READY TO START</span>
                </>
              )}
              {timer.isPaused && (
                <span className="text-yellow-400 ml-2">PAUSED</span>
              )}
            </div>
          </div>

          {/* Timer Controls */}
          <div className="flex items-center justify-center gap-4">
            {!timer.isRunning ? (
              <button
                onClick={() => setIsSessionCreatorOpen(true)}
                className="bg-gray-900 border-2 px-6 py-3 relative group cursor-pointer transition-all duration-300 font-mono font-bold"
                style={{
                  borderColor: tabColor,
                  color: tabColor,
                  boxShadow: `0 0 5px rgba(${tabColorRgb}, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)`
                }}
                onMouseEnter={(e) => {
                  e.target.style.borderColor = tabColor;
                  e.target.style.boxShadow = `0 0 15px rgba(${tabColorRgb}, 0.3)`;
                }}
                onMouseLeave={(e) => {
                  e.target.style.borderColor = tabColor;
                  e.target.style.boxShadow = `0 0 5px rgba(${tabColorRgb}, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)`;
                }}
              >
                <div className="flex items-center gap-2">
                  <Play size={18} />
                  <span>START SESSION</span>
                </div>
                <div className="absolute inset-0 opacity-0 group-hover:opacity-10 transition-opacity"
                     style={{ backgroundColor: tabColor }} />
              </button>
            ) : (
              <div className="flex gap-3">
                <button
                  onClick={timer.togglePause}
                  className="bg-gray-900 border-2 px-4 py-2 relative group cursor-pointer transition-all duration-300 font-mono font-bold"
                  style={{
                    borderColor: tabColor,
                    color: tabColor,
                    boxShadow: `0 0 5px rgba(${tabColorRgb}, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)`
                  }}
                >
                  <div className="flex items-center gap-2">
                    {timer.isPaused ? <Play size={16} /> : <Pause size={16} />}
                    <span>{timer.isPaused ? 'RESUME' : 'PAUSE'}</span>
                  </div>
                </button>

                <button
                  onClick={resetSession}
                  className="bg-gray-900 border-2 border-red-500 px-4 py-2 relative group cursor-pointer transition-all duration-300 font-mono font-bold text-red-500 hover:bg-red-500 hover:bg-opacity-10"
                >
                  <div className="flex items-center gap-2">
                    <Square size={16} />
                    <span>STOP</span>
                  </div>
                </button>
              </div>
            )}
          </div>
        </div>
      </motion.div>

      {/* Quick Start Options */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 relative"
        style={{
          borderColor: tabColor,
          boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="absolute inset-0 border-2 opacity-30 animate-pulse pointer-events-none" 
             style={{ borderColor: tabColor }} />

        <div className="relative z-10">
          <h3 className="text-lg font-mono font-bold text-white mb-4">QUICK START</h3>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {/* Pomodoro */}
            <button
              onClick={() => handleStartSession({
                workDuration: 25,
                breakDuration: 5,
                cycles: 4
              })}
              className="bg-gray-900 border border-gray-600 p-4 text-left hover:border-purple-400 transition-colors disabled:opacity-50"
              disabled={timer.isRunning}
            >
              <div className="font-mono font-bold text-white mb-2">POMODORO</div>
              <div className="text-sm text-gray-400 font-mono">
                25m work • 5m break • 4 cycles
              </div>
            </button>

            {/* Deep Work */}
            <button
              onClick={() => handleStartSession({
                workDuration: 90,
                breakDuration: 20,
                cycles: 1
              })}
              className="bg-gray-900 border border-gray-600 p-4 text-left hover:border-purple-400 transition-colors disabled:opacity-50"
              disabled={timer.isRunning}
            >
              <div className="font-mono font-bold text-white mb-2">DEEP WORK</div>
              <div className="text-sm text-gray-400 font-mono">
                90m work • 20m break • 1 cycle
              </div>
            </button>

            {/* Custom */}
            <button
              onClick={() => setIsSessionCreatorOpen(true)}
              className="bg-gray-900 border border-gray-600 p-4 text-left hover:border-purple-400 transition-colors disabled:opacity-50"
              disabled={timer.isRunning}
            >
              <div className="font-mono font-bold text-white mb-2">CUSTOM</div>
              <div className="text-sm text-gray-400 font-mono">
                Configure your own session
              </div>
            </button>
          </div>

          {/* Saved Sessions */}
          {sessions.length > 0 && (
            <div className="mt-6">
              <h4 className="font-mono font-bold text-white mb-3">TRACKED SESSIONS</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
                {sessions.slice(0, 6).map(session => (
                  <button
                    key={session.id}
                    onClick={() => handleStartSession({
                      workDuration: session.workDuration || 25,
                      breakDuration: session.breakDuration || 5,
                      cycles: session.cycles || 1
                    }, session)}
                    className="bg-gray-900 border p-3 text-left transition-colors disabled:opacity-50"
                    style={{
                      borderColor: session.colorCode || tabColor
                    }}
                    disabled={timer.isRunning}
                  >
                    <div className="flex items-center gap-2 mb-2">
                      <div 
                        className="w-3 h-3"
                        style={{ backgroundColor: session.colorCode || tabColor }}
                      />
                      <span className="font-mono font-bold text-white text-sm">
                        {session.name}
                      </span>
                    </div>
                    <div className="text-xs text-gray-400 font-mono">
                      {session.tag && (
                        <span className="px-2 py-1 bg-gray-700 border border-gray-600 mr-2">
                          {session.tag}
                        </span>
                      )}
                      <span>{session.totalTimeLogged || 0}m logged</span>
                    </div>
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </motion.div>

      {/* Session Creator Modal */}
      <SessionCreator
        isOpen={isSessionCreatorOpen}
        onClose={() => setIsSessionCreatorOpen(false)}
        onCreateSession={handleStartSession}
        sessions={sessions}
        createSession={createSession}
        tabColor={tabColor}
      />

      {/* Completion Modal */}
      {showCompletionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 w-full max-w-md"
            style={{
              borderColor: tabColor,
              boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
            }}
          >
            <div className="text-center">
              <h3 className="font-mono text-xl font-bold text-white mb-4">
                SESSION COMPLETED!
              </h3>
              <p className="font-mono text-gray-400 mb-2">
                You focused for <span className="text-white font-bold">{completedTime} minutes</span>
              </p>
              <p className="font-mono text-gray-400 mb-6">
                on <span style={{ color: currentSession?.colorCode || tabColor }}>{currentSession?.name}</span>
              </p>

              <div className="flex gap-3">
                <button
                  onClick={handleDiscardSession}
                  className="flex-1 bg-gray-900 border-2 border-gray-600 px-4 py-2 font-mono text-gray-400 hover:text-white hover:border-gray-500 transition-colors"
                >
                  DISCARD
                </button>
                <button
                  onClick={handleLogSession}
                  className="flex-1 bg-gray-900 border-2 px-4 py-2 font-mono font-bold transition-colors"
                  style={{
                    borderColor: tabColor,
                    color: tabColor
                  }}
                >
                  LOG SESSION
                </button>
              </div>
            </div>
          </motion.div>
        </div>
      )}
    </div>
  );
};

export default FocusTab;
