import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { BookOpen, Briefcase, Palette, Code, User, Trophy, Star, Zap, Crown, Shield, Lock } from 'lucide-react';

const HexagonalSkillTree = ({ categories, tabColor, tabColorRgb }) => {
  const [hoveredNode, setHoveredNode] = useState(null);

  // XP calculation for levels (exponential growth)
  const calculateXPRequired = (level) => {
    const base = 60; // 1 hour for level 1
    return Math.floor(base * Math.pow(1.4, level - 1));
  };

  // Get current level from total XP
  const getCurrentLevel = (totalXP) => {
    let level = 1;
    let xpNeeded = 0;

    while (level <= 10) {
      xpNeeded += calculateXPRequired(level);
      if (totalXP < xpNeeded) break;
      level++;
    }

    return Math.min(level, 10);
  };

  // Get XP progress for current level
  const getLevelProgress = (totalXP) => {
    const currentLevel = getCurrentLevel(totalXP);
    if (currentLevel === 10) return { current: 100, max: 100, percentage: 100 };

    let xpUsed = 0;
    for (let i = 1; i < currentLevel; i++) {
      xpUsed += calculateXPRequired(i);
    }

    const currentLevelXP = totalXP - xpUsed;
    const maxLevelXP = calculateXPRequired(currentLevel);
    const percentage = Math.floor((currentLevelXP / maxLevelXP) * 100);

    return { current: currentLevelXP, max: maxLevelXP, percentage };
  };

  const getIconComponent = (iconName) => {
    const iconMap = {
      'BookOpen': BookOpen,
      'Briefcase': Briefcase,
      'Palette': Palette,
      'Code': Code,
      'User': User
    };
    return iconMap[iconName] || User;
  };

  const getLevelTitle = (category, level) => {
    const titles = {
      study: ['ROOKIE', 'NOVICE', 'STUDENT', 'SCHOLAR', 'RESEARCHER', 'EXPERT', 'SPECIALIST', 'PROFESSOR', 'GENIUS', 'MASTERMIND'],
      work: ['INTERN', 'TRAINEE', 'WORKER', 'SPECIALIST', 'VETERAN', 'ELITE', 'MANAGER', 'DIRECTOR', 'EXECUTIVE', 'LEGEND'],
      creative: ['DREAMER', 'ARTIST', 'MAKER', 'CREATOR', 'DESIGNER', 'INNOVATOR', 'VISIONARY', 'PIONEER', 'VIRTUOSO', 'GODLIKE'],
      code: ['NOOB', 'CODER', 'PROGRAMMER', 'DEVELOPER', 'ENGINEER', 'ARCHITECT', 'GURU', 'NINJA', 'WIZARD', 'HACKER'],
      personal: ['SEEKER', 'EXPLORER', 'JOURNEYER', 'WANDERER', 'PATHFINDER', 'GUARDIAN', 'WARRIOR', 'CHAMPION', 'HERO', 'LEGEND']
    };

    const categoryKey = category.name.toLowerCase();
    return titles[categoryKey]?.[level - 1] || `LEVEL ${level}`;
  };

  // Predefined branch colors
  const getBranchColor = (categoryName) => {
    const colorMap = {
      'work': '#06B6D4',      // Cyan
      'study': '#EC4899',     // Pink
      'creative': '#8B5CF6',  // Purple
      'code': '#10B981',      // Emerald
      'personal': '#F59E0B',  // Amber
      'health': '#EF4444',    // Red
      'finance': '#3B82F6',   // Blue
      'social': '#84CC16'     // Lime
    };

    const categoryKey = categoryName.toLowerCase();
    return colorMap[categoryKey] || '#6B7280'; // Default gray
  };

  // Get node description for hover
  const getNodeDescription = (category, nodeLevel) => {
    const descriptions = {
      study: [
        "Start your learning journey",
        "Basic knowledge foundation", 
        "Developing study habits",
        "Advanced understanding",
        "Research capabilities",
        "Expert-level insights",
        "Specialized knowledge",
        "Teaching abilities",
        "Breakthrough thinking",
        "Master of the domain"
      ],
      work: [
        "Entry-level position",
        "Learning the ropes",
        "Productive contributor", 
        "Specialized skills",
        "Experienced professional",
        "High-performance elite",
        "Leadership qualities",
        "Strategic thinking",
        "Executive presence",
        "Industry legend"
      ],
      creative: [
        "Imagination awakens",
        "Artistic expression begins",
        "Creative skills develop",
        "Original ideas emerge", 
        "Design mastery grows",
        "Innovation breakthrough",
        "Visionary concepts",
        "Pioneer new paths",
        "Virtuoso performance",
        "Transcendent creativity"
      ],
      code: [
        "Hello World!",
        "Basic syntax mastery",
        "Problem-solving skills",
        "Complex applications",
        "System architecture",
        "Technical leadership",
        "Advanced algorithms",
        "Code ninja techniques", 
        "Programming wizard",
        "Elite hacker status"
      ],
      personal: [
        "Self-awareness begins",
        "Personal exploration",
        "Growth mindset",
        "New perspectives",
        "Finding direction", 
        "Inner strength",
        "Disciplined warrior",
        "Rising champion",
        "Personal hero",
        "Legendary mastery"
      ]
    };

    const categoryKey = category.name.toLowerCase();
    return descriptions[categoryKey]?.[nodeLevel - 1] || `Level ${nodeLevel} skill`;
  };

  // Hexagon SVG component
  const HexagonShape = ({ size = 48, color = '#4B5563', filled = false, glowing = false }) => (
    <svg width={size} height={size} viewBox="0 0 100 100" className="absolute inset-0">
      <defs>
        {glowing && (
          <filter id={`glow-${color.replace('#', '')}`}>
            <feGaussianBlur stdDeviation="3" result="coloredBlur"/>
            <feMerge> 
              <feMergeNode in="coloredBlur"/>
              <feMergeNode in="SourceGraphic"/>
            </feMerge>
          </filter>
        )}
      </defs>
      <polygon
        points="50,5 85,25 85,75 50,95 15,75 15,25"
        fill={filled ? color : 'transparent'}
        stroke={color}
        strokeWidth="3"
        filter={glowing ? `url(#glow-${color.replace('#', '')})` : undefined}
        style={{
          filter: glowing ? `drop-shadow(0 0 8px ${color}80)` : undefined
        }}
      />
    </svg>
  );

  // Sort categories for consistent order
  const sortedCategories = categories
    .filter(cat => (cat.xp || 0) >= 0)
    .sort((a, b) => {
      const order = ['work', 'study', 'creative', 'code', 'personal', 'health', 'finance', 'social'];
      const aIndex = order.indexOf(a.name.toLowerCase());
      const bIndex = order.indexOf(b.name.toLowerCase());
      return (aIndex === -1 ? 999 : aIndex) - (bIndex === -1 ? 999 : bIndex);
    });

  return (
    <div className="bg-gray-900 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] relative overflow-hidden"
         style={{
           borderColor: tabColor,
           boxShadow: `0 0 20px ${tabColor}40, 8px 8px 0px 0px rgba(0,0,0,1)`
         }}>

      {/* Background effects */}
      <div className="absolute inset-0 border-2 opacity-20 animate-pulse pointer-events-none" 
           style={{ borderColor: tabColor }} />
      <div className="absolute inset-0 pointer-events-none opacity-10"
           style={{ 
             background: `radial-gradient(circle at center, ${tabColor}30 0%, transparent 70%)` 
           }} />

      {/* Header */}
      <div 
        className="px-6 py-4 border-b-2 relative z-10 bg-black bg-opacity-60"
        style={{ 
          borderColor: tabColor
        }}
      >
        <div className="flex items-center justify-center">
          <div className="text-center">
            <div className="font-mono font-bold text-white text-3xl mb-2">
              SKILL TREE MATRIX
            </div>
            <div 
              className="font-mono text-sm font-bold tracking-wider"
              style={{ color: tabColor }}
            >
              {sortedCategories.length} ACTIVE SKILL BRANCHES
            </div>
          </div>
        </div>
      </div>

      {/* Unified Skill Tree Grid */}
      <div className="p-8 relative z-10">
        <div className="space-y-8">
          {sortedCategories.map((category, categoryIndex) => {
            const IconComponent = getIconComponent(category.iconName);
            const currentLevel = getCurrentLevel(category.xp || 0);
            const progress = getLevelProgress(category.xp || 0);
            const levelTitle = getLevelTitle(category, currentLevel);
            const branchColor = getBranchColor(category.name);
            const isMaxLevel = currentLevel === 10;

            return (
              <motion.div
                key={category.id}
                initial={{ opacity: 0, x: -50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: categoryIndex * 0.1 }}
                className="relative"
              >
                {/* Branch Header */}
                <div className="flex items-center mb-4">
                  <motion.div
                    className="w-16 h-16 border-2 rounded-full flex items-center justify-center mr-6 bg-black"
                    style={{ 
                      borderColor: branchColor,
                      boxShadow: `0 0 15px ${branchColor}60`
                    }}
                    whileHover={{ scale: 1.1, rotate: 5 }}
                  >
                    <IconComponent size={24} style={{ color: branchColor }} />
                  </motion.div>

                  <div className="flex-1">
                    <div className="flex items-center justify-between">
                      <div>
                        <div className="font-mono font-bold text-white text-xl">
                          {category.name.toUpperCase()} BRANCH
                        </div>
                        <div 
                          className="font-mono text-sm font-bold tracking-wider"
                          style={{ color: branchColor }}
                        >
                          {levelTitle}
                        </div>
                      </div>

                      <div className="flex items-center gap-4">
                        <div className="text-right">
                          <div 
                            className="text-2xl font-mono font-bold"
                            style={{ color: branchColor }}
                          >
                            {category.xp || 0} XP
                          </div>
                          <div className="text-xs text-gray-400">
                            {Math.floor((category.xp || 0) / 60)}h {(category.xp || 0) % 60}m
                          </div>
                        </div>

                        <div 
                          className="px-3 py-1 font-mono font-bold border-2 bg-black flex items-center gap-2"
                          style={{ 
                            borderColor: isMaxLevel ? '#FFD700' : branchColor,
                            color: isMaxLevel ? '#FFD700' : branchColor,
                            boxShadow: `0 0 10px ${isMaxLevel ? '#FFD700' : branchColor}60`
                          }}
                        >
                          {isMaxLevel ? <Crown size={16} /> : 
                           currentLevel >= 8 ? <Trophy size={16} /> :
                           currentLevel >= 6 ? <Shield size={16} /> :
                           <Star size={16} />}
                          LVL {currentLevel}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Horizontal Skill Path */}
                <div className="relative">
                  <div className="flex items-center space-x-2">
                    {Array.from({ length: 10 }, (_, index) => {
                      const nodeLevel = index + 1;
                      const isUnlocked = currentLevel >= nodeLevel;
                      const isCurrent = currentLevel === nodeLevel && currentLevel < 10;
                      const isMilestone = nodeLevel === 5 || nodeLevel === 10;
                      const nodeColor = isUnlocked ? (nodeLevel === 10 ? '#FFD700' : branchColor) : '#4B5563';

                      return (
                        <React.Fragment key={nodeLevel}>
                          {/* Skill Node */}
                          <motion.div
                            className="relative w-12 h-12 flex items-center justify-center cursor-pointer flex-shrink-0"
                            initial={{ scale: 0, rotate: -180 }}
                            animate={{ scale: 1, rotate: 0 }}
                            transition={{ delay: (categoryIndex * 0.5) + (nodeLevel * 0.05), type: "spring" }}
                            whileHover={{ scale: 1.2 }}
                            onHoverStart={() => setHoveredNode({ category: category.name, level: nodeLevel })}
                            onHoverEnd={() => setHoveredNode(null)}
                          >
                            <HexagonShape 
                              size={48}
                              color={nodeColor}
                              filled={isUnlocked}
                              glowing={isUnlocked && (isCurrent || isMilestone)}
                            />

                            <div className={`relative z-20 ${isCurrent ? 'animate-pulse' : ''}`}>
                              {isMilestone && isUnlocked ? (
                                <motion.div
                                  animate={{ 
                                    rotate: nodeLevel === 10 ? [0, 360] : [0, 180, 360],
                                    scale: nodeLevel === 10 ? [1, 1.2, 1] : [1, 1.1, 1]
                                  }}
                                  transition={{ 
                                    duration: nodeLevel === 10 ? 4 : 2, 
                                    repeat: Infinity 
                                  }}
                                  style={{ color: isUnlocked ? (nodeLevel === 10 ? '#000000' : '#000000') : '#6B7280' }}
                                >
                                  {nodeLevel === 10 ? <Crown size={16} /> : <Trophy size={16} />}
                                </motion.div>
                              ) : isUnlocked ? (
                                <Zap size={12} style={{ color: '#000000' }} />
                              ) : (
                                <div className="font-mono font-bold text-xs text-gray-400">
                                  {nodeLevel}
                                </div>
                              )}
                            </div>

                            {/* Hover tooltip */}
                            {hoveredNode?.category === category.name && hoveredNode?.level === nodeLevel && (
                              <motion.div
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                className="absolute bottom-full mb-3 left-1/2 transform -translate-x-1/2 bg-black border-2 px-3 py-2 rounded whitespace-nowrap z-50"
                                style={{ borderColor: nodeLevel === 10 ? '#FFD700' : branchColor }}
                              >
                                <div 
                                  className="text-sm font-mono font-bold"
                                  style={{ color: nodeLevel === 10 ? '#FFD700' : branchColor }}
                                >
                                  Level {nodeLevel} {nodeLevel === 10 ? '- MASTERY' : ''}
                                </div>
                                <div className="text-xs text-gray-300">
                                  {getNodeDescription(category, nodeLevel)}
                                </div>
                              </motion.div>
                            )}
                          </motion.div>

                          {/* Connecting Line */}
                          {index < 9 && (
                            <div 
                              className="flex-shrink-0 h-0.5 w-4 transition-all duration-500"
                              style={{
                                backgroundColor: isUnlocked && currentLevel > nodeLevel ? branchColor : '#4B5563',
                                boxShadow: isUnlocked && currentLevel > nodeLevel ? `0 0 4px ${branchColor}` : 'none'
                              }}
                            />
                          )}
                        </React.Fragment>
                      );
                    })}
                  </div>

                  {/* Progress Bar for Branch */}
                  <div className="mt-4 space-y-2">
                    <div className="flex justify-between text-xs font-mono">
                      <span className="text-gray-400">
                        {currentLevel < 10 ? `NEXT: LEVEL ${currentLevel + 1}` : 'BRANCH MASTERED'}
                      </span>
                      <span style={{ color: branchColor }}>
                        {currentLevel < 10 ? `${progress.current}/${progress.max} XP` : 'COMPLETED'}
                      </span>
                    </div>

                    <div className="relative">
                      <div 
                        className="w-full h-2 border bg-gray-800"
                        style={{ borderColor: branchColor }}
                      >
                        <div 
                          className="h-full transition-all duration-1000 relative overflow-hidden"
                          style={{ 
                            width: `${progress.percentage}%`,
                            background: `linear-gradient(90deg, ${branchColor}, ${branchColor}CC)`
                          }}
                        >
                          {/* Scan line */}
                          <motion.div 
                            className="absolute inset-0 opacity-60"
                            style={{
                              background: 'linear-gradient(90deg, transparent 0%, rgba(255,255,255,0.8) 50%, transparent 100%)'
                            }}
                            animate={{ x: ['-100%', '200%'] }}
                            transition={{ 
                              duration: 3,
                              repeat: Infinity,
                              repeatType: 'loop',
                              ease: 'linear'
                            }}
                          />
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </motion.div>
            );
          })}
        </div>

        {/* Overall Stats */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: sortedCategories.length * 0.1 + 0.3 }}
          className="mt-12 grid grid-cols-3 gap-6 bg-black bg-opacity-40 p-6 border-2"
          style={{ borderColor: tabColor }}
        >
          <div className="text-center">
            <div 
              className="text-3xl font-mono font-bold"
              style={{ color: tabColor }}
            >
              {sortedCategories.reduce((sum, cat) => sum + (cat.xp || 0), 0)}
            </div>
            <div className="text-sm text-gray-400 font-mono">TOTAL XP</div>
          </div>
          <div className="text-center">
            <div 
              className="text-3xl font-mono font-bold"
              style={{ color: tabColor }}
            >
              {sortedCategories.filter(cat => getCurrentLevel(cat.xp || 0) === 10).length}
            </div>
            <div className="text-sm text-gray-400 font-mono">MASTERED BRANCHES</div>
          </div>
          <div className="text-center">
            <div 
              className="text-3xl font-mono font-bold"
              style={{ color: tabColor }}
            >
              {Math.floor(sortedCategories.reduce((sum, cat) => sum + (cat.xp || 0), 0) / 60)}h
            </div>
            <div className="text-sm text-gray-400 font-mono">TOTAL GRIND TIME</div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default HexagonalSkillTree;
