import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Plus, Search, FileText, Clock, Save, X, Maximize2, Minimize2 } from 'lucide-react';
import PixelButton from '../PixelButton';
import PixelInput from '../PixelInput';
import NoteCard from '../notes/NoteCard';
import NoteModal from '../notes/NoteModal';
import useNotes from '../../hooks/useNotes';
import useFolders from '../../hooks/useFolders';
import useNotebooks from '../../hooks/useNotebooks';

const NotesTab = () => {
  const { notes, loading, error, deleteNote, createNote, updateNote } = useNotes();
  const { folders } = useFolders();
  const { notebooks } = useNotebooks();

  // Quick entry state
  const [quickTitle, setQuickTitle] = useState('');
  const [quickContent, setQuickContent] = useState('');
  const [quickTags, setQuickTags] = useState('');
  const [isSaving, setIsSaving] = useState(false);
  const [saveStatus, setSaveStatus] = useState('READY');
  const [isFullscreen, setIsFullscreen] = useState(false);

  // Modal state
  const [activeNote, setActiveNote] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  // Get recent notes (last 8)
  const recentNotes = notes
    .sort((a, b) => new Date(b.createdAt || b.date) - new Date(a.createdAt || a.date))
    .slice(0, 8);

  // Auto-save functionality
  useEffect(() => {
    if (quickTitle.trim() || quickContent.trim()) {
      setSaveStatus('TYPING...');
    } else {
      setSaveStatus('READY');
    }
  }, [quickTitle, quickContent]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Escape to exit fullscreen
      if (e.key === 'Escape' && isFullscreen) {
        e.preventDefault();
        setIsFullscreen(false);
      }
      // Ctrl/Cmd + Enter to save
      if ((e.ctrlKey || e.metaKey) && e.key === 'Enter') {
        e.preventDefault();
        if (!isSaving && (quickTitle.trim() || quickContent.trim())) {
          handleQuickSave();
        }
      }
      // F11 to toggle fullscreen
      if (e.key === 'F11') {
        e.preventDefault();
        setIsFullscreen(prev => !prev);
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isFullscreen, isSaving, quickTitle, quickContent]); // Fixed dependencies

  const handleQuickSave = async () => {
    if (!quickTitle.trim() && !quickContent.trim()) return;

    setIsSaving(true);
    setSaveStatus('SAVING...');

    try {
      const noteData = {
        title: quickTitle.trim() || 'Untitled Note',
        content: quickContent.trim(),
        tags: quickTags.trim() ? quickTags.split(',').map(tag => tag.trim()) : [],
        color: '#4ADE80', // Default green
      };

      await createNote(noteData);

      // Clear form
      setQuickTitle('');
      setQuickContent('');
      setQuickTags('');
      setSaveStatus('SAVED!');

      // Reset status after 2 seconds
      setTimeout(() => setSaveStatus('READY'), 2000);

    } catch (error) {
      console.error('Failed to save quick note:', error);
      setSaveStatus('ERROR');
      setTimeout(() => setSaveStatus('READY'), 3000);
    } finally {
      setIsSaving(false);
    }
  };

  const handleClearForm = () => {
    setQuickTitle('');
    setQuickContent('');
    setQuickTags('');
    setSaveStatus('READY');
  };

  const handleEditNote = (note) => {
    setActiveNote(note);
    setIsEditModalOpen(true);
  };

  const handleUpdateNote = async (noteData) => {
    try {
      await updateNote(activeNote.id, noteData);
      setIsEditModalOpen(false);
      setActiveNote(null);
    } catch (error) {
      console.error('Failed to update note:', error);
      alert('Failed to update note: ' + error.message);
    }
  };

  const toggleFullscreen = () => {
    console.log('Toggling fullscreen:', !isFullscreen);
    setIsFullscreen(!isFullscreen);
  };

  // Input styles with forced white text
  const inputStyles = {
    backgroundColor: '#111827',
    color: '#ffffff',
    border: '2px solid #4b5563',
    fontFamily: 'monospace',
  };

  const inputFocusStyles = {
    ...inputStyles,
    borderColor: '#22d3ee',
  };

  // Fullscreen Modal Component
  const FullscreenTerminal = () => (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 bg-black bg-opacity-95 z-[9999] flex items-center justify-center p-4"
      onClick={(e) => {
        if (e.target === e.currentTarget) {
          setIsFullscreen(false);
        }
      }}
    >
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        className="bg-gray-800 border-2 border-cyan-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-8 relative w-full max-w-6xl h-full max-h-[90vh] overflow-auto"
        style={{
          boxShadow: '0 0 30px rgba(34, 211, 238, 0.4), 8px 8px 0px 0px rgba(0,0,0,1)'
        }}
      >
        <div className="absolute inset-0 border-2 border-cyan-400 opacity-30 animate-pulse pointer-events-none" />

        {/* Fullscreen Header */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center gap-4">
            <h2 className="text-2xl font-mono font-bold text-white">FULLSCREEN ENTRY TERMINAL</h2>
            <div className="flex items-center gap-2 text-sm font-mono">
              <div className={`w-3 h-3 ${
                saveStatus === 'READY' ? 'bg-green-400' :
                saveStatus === 'TYPING...' ? 'bg-yellow-400' :
                saveStatus === 'SAVING...' ? 'bg-blue-400' :
                saveStatus === 'SAVED!' ? 'bg-green-400' :
                'bg-red-400'
              }`} />
              <span className="text-cyan-400">{saveStatus}</span>
            </div>
          </div>
          <button
            onClick={() => setIsFullscreen(false)}
            className="bg-gray-900 border-2 border-cyan-400 p-2 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] text-cyan-400"
            style={{
              boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
            }}
          >
            <Minimize2 size={20} />
            <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
          </button>
        </div>

        {/* Fullscreen Form */}
        <div className="space-y-6">
          {/* Title Input */}
          <div>
            <label className="block text-lg font-mono text-gray-400 mb-3">TITLE</label>
            <input
              value={quickTitle}
              onChange={(e) => setQuickTitle(e.target.value)}
              placeholder="Enter note title..."
              className="w-full px-6 py-4 transition-colors placeholder-gray-500 bg-gray-900 border-2 border-gray-600 font-mono text-lg focus:border-cyan-400 focus:outline-none"
              style={{ color: '#ffffff' }}
              autoFocus
            />
          </div>

          {/* Content Input */}
          <div className="flex-1">
            <label className="block text-lg font-mono text-gray-400 mb-3">CONTENT</label>
            <textarea
              value={quickContent}
              onChange={(e) => setQuickContent(e.target.value)}
              placeholder="Start typing your note content..."
              className="w-full px-6 py-4 transition-colors resize-none placeholder-gray-500 bg-gray-900 border-2 border-gray-600 font-mono text-lg focus:border-cyan-400 focus:outline-none"
              style={{ color: '#ffffff' }}
              rows={20}
            />
          </div>

          {/* Tags Input */}
          <div>
            <label className="block text-lg font-mono text-gray-400 mb-3">TAGS (comma separated)</label>
            <input
              value={quickTags}
              onChange={(e) => setQuickTags(e.target.value)}
              placeholder="idea, project, important..."
              className="w-full px-6 py-4 transition-colors placeholder-gray-500 bg-gray-900 border-2 border-gray-600 font-mono text-lg focus:border-cyan-400 focus:outline-none"
              style={{ color: '#ffffff' }}
            />
          </div>

          {/* Fullscreen Action Buttons */}
          <div className="flex gap-4 pt-6">
            <button
              onClick={handleQuickSave}
              disabled={isSaving || (!quickTitle.trim() && !quickContent.trim())}
              className="bg-gray-900 border-2 border-cyan-400 px-8 py-4 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] font-mono font-bold text-cyan-400 disabled:opacity-50 disabled:cursor-not-allowed"
              style={{
                boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
              }}
            >
              <div className="flex items-center gap-3">
                <Save size={20} className="text-cyan-400" />
                <span className="text-cyan-400 text-lg">{isSaving ? 'SAVING...' : 'SAVE ENTRY'}</span>
              </div>
              <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
            </button>

            <button
              onClick={handleClearForm}
              className="bg-gray-900 border-2 border-cyan-400 px-6 py-4 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] font-mono font-bold text-cyan-400"
              style={{
                boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
              }}
            >
              <div className="flex items-center gap-3">
                <X size={20} className="text-cyan-400" />
                <span className="text-cyan-400 text-lg">CLEAR</span>
              </div>
              <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
            </button>
          </div>

          {/* Keyboard Shortcuts Help */}
          <div className="text-xs font-mono text-gray-500 pt-4 border-t border-gray-600">
            <div className="flex flex-wrap gap-4">
              <span>ESC: Exit fullscreen</span>
              <span>CTRL/CMD + ENTER: Save</span>
              <span>F11: Toggle fullscreen</span>
            </div>
          </div>
        </div>
      </motion.div>
    </motion.div>
  );

  return (
    <div className="notes-tab-container p-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="font-mono text-3xl font-bold text-white mb-2 flex items-center gap-3">
          <div className="w-6 h-6 bg-purple-400 border border-gray-600" />
          RAPID DEPLOYMENT SYSTEM
        </h1>
        <p className="text-gray-400 font-mono text-sm">
          Quick entry terminal for immediate data capture and recent activity monitoring
        </p>
      </div>

      {/* Error Display */}
      {error && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-gray-800 border-2 border-red-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-4 mb-6"
          style={{
            boxShadow: '0 0 20px rgba(239, 68, 68, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
          }}
        >
          <div className="font-mono text-red-400">
            ERROR: {error}
          </div>
        </motion.div>
      )}

      {/* Split Layout */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">

        {/* Left Side - Quick Entry Terminal (2/3 width) */}
        <div className="lg:col-span-2">
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="bg-gray-800 border-2 border-cyan-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 relative h-full"
            style={{
              boxShadow: '0 0 20px rgba(34, 211, 238, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
            }}
          >
            <div className="absolute inset-0 border-2 border-cyan-400 opacity-30 animate-pulse pointer-events-none" />

            {/* Header */}
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-mono font-bold text-white">ENTRY TERMINAL</h2>
              <div className="flex items-center gap-4">
                <div className="flex items-center gap-2 text-xs font-mono">
                  <div className={`w-2 h-2 ${
                    saveStatus === 'READY' ? 'bg-green-400' :
                    saveStatus === 'TYPING...' ? 'bg-yellow-400' :
                    saveStatus === 'SAVING...' ? 'bg-blue-400' :
                    saveStatus === 'SAVED!' ? 'bg-green-400' :
                    'bg-red-400'
                  }`} />
                  <span className="text-cyan-400">{saveStatus}</span>
                </div>
                <button
                  onClick={() => {
                    console.log('Fullscreen button clicked');
                    toggleFullscreen();
                  }}
                  className="bg-gray-900 border border-cyan-400 p-2 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_10px_rgba(34,211,238,0.3)] text-cyan-400"
                  style={{
                    boxShadow: '0 0 3px rgba(34, 211, 238, 0.2), 1px 1px 0px 0px rgba(0,0,0,1)'
                  }}
                  title="Fullscreen Mode (F11)"
                >
                  <Maximize2 size={16} />
                  <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
                </button>
              </div>
            </div>

            {/* Quick Entry Form */}
            <div className="space-y-4">
              {/* Title Input */}
              <div>
                <label className="block text-sm font-mono text-gray-400 mb-2">TITLE</label>
                <input
                  value={quickTitle}
                  onChange={(e) => setQuickTitle(e.target.value)}
                  placeholder="Enter note title..."
                  className="w-full px-4 py-3 transition-colors placeholder-gray-500 bg-gray-900 border-2 border-gray-600 font-mono text-sm focus:border-cyan-400 focus:outline-none"
                  style={{ color: '#ffffff' }}
                  autoFocus
                />
              </div>

              {/* Content Input */}
              <div>
                <label className="block text-sm font-mono text-gray-400 mb-2">CONTENT</label>
                <textarea
                  value={quickContent}
                  onChange={(e) => setQuickContent(e.target.value)}
                  placeholder="Start typing your note content..."
                  className="w-full px-4 py-3 transition-colors resize-none placeholder-gray-500 bg-gray-900 border-2 border-gray-600 font-mono text-sm focus:border-cyan-400 focus:outline-none"
                  style={{ color: '#ffffff' }}
                  rows={8}
                />
              </div>

              {/* Tags Input */}
              <div>
                <label className="block text-sm font-mono text-gray-400 mb-2">TAGS (comma separated)</label>
                <input
                  value={quickTags}
                  onChange={(e) => setQuickTags(e.target.value)}
                  placeholder="idea, project, important..."
                  className="w-full px-4 py-3 transition-colors placeholder-gray-500 bg-gray-900 border-2 border-gray-600 font-mono text-sm focus:border-cyan-400 focus:outline-none"
                  style={{ color: '#ffffff' }}
                />
              </div>

              {/* Action Buttons */}
              <div className="flex gap-3 pt-4">
                <button
                  onClick={handleQuickSave}
                  disabled={isSaving || (!quickTitle.trim() && !quickContent.trim())}
                  className="bg-gray-900 border-2 border-cyan-400 px-6 py-3 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] font-mono font-bold text-cyan-400 disabled:opacity-50 disabled:cursor-not-allowed"
                  style={{
                    boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
                  }}
                >
                  <div className="flex items-center gap-2">
                    <Save size={18} className="text-cyan-400" />
                    <span className="text-cyan-400">{isSaving ? 'SAVING...' : 'SAVE ENTRY'}</span>
                  </div>
                  <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
                </button>

                <button
                  onClick={handleClearForm}
                  className="bg-gray-900 border-2 border-cyan-400 px-4 py-3 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] font-mono font-bold text-cyan-400"
                  style={{
                    boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
                  }}
                >
                  <div className="flex items-center gap-2">
                    <X size={18} className="text-cyan-400" />
                    <span className="text-cyan-400">CLEAR</span>
                  </div>
                  <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
                </button>
              </div>
            </div>
          </motion.div>
        </div>

        {/* Right Side - Recent Activity Feed (1/3 width) */}
        <div className="lg:col-span-1">
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.1 }}
            className="bg-gray-800 border-2 border-cyan-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 relative h-full"
            style={{
              boxShadow: '0 0 20px rgba(34, 211, 238, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
            }}
          >
            <div className="absolute inset-0 border-2 border-cyan-400 opacity-30 animate-pulse pointer-events-none" />

            {/* Header */}
            <div className="flex items-center gap-2 mb-6">
              <Clock size={20} className="text-cyan-400" />
              <h2 className="text-xl font-mono font-bold text-white">ACTIVITY LOG</h2>
            </div>

            {/* Recent Notes List */}
            {loading ? (
              <div className="text-center py-8 font-mono text-white">
                <motion.div
                  animate={{ rotate: 360 }}
                  transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                  className="inline-block mb-4"
                >
                  <FileText size={24} className="text-cyan-400" />
                </motion.div>
                <div className="text-sm">Loading...</div>
              </div>
            ) : recentNotes.length > 0 ? (
              <div className="space-y-3">
                {recentNotes.map(note => {
                  const noteColor = note.color || note.colorCode || '#4ADE80';
                  const hexToRgb = (hex) => {
                    if (!hex || !hex.startsWith('#')) return '74, 222, 128';
                    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
                    return result ? 
                      `${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}` :
                      '74, 222, 128';
                  };
                  const rgbColor = hexToRgb(noteColor);

                  return (
                    <motion.div
                      key={note.id || note.filename || note.title}
                      className="bg-gray-900 border-2 p-3 cursor-pointer group transition-all duration-300 hover:scale-[1.02]"
                      style={{
                        borderColor: noteColor,
                        boxShadow: `0 0 5px rgba(${rgbColor}, 0.3), 1px 1px 0px 0px rgba(0,0,0,1)`,
                      }}
                      whileHover={{
                        boxShadow: `0 0 10px rgba(${rgbColor}, 0.6), 1px 1px 0px 0px rgba(0,0,0,1)`
                      }}
                      onClick={() => handleEditNote(note)}
                    >
                      <div className="flex items-start gap-2">
                        <FileText size={14} style={{ color: noteColor }} className="mt-0.5 flex-shrink-0" />
                        <div className="flex-1 min-w-0">
                          <h4 className="font-mono font-bold text-white text-sm truncate mb-1">
                            {note.title}
                          </h4>
                          <p className="text-xs text-gray-400 line-clamp-2">
                            {note.content && note.content.length > 60 
                              ? `${note.content.substring(0, 60)}...` 
                              : note.content}
                          </p>
                          <div className="text-xs text-gray-500 font-mono mt-1">
                            {new Date(note.createdAt || note.date).toLocaleDateString()}
                          </div>
                        </div>
                      </div>
                    </motion.div>
                  );
                })}

                {/* View All Button */}
                <div className="pt-4 border-t border-gray-600">
                  <button className="w-full bg-gray-900 border border-cyan-400 px-3 py-2 text-cyan-400 font-mono text-sm hover:bg-gray-800 transition-colors hover:shadow-[0_0_10px_rgba(34,211,238,0.3)]"
                    style={{
                      boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 1px 1px 0px 0px rgba(0,0,0,1)'
                    }}
                  >
                    <span className="text-cyan-400">VIEW ALL ENTRIES </span>
                  </button>
                </div>
              </div>
            ) : (
              <div className="text-center py-8">
                <FileText size={32} className="text-gray-500 mx-auto mb-3" />
                <p className="text-gray-400 font-mono text-sm">No recent entries</p>
                <p className="text-gray-500 font-mono text-xs mt-1">Create your first note!</p>
              </div>
            )}
          </motion.div>
        </div>
      </div>

      {/* Fullscreen Modal with AnimatePresence */}
      <AnimatePresence mode="wait">
        {isFullscreen && <FullscreenTerminal />}
      </AnimatePresence>

      {/* Edit Modal */}
      <NoteModal
        isOpen={isEditModalOpen}
        onClose={() => {
          setIsEditModalOpen(false);
          setActiveNote(null);
        }}
        onSave={handleUpdateNote}
        folders={folders}
        notebooks={notebooks}
        existingNote={activeNote}
      />
    </div>
  );
};

export default NotesTab;
