import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Crown, Zap, Target, Star, Trophy, X, ChevronDown, Palette, Award, Settings, Camera } from 'lucide-react';
import PixelPageJenn from '../../assets/icons/PixelPageJenn.PNG';
import TaskStats from './TaskStats';
import { playerRanks, getRankByXP, getNextRank, getRankByLevel } from '../../data/ranks';
import achievementService from '../../services/achievementService';
import { useTheme } from '../../contexts/ThemeContext';
import { allAchievements, tierInfo } from '../../data/achievements';
import { defaultProfilePics, getSelectedProfilePic, saveProfilePicSelection, getProfilePicById } from '../../data/profilePics';

const HeroCard = ({ player, notes = [], tasks = [], taskLists = [] }) => {
  const [showEasterEgg, setShowEasterEgg] = useState(false);
  const [showStyleDropdown, setShowStyleDropdown] = useState(false);
  const [showBadgeSelector, setShowBadgeSelector] = useState(false);
  const [showProfilePicEditor, setShowProfilePicEditor] = useState(false);
  const [selectedBadges, setSelectedBadges] = useState(() => {
    const saved = localStorage.getItem('selectedProfileBadges');
    return saved ? JSON.parse(saved) : [];
  });
  const [selectedProfilePic, setSelectedProfilePic] = useState(() => getSelectedProfilePic());
  const { currentTheme, themes, switchTheme, getThemeColors } = useTheme();

  const themeColors = getThemeColors();

  // Badge management functions
  const getUnlockedAchievements = () => {
    return achievementService.getUnlockedAchievements();
  };

  const toggleBadgeSelection = (achievementId) => {
    setSelectedBadges(prev => {
      let newSelection;
      if (prev.includes(achievementId)) {
        // Remove badge
        newSelection = prev.filter(id => id !== achievementId);
      } else if (prev.length < 3) {
        // Add badge (max 3)
        newSelection = [...prev, achievementId];
      } else {
        // Replace oldest badge
        newSelection = [...prev.slice(1), achievementId];
      }
      localStorage.setItem('selectedProfileBadges', JSON.stringify(newSelection));
      return newSelection;
    });
  };

  // Profile pic management
  const changeProfilePic = (picId) => {
    setSelectedProfilePic(picId);
    saveProfilePicSelection(picId);
    setShowProfilePicEditor(false);
  };

  const getCurrentProfilePicData = () => {
    return getProfilePicById(selectedProfilePic);
  };

  // Get highest skill from tracker data
  const getHighestSkill = () => {
    try {
      const categories = JSON.parse(localStorage.getItem('focusCategories') || '[]');
      if (categories.length === 0) return null;

      // Calculate level for each skill (100 XP per level)
      const skillsWithLevels = categories.map(category => ({
        ...category,
        level: Math.floor(category.xp / 100) + 1
      }));

      // Find the highest level skill
      const highestSkill = skillsWithLevels.reduce((highest, current) => 
        current.level > highest.level ? current : highest
      );

      return highestSkill.level > 1 ? highestSkill : null;
    } catch (error) {
      console.error('Error getting highest skill:', error);
      return null;
    }
  };

  // Calculate XP and level (matching your GameUtilities.java logic)
  const calculateTotalXP = (notes) => {
    let totalXP = 0;

    for (const note of notes) {
      // Base XP per note
      totalXP += 10;

      // Bonus XP for content length
      const wordCount = note.content ? note.content.split(/\s+/).length : 0;
      totalXP += Math.min(Math.floor(wordCount / 10), 50);

      // Bonus XP for using tags
      const tags = note.tagsString ? note.tagsString.split(',').filter(tag => tag.trim()) : [];
      totalXP += tags.length * 5;

      // Bonus XP for longer titles
      if (note.title && note.title.length > 20) {
        totalXP += 5;
      }
    }

    return totalXP;
  };

  const calculateLevelFromXP = (totalXP) => {
    // New exponential leveling system - much harder progression
    // Level 1: 0-499 XP, Level 2: 500-1499 XP, Level 3: 1500-2999 XP, etc.
    // Each level requires 1000 more XP than the previous gap
    let level = 1;
    let requiredXP = 0;
    let xpGap = 500; // Starting gap for level 2

    while (totalXP >= requiredXP + xpGap) {
      requiredXP += xpGap;
      level++;
      xpGap += 1000; // Each level requires 1000 more XP than previous gap
    }

    return level;
  };

  const getCurrentRankInfo = (level, totalXP) => {
    // Get rank by XP (more accurate than by level)
    const currentRank = getRankByXP(totalXP);
    const nextRank = getNextRank(currentRank.level);

    return {
      current: currentRank,
      next: nextRank
    };
  };

  // Calculate player stats
  const achievementStats = achievementService.getStats();
  const totalXP = achievementStats.totalXP || calculateTotalXP(notes); // Use achievement XP if available
  const currentLevel = calculateLevelFromXP(totalXP);
  const rankInfo = getCurrentRankInfo(currentLevel, totalXP);

  // XP progress calculation for current rank
  const currentRankXP = rankInfo.current.minXP;
  const nextRankXP = rankInfo.next ? rankInfo.next.minXP : currentRankXP + 1000;
  const xpInCurrentRank = totalXP - currentRankXP;
  const xpNeededForNextRank = nextRankXP - currentRankXP;
  const progressPercentage = Math.floor((xpInCurrentRank / xpNeededForNextRank) * 100);

  const playerTitle = rankInfo.current.name;
  const nextLevelTitle = rankInfo.next ? rankInfo.next.name : 'Max Rank Achieved!';

  // Generate pixel avatar based on username
  const generatePixelAvatar = (username) => {
    const colors = [
      '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FECA57',
      '#FF9FF3', '#54A0FF', '#5F27CD', '#00D2D3', '#FF9F43'
    ];

    const initial = username ? username.charAt(0).toUpperCase() : 'P';
    const colorIndex = username ? username.length % colors.length : 0;
    const bgColor = colors[colorIndex];

    return { initial, bgColor };
  };

  // Mock player data if no player provided
  const mockPlayer = {
    username: "user",
    level: 1,
    experience: 0
  };

  const currentPlayer = player || mockPlayer;
  const highestSkill = getHighestSkill();

  return (
    <div className="space-y-6">
      {/* Main Profile Section - Updated with theme colors */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-gray-900 border p-8 relative overflow-hidden"
        style={{
          borderColor: themeColors.secondary,
          boxShadow: `0 0 3px ${themeColors.secondary}50, 1px 1px 0px 0px rgba(0,0,0,1)`
        }}
      >
        {/* Gradient overlay with theme colors */}
        <div className="absolute inset-0 pointer-events-none"
             style={{
               background: `linear-gradient(to bottom right, ${themeColors.secondary}15, ${themeColors.secondary}20)`
             }} />

        {/* Profile Style Button - Top Right - Updated to pink theme */}
        <div className="absolute top-4 right-4 z-20">
          <div className="relative">
            <button
              onClick={() => setShowStyleDropdown(!showStyleDropdown)}
              className="bg-gray-900 border px-3 py-2 relative group cursor-pointer transition-all duration-300 hover:shadow-[0_0_8px_rgba(244,114,182,0.4)] font-mono font-bold overflow-hidden flex items-center gap-2"
              style={{
                borderColor: themeColors.primary,
                color: themeColors.primary,
                boxShadow: `0 0 3px ${themeColors.primary}50, 1px 1px 0px 0px rgba(0,0,0,1)`
              }}
            >
              <div className="absolute inset-0 pointer-events-none" 
                   style={{
                     background: `linear-gradient(to bottom right, ${themeColors.primary}08, ${themeColors.primary}12)`
                   }} />
              <div className="relative z-10 flex items-center gap-2">
                <Palette size={14} style={{ color: themeColors.primary }} />
                <span className="text-xs">PROFILE THEME</span>
                <ChevronDown 
                  size={12} 
                  className={`transition-transform duration-200 ${showStyleDropdown ? 'rotate-180' : ''}`} 
                  style={{ color: themeColors.primary }}
                />
              </div>
              <div className="absolute inset-0 opacity-0 group-hover:opacity-5 transition-opacity"
                   style={{ backgroundColor: themeColors.primary }} />
            </button>

            {/* Dropdown Menu - Updated to pink theme */}
            <AnimatePresence>
              {showStyleDropdown && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  className="absolute top-full right-0 mt-2 bg-gray-900 border shadow-[4px_4px_0px_0px_rgba(0,0,0,1)] min-w-[200px] z-30"
                  style={{
                    borderColor: themeColors.primary,
                    boxShadow: `0 0 10px ${themeColors.primary}30, 4px 4px 0px 0px rgba(0,0,0,1)`
                  }}
                >
                  <div className="absolute inset-0 pointer-events-none" 
                       style={{
                         background: `linear-gradient(to bottom right, ${themeColors.primary}08, ${themeColors.primary}12)`
                       }} />
                  <div className="relative z-10 p-3">
                    <div className="text-xs font-mono font-bold mb-2" style={{ color: themeColors.primary }}>
                      CHOOSE THEME:
                    </div>

                    {/* Style Options */}
                    <div className="space-y-2">
                      {Object.entries(themes).map(([key, theme]) => (
                        <button
                          key={key}
                          onClick={() => {
                            switchTheme(key);
                            setShowStyleDropdown(false);
                          }}
                          className={`w-full text-left px-3 py-2 text-xs font-mono transition-colors border ${
                            currentTheme === key 
                              ? 'border-opacity-100 bg-gray-800' 
                              : 'border-gray-600 hover:bg-gray-800'
                          }`}
                          style={{
                            color: currentTheme === key ? theme.primary : '#9CA3AF',
                            borderColor: currentTheme === key ? theme.primary : '#4B5563'
                          }}
                        >
                          {theme.name}
                        </button>
                      ))}
                    </div>
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </div>

        <div className="relative z-10 flex items-start gap-8 lg:gap-12">
          {/* Avatar and Level Badge section - Updated with purple theme */}
          <div className="flex-shrink-0">
            <div className="relative">            <div className="w-32 h-32 lg:w-40 lg:h-40 border-2 shadow-[6px_6px_0px_0px_rgba(0,0,0,1)] flex items-center justify-center relative overflow-hidden bg-gray-700"
                 style={{
                   borderColor: themeColors.secondary,
                   boxShadow: `0 0 10px ${themeColors.secondary}30, 6px 6px 0px 0px rgba(0,0,0,1)`
                 }}>

                {/* Custom Pixel Profile Image */}
                <img 
                  src={PixelPageJenn} // For now, keep using the original until we have actual images
                  alt="Pixel Profile Avatar"
                  className="w-full h-full object-cover relative z-10"
                  style={{ 
                    imageRendering: 'pixelated',
                    imageRendering: '-moz-crisp-edges',
                    imageRendering: 'crisp-edges'
                  }}
                />

                {/* Shimmer Overlay */}
                <div 
                  className="absolute inset-0 opacity-20 z-20 pointer-events-none"
                  style={{
                    background: `linear-gradient(
                      90deg,
                      transparent 0%,
                      transparent 40%,
                      rgba(255, 255, 255, 0.6) 45%,
                      rgba(255, 255, 255, 0.6) 50%,
                      rgba(255, 255, 255, 0.6) 55%,
                      transparent 60%,
                      transparent 100%
                    )`,
                    animation: 'shimmer-slide 5s ease-in-out infinite',
                    backgroundSize: '200% 100%'
                  }}
                />

                {/* Level decorations overlay */}
                <div className="absolute inset-0 flex items-center justify-center z-30">
                  {/* Crown for high levels */}
                  {currentLevel >= 8 && (
                    <Crown size={20} className="absolute -top-4 -right-3 text-yellow-400 drop-shadow-lg" />
                  )}
                  {/* Star effects for legendary levels */}
                  {currentLevel >= 10 && (
                    <>
                      <Star size={16} className="absolute -top-2 -left-4 text-yellow-300 animate-pulse drop-shadow-lg" />
                      <Star size={14} className="absolute -bottom-2 -right-4 text-yellow-300 animate-pulse drop-shadow-lg" style={{ animationDelay: '0.5s' }} />
                    </>
                  )}
                </div>

                {/* Level ring around avatar for high levels */}
                {currentLevel >= 5 && (
                  <div className="absolute inset-0 border-2 border-purple-400 opacity-60 animate-pulse z-40" />
                )}
              </div>

              {/* Profile Pic Edit Button */}
              <button
                onClick={() => setShowProfilePicEditor(true)}
                className="absolute -bottom-2 -right-2 w-8 h-8 bg-gray-900 border-2 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)] flex items-center justify-center hover:bg-gray-800 transition-colors z-50"
                style={{
                  borderColor: themeColors.primary,
                  boxShadow: `0 0 6px ${themeColors.primary}40, 2px 2px 0px 0px rgba(0,0,0,1)`
                }}
              >
                <Camera size={14} style={{ color: themeColors.primary }} />
              </button>
            </div>

            {/* Level Badge - updated with pink theme and shiny effects */}
            <div className="mt-4 relative">
              <div className="bg-gray-900 border-2 shadow-[4px_4px_0px_0px_rgba(0,0,0,1)] text-center px-4 py-2 relative overflow-hidden group"
                   style={{
                     borderColor: themeColors.primary,
                     boxShadow: `0 0 10px ${themeColors.primary}30, 4px 4px 0px 0px rgba(0,0,0,1)`
                   }}>
                {/* Shiny sweep animation */}
                <div className="absolute inset-0 bg-gradient-to-r from-transparent via-pink-400 to-transparent opacity-30 -skew-x-12 translate-x-[-100%] group-hover:translate-x-[200%] transition-transform duration-1000" />
                {/* Additional shine effect */}
                <div className="absolute inset-0 bg-gradient-to-br from-pink-300/20 via-transparent to-pink-500/20 animate-pulse" />
                <span className="font-mono font-bold text-lg relative z-10" style={{ color: themeColors.primary }}>LVL {currentLevel}</span>
              </div>

              {/* Online Status Indicator - unchanged */}
              <div className="mt-2 flex items-center justify-center gap-2 bg-gray-900 border border-gray-600 px-3 py-1 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)]">
                <button
                  onClick={() => setShowEasterEgg(true)}
                  className="w-2 h-2 bg-green-400 rounded-full animate-pulse cursor-pointer hover:bg-green-300 transition-colors" 
                  style={{ 
                    boxShadow: '0 0 6px rgba(34, 197, 94, 0.8)' 
                  }}
                  title=""
                />
                <span className="font-mono text-xs font-bold text-green-400">ONLINE</span>
              </div>
            </div>
          </div>

          {/* Player Info - updated with purple theme */}
          <div className="flex-1 min-w-0">
            <div className="mb-2">
              <span className="text-lg font-mono block" style={{ color: themeColors.secondary }}>USERNAME:</span>
              <div className="flex items-center gap-3 flex-wrap">
                <h2 className="text-3xl lg:text-4xl font-bold font-mono text-white break-words">
                  {currentPlayer.username}
                </h2>
                {/* Highest Skill Badge */}
                {highestSkill && (
                  <div className="bg-gray-900 border border-cyan-400 px-3 py-1 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)] relative overflow-hidden group"
                       style={{
                         boxShadow: `0 0 8px ${highestSkill.color}40, 2px 2px 0px 0px rgba(0,0,0,1)`
                       }}>
                    <div className="absolute inset-0 bg-gradient-to-br from-cyan-400/10 to-cyan-600/15 pointer-events-none" />
                    <div className="relative z-10 flex items-center gap-2">
                      <Star size={14} style={{ color: highestSkill.color }} />
                      <span className="font-mono text-xs font-bold text-cyan-400">
                        {highestSkill.name.toUpperCase()} LV{highestSkill.level}
                      </span>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Mini Achievement Badges */}
            {selectedBadges.length > 0 ? (
              <div className="mb-4">
                <div className="flex items-center gap-2 mb-2">
                  <span className="text-sm font-mono text-gray-400">FEATURED BADGES:</span>
                  <button
                    onClick={() => setShowBadgeSelector(true)}
                    className="text-xs font-mono text-gray-500 hover:text-gray-300 transition-colors"
                  >
                    <Settings size={12} className="inline mr-1" />
                    EDIT
                  </button>
                </div>
                <div className="flex items-center gap-2 flex-wrap">
                  {selectedBadges.map(badgeId => {
                    const achievement = allAchievements.find(a => a.id === badgeId);
                    if (!achievement) return null;
                    const tier = tierInfo[achievement.tier];

                    return (
                      <div
                        key={badgeId}
                        className="bg-gray-900 border px-2 py-1 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)] relative overflow-hidden group"
                        style={{
                          borderColor: tier.color,
                          boxShadow: `0 0 6px ${tier.color}40, 2px 2px 0px 0px rgba(0,0,0,1)`
                        }}
                      >
                        <div className="absolute inset-0 pointer-events-none" 
                             style={{
                               background: `linear-gradient(to bottom right, ${tier.color}10, ${tier.color}15)`
                             }} />
                        <div className="relative z-10 flex items-center gap-1">
                          <span className="text-xs">{achievement.icon}</span>
                          <span className="font-mono text-xs font-bold" style={{ color: tier.color }}>
                            {achievement.name}
                          </span>
                        </div>
                      </div>
                    );
                  })}
                  {selectedBadges.length < 3 && (
                    <button
                      onClick={() => setShowBadgeSelector(true)}
                      className="bg-gray-800 border border-gray-600 px-2 py-1 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)] text-gray-400 hover:text-white hover:border-gray-500 transition-colors"
                    >
                      <Award size={12} className="inline mr-1" />
                      <span className="font-mono text-xs">ADD BADGE</span>
                    </button>
                  )}
                </div>
              </div>
            ) : getUnlockedAchievements().length > 0 && (
              <div className="mb-4">
                <button
                  onClick={() => setShowBadgeSelector(true)}
                  className="bg-gray-800 border border-gray-600 px-3 py-2 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)] text-gray-400 hover:text-white hover:border-gray-500 transition-colors"
                >
                  <Award size={14} className="inline mr-2" />
                  <span className="font-mono text-sm">SELECT PROFILE BADGES</span>
                </button>
              </div>
            )}

            {/* Player Title section - Enhanced with new rank system */}
            <div className="flex flex-wrap items-center gap-3 mb-4">
              <div className="relative">
                <div className="bg-gray-900 border-4 border-pink-400 shadow-[6px_6px_0px_0px_rgba(0,0,0,1)] px-5 py-3 relative overflow-hidden group"
                     style={{
                       boxShadow: `0 0 15px ${rankInfo.current.color}40, 6px 6px 0px 0px rgba(0,0,0,1)`,
                       borderColor: rankInfo.current.color
                     }}>
                  {/* Shiny striped background with rank color */}
                  <div 
                    className="absolute inset-0 opacity-25"
                    style={{
                      background: `
                        repeating-linear-gradient(
                          45deg,
                          ${rankInfo.current.color},
                          ${rankInfo.current.color} 2px,
                          transparent 2px,
                          transparent 4px
                        )
                      `
                    }}
                  />           
                  <p className="text-lg lg:text-xl font-mono font-bold relative z-10"
                     style={{ color: rankInfo.current.color }}>
                    {rankInfo.current.icon} {rankInfo.current.name}
                  </p>
                </div>

                {/* Enhanced corner decorations with rank-appropriate colors */}
                <div className="absolute -top-3 -left-3">
                  <div className="relative w-8 h-8">
                    <span className="absolute top-0 left-2 text-white text-xl animate-pulse font-bold"></span>
                    <span className="absolute top-2 left-5 text-lg animate-pulse font-bold" 
                          style={{ color: rankInfo.current.color, animationDelay: '0.5s' }}></span>
                    <span className="absolute top-5 left-0 text-cyan-500 text-lg animate-pulse font-bold" 
                          style={{ animationDelay: '1s' }}></span>
                  </div>
                </div>
                <div className="absolute -top-3 -right-3">
                  <div className="relative w-8 h-8">
                    <span className="absolute top-0 right-2 text-white text-xl animate-pulse font-bold"></span>
                    <span className="absolute top-2 right-5 text-lg animate-pulse font-bold" 
                          style={{ color: rankInfo.current.color, animationDelay: '0.5s' }}></span>
                    <span className="absolute top-5 right-0 text-cyan-500 text-lg animate-pulse font-bold" 
                          style={{ animationDelay: '1s' }}></span>
                  </div>
                </div>
              </div>

              {/* Elite badge for high-level players */}
              {currentLevel >= 10 && (
                <div className="bg-gradient-to-r from-yellow-400 to-orange-400 text-black px-3 py-1 border-2 border-gray-600 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)] font-mono font-bold text-sm">
                  ELITE
                </div>
              )}

              {/* Legendary badge for max-level players */}
              {currentLevel >= 15 && (
                <div className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-3 py-1 border-2 border-gray-600 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)] font-mono font-bold text-sm">
                  LEGENDARY
                </div>
              )}
            </div>

            {/* Next Rank - updated with new rank system */}
            <div className="mb-6">
              <span className="font-mono text-gray-400 flex items-center text-sm">
                <Target size={14} className="inline mr-1" />
                Next Rank: 
                <span className="ml-1" style={{ color: rankInfo.next?.color || '#9CA3AF' }}>
                  {rankInfo.next ? `${rankInfo.next.icon} ${rankInfo.next.name}` : 'Max Rank Achieved!'}
                </span>
              </span>
            </div>

            {/* Enhanced XP Progress Bar with rank colors */}
            <div className="space-y-3">
              <div className="relative">
                <div className="w-full h-10 bg-gray-900 border-2 relative overflow-hidden"
                     style={{
                       clipPath: 'polygon(0 0, 100% 0, calc(100% - 20px) 100%, 0 100%)',
                       borderColor: rankInfo.current.color
                     }}>
                  <motion.div
                    initial={{ width: 0 }}
                    animate={{ width: `${progressPercentage}%` }}
                    transition={{ duration: 2, ease: "easeOut" }}
                    className="h-full relative"
                    style={{
                      background: `linear-gradient(90deg, ${rankInfo.current.color}, ${rankInfo.next?.color || rankInfo.current.color})`
                    }}
                  >
                    {/* Animated shine effect */}
                    <div className="absolute inset-0 bg-gradient-to-b from-transparent via-white to-transparent opacity-20"
                         style={{ animation: 'pulse 2s ease-in-out infinite' }} />
                    {/* Additional sparkle effect */}
                    <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent opacity-30 -skew-x-12 animate-pulse" />
                  </motion.div>

                  {/* Corner decorations with rank color */}
                  <div className="absolute top-0 left-0 w-2 h-2 border-t-2 border-l-2" 
                       style={{ borderColor: rankInfo.current.color }} />
                  <div className="absolute top-0 right-0 w-2 h-2 border-t-2 border-r-2" 
                       style={{ borderColor: rankInfo.current.color }} />
                  <div className="absolute bottom-0 left-0 w-2 h-2 border-b-2 border-l-2" 
                       style={{ borderColor: rankInfo.current.color }} />
                </div>

                <div className="absolute inset-0 flex items-center justify-center">
                  <span className="text-sm font-mono font-bold text-white"
                        style={{
                          textShadow: '2px 2px 0px rgba(0,0,0,0.8)'
                        }}>
                    {progressPercentage}%
                  </span>
                </div>
              </div>

              <div className="flex flex-wrap items-center justify-between text-sm gap-2">
                <span className="text-base font-mono text-gray-300">
                  <Zap size={18} className="inline mr-2 text-white" />
                  Experience Points
                </span>
                <span className="text-base font-mono text-white bg-gray-700 px-3 py-1 border border-gray-600">
                  {xpInCurrentRank}/{xpNeededForNextRank} XP
                </span>
              </div>
            </div>
          </div>
        </div>
      </motion.div>

      {/* LOG STATS Section - Updated with purple theme */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        className="bg-gray-900 border border-purple-400 p-6 relative overflow-hidden"
        style={{
          boxShadow: '0 0 3px rgba(139, 92, 246, 0.3), 1px 1px 0px 0px rgba(0,0,0,1)'
        }}
      >
        {/* Gradient overlay with purple theme */}
        <div className="absolute inset-0 bg-gradient-to-br from-purple-400/15 to-purple-600/20 pointer-events-none" />

        <div className="relative z-10">
          <h3 className="text-lg font-mono font-bold text-white flex items-center mb-4">
            <span className="text-pink-400 mr-2 text-xl"></span>
            LOG STATS
          </h3>

          <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
            {/* Total Notes */}
            <div className="bg-gray-900 border border-gray-600 p-4 relative overflow-hidden">
              <div className="absolute inset-0 bg-gradient-to-br from-purple-500/10 to-purple-600/15 pointer-events-none" />
              <div className="relative z-10">
                <div className="text-xs font-mono text-gray-400">LOGS</div>
                <div className="text-2xl font-mono font-bold text-white">{notes.length}</div>
