import React from 'react';
import { motion } from 'framer-motion';
import { FileText, Archive, Trophy, User } from 'lucide-react';

const TabNavigation = ({ activeTab, onTabChange }) => {
  const tabs = [
    { id: 'notes', label: 'MISSIONS', icon: FileText, color: '#8B5CF6' }, // Purple 500
    { id: 'library', label: 'VAULT', icon: Archive, color: '#6366F1' }, // Indigo 500  
    { id: 'achievements', label: 'ACHIEVEMENTS', icon: Trophy, color: '#3B82F6' }, // Blue 500
    { id: 'profile', label: 'PROFILE', icon: User, color: '#22D3EE' } // Cyan 400
  ];

  return (
    <div className="pixel-tab-navigation">
      <div className="tab-container">
        {tabs.map((tab) => {
          const IconComponent = tab.icon;
          const isActive = activeTab === tab.id;

          return (
            <motion.button
              key={tab.id}
              className={`pixel-tab ${isActive ? 'active' : ''}`}
              onClick={() => onTabChange(tab.id)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              style={{
                '--tab-color': tab.color,
                '--tab-shadow': isActive ? `0 0 20px ${tab.color}40, 0 8px 16px rgba(192, 192, 192, 0.3)` : 'none'
              }}
            >
              <div className="tab-content">
                <IconComponent size={16} className="tab-icon" />
                <span className="tab-label">{tab.label}</span>
              </div>

              {isActive && (
                <motion.div
                  className="tab-indicator"
                  layoutId="activeTab"
                  initial={false}
                  transition={{ type: "spring", stiffness: 300, damping: 30 }}
                />
              )}
            </motion.button>
          );
        })}
      </div>
    </div>
  );
};

export default TabNavigation;
