import React from 'react';
import { motion } from 'framer-motion';
import { Crown, Zap, Target } from 'lucide-react';

const HeroCard = ({ player, notes = [] }) => {
  // Calculate XP and level (matching your GameUtilities.java logic)
  const calculateTotalXP = (notes) => {
    let totalXP = 0;

    for (const note of notes) {
      // Base XP per note
      totalXP += 10;

      // Bonus XP for content length
      const wordCount = note.content ? note.content.split(/\s+/).length : 0;
      totalXP += Math.min(Math.floor(wordCount / 10), 50);

      // Bonus XP for using tags
      const tags = note.tagsString ? note.tagsString.split(',').filter(tag => tag.trim()) : [];
      totalXP += tags.length * 5;

      // Bonus XP for longer titles
      if (note.title && note.title.length > 20) {
        totalXP += 5;
      }
    }

    return totalXP;
  };

  const calculateLevelFromXP = (totalXP) => {
    return Math.floor(Math.sqrt(totalXP / 50.0)) + 1;
  };

  const getRankNameForLevel = (level) => {
    switch (level) {
      case 1: return "Novice Scribe";
      case 2: return "Apprentice Writer";
      case 3: return "Skilled Chronicler";
      case 4: return "Expert Documentarian";
      case 5: return "Master Archivist";
      case 6: return "Elite Wordsmith";
      case 7: return "Distinguished Author";
      case 8: return "Legendary Scribe";
      case 9: return "Mythical Chronicler";
      case 10: return "Grandmaster of Words";
      default: return level < 15 ? `Ascended Writer` : `Transcendent Scribe`;
    }
  };

  // Calculate player stats
  const totalXP = calculateTotalXP(notes);
  const currentLevel = calculateLevelFromXP(totalXP);
  const currentLevelBaseXP = Math.pow(currentLevel - 1, 2) * 50;
  const nextLevelXP = Math.pow(currentLevel, 2) * 50;
  const xpInCurrentLevel = totalXP - currentLevelBaseXP;
  const xpNeededForNextLevel = nextLevelXP - currentLevelBaseXP;
  const progressPercentage = Math.floor((xpInCurrentLevel / xpNeededForNextLevel) * 100);

  const playerTitle = getRankNameForLevel(currentLevel);
  const nextLevelTitle = getRankNameForLevel(currentLevel + 1);

  // Generate pixel avatar based on username
  const generatePixelAvatar = (username) => {
    const colors = [
      '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FECA57',
      '#FF9FF3', '#54A0FF', '#5F27CD', '#00D2D3', '#FF9F43'
    ];

    const initial = username ? username.charAt(0).toUpperCase() : 'P';
    const colorIndex = username ? username.length % colors.length : 0;
    const bgColor = colors[colorIndex];

    return { initial, bgColor };
  };

  const avatar = generatePixelAvatar(player?.username || 'Player');

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-gray-800 border-2 border-gray-600 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6"
    >
      <div className="flex items-start gap-6">
        {/* Pixel Art Avatar */}
        <div className="flex-shrink-0">
          <div 
            className="w-20 h-20 border-2 border-gray-400 shadow-[4px_4px_0px_0px_rgba(0,0,0,1)] flex items-center justify-center relative overflow-hidden"
            style={{ 
              backgroundColor: avatar.bgColor,
              imageRendering: 'pixelated',
              background: `
                linear-gradient(45deg, ${avatar.bgColor} 25%, transparent 25%),
                linear-gradient(-45deg, ${avatar.bgColor} 25%, transparent 25%),
                linear-gradient(45deg, transparent 75%, ${avatar.bgColor} 75%),
                linear-gradient(-45deg, transparent 75%, ${avatar.bgColor} 75%)
              `,
              backgroundSize: '8px 8px',
              backgroundPosition: '0 0, 0 4px, 4px -4px, -4px 0px'
            }}
          >
            {/* Pixel character */}
            <div className="relative">
              <span className="text-2xl font-bold font-mono text-white drop-shadow-lg">
                {avatar.initial}
              </span>
              {/* Crown for high levels */}
              {currentLevel >= 8 && (
                <Crown size={12} className="absolute -top-3 -right-2 text-yellow-400" />
              )}
            </div>
          </div>
        </div>

        {/* Player Info */}
        <div className="flex-1">
          <div className="flex items-start justify-between mb-4">
            <div>
              <h2 className="text-2xl font-bold font-mono text-white mb-1">
                {player?.username || 'Player'}
              </h2>
              <div className="flex items-center gap-2">
                <p className="text-lg font-mono text-yellow-400 font-bold">
                  {playerTitle}
                </p>
                <span className="text-sm font-mono text-gray-400">
                  (Lvl {currentLevel})
                </span>
              </div>
            </div>

            {/* Level Badge */}
            <div className="bg-yellow-400 text-black px-3 py-1 border-2 border-gray-600 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)]">
              <span className="font-mono font-bold text-sm">LV.{currentLevel}</span>
            </div>
          </div>

          {/* XP Progress Bar */}
          <div className="space-y-2">
            <div className="flex justify-between items-center">
              <span className="text-sm font-mono text-gray-300">
                <Zap size={14} className="inline mr-1 text-yellow-400" />
                Experience Points
              </span>
              <span className="text-sm font-mono text-white">
                {xpInCurrentLevel}/{xpNeededForNextLevel} XP
              </span>
            </div>

            {/* Pixel Art Progress Bar */}
            <div className="relative">
              <div className="w-full h-6 bg-gray-700 border-2 border-gray-500 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)]">
                <motion.div
                  initial={{ width: 0 }}
                  animate={{ width: `${progressPercentage}%` }}
                  transition={{ duration: 1, ease: "easeOut" }}
                  className="h-full bg-gradient-to-r from-green-400 to-green-500 relative overflow-hidden"
                  style={{
                    imageRendering: 'pixelated',
                    background: `
                      repeating-linear-gradient(
                        45deg,
                        #4ADE80,
                        #4ADE80 2px,
                        #22C55E 2px,
                        #22C55E 4px
                      )
                    `
                  }}
                >
                  {/* Pixel texture overlay */}
                  <div className="absolute inset-0 opacity-30"
                    style={{
                      background: `
                        repeating-linear-gradient(
                          0deg,
                          transparent,
                          transparent 1px,
                          rgba(255,255,255,0.1) 1px,
                          rgba(255,255,255,0.1) 2px
                        )
                      `
                    }}
                  />
                </motion.div>
              </div>

              {/* Progress percentage */}
              <div className="absolute inset-0 flex items-center justify-center">
                <span className="text-xs font-mono font-bold text-white drop-shadow-lg">
                  {progressPercentage}%
                </span>
              </div>
            </div>

            {/* Next Level Info */}
            <div className="flex items-center justify-between text-xs">
              <span className="font-mono text-gray-400">
                <Target size={12} className="inline mr-1" />
                Next: {nextLevelTitle}
              </span>
              <span className="font-mono text-green-400">
                +{nextLevelXP - totalXP} XP needed
              </span>
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default HeroCard;
