import React from 'react';
import { motion } from 'framer-motion';
import { Star, Award, Trophy, Crown, Lock, Target, Zap, Shield, Gem, Hexagon } from 'lucide-react';
import { tierInfo } from '../../data/achievements';

const AchievementBadge = ({ achievement, size = 'large', isUnlocked = false, onClick, showTitle = true }) => {
  const tier = tierInfo[achievement.tier];
  const unlocked = isUnlocked || achievement.unlockedAt;

  // Get icon based on achievement category or type
  const getAchievementIcon = (achievement) => {
    const iconSize = size === 'small' ? 20 : size === 'medium' ? 24 : 32;

    // Map achievements to appropriate icons
    if (achievement.name.includes('First') || achievement.name.includes('Beginner')) {
      return <Star size={iconSize} />;
    }
    if (achievement.name.includes('Word') || achievement.name.includes('Writer')) {
      return <Award size={iconSize} />;
    }
    if (achievement.name.includes('Speed') || achievement.name.includes('Fast')) {
      return <Trophy size={iconSize} />;
    }
    if (achievement.name.includes('Master') || achievement.name.includes('Expert')) {
      return <Crown size={iconSize} />;
    }
    if (achievement.name.includes('Focus') || achievement.name.includes('Session')) {
      return <Target size={iconSize} />;
    }
    if (achievement.name.includes('Power') || achievement.name.includes('Energy')) {
      return <Zap size={iconSize} />;
    }
    if (achievement.name.includes('Guard') || achievement.name.includes('Protect')) {
      return <Shield size={iconSize} />;
    }
    if (achievement.name.includes('Rare') || achievement.name.includes('Special')) {
      return <Gem size={iconSize} />;
    }

    // Default icon based on tier
    switch (achievement.tier) {
      case 'common': return <Star size={iconSize} />;
      case 'uncommon': return <Award size={iconSize} />;
      case 'rare': return <Trophy size={iconSize} />;
      case 'legendary': return <Crown size={iconSize} />;
      default: return <Hexagon size={iconSize} />;
    }
  };

  // Badge styling based on tier (matching your image exactly)
  const getBadgeStyle = (tierName) => {
    switch (tierName.toLowerCase()) {
      case 'common':
        return {
          borderColor: '#22D3EE', // Cyan
          glowColor: '#22D3EE',
          bgColor: 'rgba(34, 211, 238, 0.08)',
          textColor: '#22D3EE',
          tierLabel: 'COMMON'
        };
      case 'uncommon':
        return {
          borderColor: '#A855F7', // Purple  
          glowColor: '#A855F7',
          bgColor: 'rgba(168, 85, 247, 0.08)',
          textColor: '#A855F7',
          tierLabel: 'UNCOMMON'
        };
      case 'rare':
        return {
          borderColor: '#F59E0B', // Orange/Gold
          glowColor: '#F59E0B', 
          bgColor: 'rgba(245, 158, 11, 0.08)',
          textColor: '#F59E0B',
          tierLabel: 'RARE'
        };
      case 'legendary':
        return {
          borderColor: '#DC2626', // Red
          glowColor: '#DC2626',
          bgColor: 'rgba(220, 38, 38, 0.08)', 
          textColor: '#DC2626',
          tierLabel: 'LEGENDARY'
        };
      default:
        return {
          borderColor: '#6B7280',
          glowColor: '#6B7280',
          bgColor: 'rgba(107, 114, 128, 0.08)',
          textColor: '#6B7280',
          tierLabel: 'COMMON'
        };
    }
  };

  const style = getBadgeStyle(achievement.tier);

  return (
    <motion.div
      className="flex flex-col items-center cursor-pointer"
      whileHover={{ scale: 1.03 }}
      whileTap={{ scale: 0.97 }}
      onClick={onClick}
    >
      {/* Main Badge Container - Matching your image dimensions */}
      <div
        className={`
          relative w-80 h-32 border-2 p-6 flex items-center justify-start
          transition-all duration-300 bg-gray-900
          ${unlocked ? 'opacity-100' : 'opacity-70'}
        `}
        style={{
          borderColor: style.borderColor,
          backgroundColor: unlocked ? style.bgColor : 'rgba(31, 41, 55, 0.9)',
          boxShadow: unlocked 
            ? `0 0 25px ${style.glowColor}40, inset 0 0 25px ${style.glowColor}10`
            : 'inset 0 0 10px rgba(0,0,0,0.3)'
        }}
      >
        {/* Animated Glow Effect for Unlocked Badges */}
        {unlocked && (
          <motion.div
            className="absolute inset-0 border-2 opacity-30"
            style={{ borderColor: style.borderColor }}
            animate={{ 
              opacity: [0.2, 0.5, 0.2],
            }}
            transition={{ 
              duration: 3, 
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        )}

        {/* Left side - Icon in Circle */}
        <div className="flex-shrink-0 mr-6">
          <div 
            className="w-16 h-16 border-2 rounded-full flex items-center justify-center"
            style={{
              borderColor: unlocked ? style.borderColor : '#4B5563',
              backgroundColor: unlocked ? `${style.glowColor}15` : 'rgba(75, 85, 99, 0.2)'
            }}
          >
            {unlocked ? (
              <div style={{ color: style.textColor }}>
                {getAchievementIcon(achievement)}
              </div>
            ) : (
              <Lock size={24} className="text-gray-500" />
            )}
          </div>
        </div>

        {/* Right side - Text Content */}
        <div className="flex-1 min-w-0">
          {/* Achievement Title */}
          <div 
            className="font-mono font-bold text-lg mb-2 leading-tight"
            style={{ color: unlocked ? '#FFFFFF' : '#9CA3AF' }}
          >
            {achievement.name}
          </div>

          {/* Achievement Description */}
          <div 
            className="font-mono text-sm opacity-90 leading-tight mb-3"
            style={{ color: unlocked ? '#D1D5DB' : '#6B7280' }}
          >
            {achievement.description}
          </div>

          {/* Tier Badge at Bottom */}
          <div 
            className="inline-block px-3 py-1 text-xs font-mono font-bold border"
            style={{
              backgroundColor: unlocked ? style.borderColor : '#4B5563',
              color: unlocked ? '#000000' : '#9CA3AF',
              borderColor: unlocked ? style.borderColor : '#4B5563'
            }}
          >
            ★ {style.tierLabel}
          </div>
        </div>

        {/* Special Effects for High Tier Badges */}
        {unlocked && achievement.tier === 'legendary' && (
          <>
            {/* Subtle rotating glow */}
            <motion.div
              className="absolute inset-0 opacity-20"
              style={{
                background: `conic-gradient(from 0deg, ${style.glowColor}40, transparent, ${style.glowColor}40)`
              }}
              animate={{ rotate: 360 }}
              transition={{ duration: 15, repeat: Infinity, ease: "linear" }}
            />

            {/* Corner sparkles */}
            <motion.div
              className="absolute top-2 right-2 text-yellow-400 text-sm"
              animate={{ 
                opacity: [0, 1, 0],
                scale: [0.8, 1.2, 0.8]
              }}
              transition={{ 
                duration: 2, 
                repeat: Infinity,
                delay: 0
              }}
            >
              ✨
            </motion.div>
          </>
        )}

        {/* Rare Badge Effects */}
        {unlocked && achievement.tier === 'rare' && (
          <motion.div
            className="absolute top-2 right-2 text-yellow-400 text-sm"
            animate={{ 
              opacity: [0, 1, 0],
              rotate: [0, 360]
            }}
            transition={{ 
              duration: 3, 
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            ⭐
          </motion.div>
        )}
      </div>
    </motion.div>
  );
};

export default AchievementBadge;
