import React from 'react';
import { motion } from 'framer-motion';
import { tierInfo } from '../../data/achievements';

const AchievementBadge = ({ achievement, size = 'medium', showGlow = true, isUnlocked = false, onClick }) => {
  const tier = tierInfo[achievement.tier];
  const unlocked = isUnlocked || achievement.unlockedAt;

  const sizes = {
    small: { badge: 48, icon: 16, text: 'text-xs' },
    medium: { badge: 72, icon: 24, text: 'text-sm' },
    large: { badge: 96, icon: 32, text: 'text-base' },
    xlarge: { badge: 128, icon: 40, text: 'text-lg' }
  };

  const currentSize = sizes[size];

  return (
    <motion.div
      className={`relative cursor-pointer ${currentSize.text}`}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      onClick={onClick}
    >
      {/* Outer glow effect for unlocked badges */}
      {unlocked && showGlow && (
        <motion.div 
          className="absolute inset-0 rounded-full blur-md"
          style={{
            background: `radial-gradient(circle, ${tier.color}60 0%, ${tier.color}30 50%, transparent 70%)`,
            width: currentSize.badge + 20,
            height: currentSize.badge + 20,
            left: -10,
            top: -10
          }}
          animate={{ 
            opacity: [0.5, 0.8, 0.5],
            scale: [1, 1.1, 1]
          }}
          transition={{ 
            duration: 2, 
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
      )}

      {/* Main badge container */}
      <div
        className="relative border-4 rounded-full flex items-center justify-center overflow-hidden"
        style={{
          width: currentSize.badge,
          height: currentSize.badge,
          borderColor: unlocked ? tier.color : '#4B5563',
          backgroundColor: unlocked ? `${tier.color}25` : '#1F2937',
          boxShadow: unlocked 
            ? `0 0 20px ${tier.color}40, inset 0 0 20px ${tier.color}15, 2px 2px 0px 0px rgba(0,0,0,1)`
            : 'inset 0 0 10px rgba(0,0,0,0.5), 2px 2px 0px 0px rgba(0,0,0,1)'
        }}
      >
        {/* Animated gradient background for unlocked badges */}
        {unlocked && (
          <motion.div 
            className="absolute inset-0 rounded-full opacity-20"
            style={{
              background: `conic-gradient(from 0deg, ${tier.color}80, transparent, ${tier.color}80)`
            }}
            animate={{ rotate: 360 }}
            transition={{ duration: 8, repeat: Infinity, ease: "linear" }}
          />
        )}

        {/* Shine effect overlay */}
        {unlocked && (
          <div 
            className="absolute inset-0 rounded-full opacity-30"
            style={{
              background: `linear-gradient(135deg, 
                ${tier.color}40 0%, 
                transparent 30%, 
                transparent 70%, 
                ${tier.color}20 100%)`
            }}
          />
        )}

        {/* Badge content */}
        <div className="relative z-10 text-center">
          {unlocked ? (
            <motion.div 
              className="font-mono font-bold flex items-center justify-center"
              style={{ 
                color: tier.color,
                fontSize: currentSize.icon,
                textShadow: `0 0 10px ${tier.color}80, 2px 2px 0px rgba(0,0,0,1)`
              }}
              animate={{ 
                scale: [1, 1.1, 1],
                rotate: [0, 5, -5, 0]
              }}
              transition={{ 
                duration: 3, 
                repeat: Infinity,
                ease: "easeInOut"
              }}
            >
              {tier.emoji}
            </motion.div>
          ) : (
            <div className="text-gray-600 font-mono font-bold flex items-center justify-center">
              <motion.div
                animate={{ 
                  opacity: [0.3, 0.7, 0.3]
                }}
                transition={{ 
                  duration: 2, 
                  repeat: Infinity 
                }}
                style={{ fontSize: currentSize.icon }}
              >
                🔒
              </motion.div>
            </div>
          )}
        </div>

        {/* Special effects for legendary badges */}
        {unlocked && achievement.tier === 'legendary' && (
          <>
            <motion.div
              className="absolute inset-0 rounded-full border-2 opacity-60"
              style={{ borderColor: `${tier.color}80` }}
              animate={{ rotate: 360 }}
              transition={{ duration: 6, repeat: Infinity, ease: "linear" }}
            />
            <motion.div
              className="absolute inset-0 rounded-full border border-white opacity-40"
              animate={{ 
                rotate: -360,
                scale: [1, 1.05, 1]
              }}
              transition={{ 
                rotate: { duration: 4, repeat: Infinity, ease: "linear" },
                scale: { duration: 2, repeat: Infinity, ease: "easeInOut" }
              }}
            />
          </>
        )}
      </div>

      {/* Tier indicator badge */}
      <div 
        className="absolute -bottom-2 left-1/2 transform -translate-x-1/2 px-2 py-1 rounded text-xs font-mono font-bold border-2"
        style={{
          backgroundColor: unlocked ? tier.color : '#4B5563',
          color: unlocked ? '#000' : '#9CA3AF',
          borderColor: unlocked ? tier.color : '#4B5563',
          boxShadow: '1px 1px 0px 0px rgba(0,0,0,1)'
        }}
      >
        {tier.name.charAt(0)}
      </div>

      {/* Sparkle effects for high-tier unlocked badges */}
      {unlocked && (achievement.tier === 'rare' || achievement.tier === 'legendary') && (
        <>
          <motion.div
            className="absolute -top-1 -left-1 text-white text-xs"
            animate={{ 
              opacity: [0, 1, 0],
              scale: [0.5, 1, 0.5]
            }}
            transition={{ 
              duration: 1.5, 
              repeat: Infinity,
              delay: 0
            }}
          >
            ✨
          </motion.div>
          <motion.div
            className="absolute -top-1 -right-1 text-white text-xs"
            animate={{ 
              opacity: [0, 1, 0],
              scale: [0.5, 1, 0.5]
            }}
            transition={{ 
              duration: 1.5, 
              repeat: Infinity,
              delay: 0.5
            }}
          >
            ⭐
          </motion.div>
          <motion.div
            className="absolute -bottom-1 -right-1 text-white text-xs"
            animate={{ 
              opacity: [0, 1, 0],
              scale: [0.5, 1, 0.5]
            }}
            transition={{ 
              duration: 1.5, 
              repeat: Infinity,
              delay: 1
            }}
          >
            💫
          </motion.div>
        </>
      )}
    </motion.div>
  );
};

export default AchievementBadge;
