import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Crown, Star, Trophy, X, ChevronDown, Palette, Award, Camera, Code, Zap, Monitor, 
  Rocket, Users, PenTool, BookOpen, GraduationCap, Microscope, Dumbbell, Music, 
  Briefcase, TrendingUp, Shield, Target, Timer, CheckSquare, Tag, Calendar, 
  FileText, Lightbulb, Coffee, Brain, Heart, Flame, Diamond, Sparkles, Plus, Settings
} from 'lucide-react';
import PixelPageJenn from '../../assets/icons/PixelPageJenn.PNG';
import TaskStats from './TaskStats';
import FavoriteBadgesSection from './FavoriteBadgesSection';
import BadgeSelectorModal from './BadgeSelectorModal';
import ProfessionBadge from './ProfessionBadge';
import LogStatsSection from './LogStatsSection';
import { getRankByXP, getNextRank } from '../../data/ranks';
import { useTheme } from '../../contexts/ThemeContext';
import { defaultProfilePics, getSelectedProfilePic, saveProfilePicSelection, getProfilePicById } from '../../data/profilePics';
import { useProfileBadges } from '../../hooks/useProfileBadges';
import { calculateSkillsAndProfession } from '../../services/professionService';

const HeroCard = ({ player, notes = [], tasks = [], taskLists = [] }) => {
  const [showEasterEgg, setShowEasterEgg] = useState(false);
  const [showStyleDropdown, setShowStyleDropdown] = useState(false);
  const [showProfilePicEditor, setShowProfilePicEditor] = useState(false);
  const [selectedProfilePic, setSelectedProfilePic] = useState(() => getSelectedProfilePic());
  const [highestSkill, setHighestSkill] = useState(null);

  const { currentTheme, themes, switchTheme, getThemeColors } = useTheme();
  const themeColors = getThemeColors();

  // Use the profile badges hook
  const {
    selectedBadges,
    showBadgeSelector,
    achievementStats,
    addBadge,
    removeBadge,
    getAvailableAchievements,
    openBadgeSelector,
    closeBadgeSelector,
    canAddMoreBadges,
    hasUnlockedAchievements
  } = useProfileBadges(player?.username || 'user');

  // Calculate highest skill and profession on mount and category changes
  useEffect(() => {
    const calculateHighestSkill = () => {
      try {
        const categories = JSON.parse(localStorage.getItem('focusCategories') || '[]');
        if (categories.length === 0) {
          console.log('🔍 No focus categories found in localStorage');
          setHighestSkill(null);
          return;
        }

        const { skillsWithLevels, highestSkill: calculatedHighest } = calculateSkillsAndProfession(categories);
        setHighestSkill(calculatedHighest.level > 1 ? calculatedHighest : null);
      } catch (error) {
        console.error('❌ Error calculating highest skill:', error);
        setHighestSkill(null);
      }
    };

    calculateHighestSkill();

    // Listen for localStorage changes to recalculate
    const handleStorageChange = () => {
      calculateHighestSkill();
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Profile pic management
  const changeProfilePic = (picId) => {
    setSelectedProfilePic(picId);
    saveProfilePicSelection(picId);
    setShowProfilePicEditor(false);
  };

  const handleProfilePicChange = (base64String) => {
    // Save custom profile pic
    setSelectedProfilePic(base64String);
    saveProfilePicSelection(base64String);
  };

  // Calculate total XP from all sources
  const calculateTotalXP = () => {
    let totalXP = 0;
    let breakdown = { notes: 0, achievements: 0, focus: 0, missions: 0 };

    // 1. Achievement XP (from unlocked achievements)
    const achStats = achievementStats || { totalXP: 0 };
    const achievementXP = achStats.totalXP || 0;
    totalXP += achievementXP;
    breakdown.achievements = achievementXP;

    // 2. Notes XP
    let notesXP = 0;
    for (const note of notes) {
      // Base XP per note
      notesXP += 10;

      // Bonus XP for content length
      const wordCount = note.content ? note.content.split(/\s+/).length : 0;
      notesXP += Math.min(Math.floor(wordCount / 10), 50);

      // Bonus XP for using tags
      const tags = note.tagsString ? note.tagsString.split(',').filter(tag => tag.trim()) : [];
      notesXP += tags.length * 5;

      // Bonus XP for longer titles
      if (note.title && note.title.length > 20) {
        notesXP += 5;
      }
    }
    totalXP += notesXP;
    breakdown.notes = notesXP;

    // 3. Focus Timer XP (from tracking sessions)
    let focusXP = 0;
    try {
      const categories = JSON.parse(localStorage.getItem('focusCategories') || '[]');
      focusXP = categories.reduce((sum, category) => sum + (category.xp || 0), 0);

      // Debug check for focus XP inflation
      if (focusXP > 200) {
        console.warn('🚨 FOCUS XP IS TOO HIGH!', focusXP, 'Categories:', categories);
        console.warn('⚠️ TEMPORARILY CAPPING FOCUS XP FROM', focusXP, 'TO 100');
        focusXP = 100; // Temporary cap until we can fix the focus timer XP system
      }

      totalXP += focusXP;
    } catch (error) {
      console.error('Error calculating focus XP:', error);
    }
    breakdown.focus = focusXP;

    // 4. Mission Completion XP (2 XP per completed task)
    const completedTasks = tasks.filter(task => task.completed);
    const missionXP = completedTasks.length * 2;
    totalXP += missionXP;
    breakdown.missions = missionXP;

    return totalXP;
  };

  // Calculate player stats
  const totalXP = calculateTotalXP();
  const currentRank = getRankByXP(totalXP);
  const nextRank = getNextRank(currentRank.level);

  // XP progress calculation for current rank
  const currentRankMinXP = currentRank.minXP;
  const nextRankMinXP = nextRank ? nextRank.minXP : currentRankMinXP + 1000;
  const xpInCurrentRank = totalXP - currentRankMinXP;
  const xpNeededForNextRank = nextRankMinXP - currentRankMinXP;

  const progressPercentage = nextRank ? 
    Math.min(100, Math.max(0, Math.floor((xpInCurrentRank / xpNeededForNextRank) * 100))) :
    100;

  // Mock player data if no player provided
  const mockPlayer = {
    username: "user",
    level: 1,
    experience: 0
  };

  const currentPlayer = player || mockPlayer;

  // Calculate total time spent from focus categories
  const calculateTotalTime = () => {
    try {
      const categories = JSON.parse(localStorage.getItem('focusCategories') || '[]');
      return categories.reduce((total, category) => {
        // Convert XP back to time (assuming 1 XP = 1 minute of focused time)
        return total + (category.xp || 0);
      }, 0);
    } catch (error) {
      console.error('Error calculating total time:', error);
      return 0;
    }
  };

  const totalTimeMinutes = calculateTotalTime();
  const totalSessions = 0; // TODO: Implement session tracking

  // Badge selector handlers
  const handleSelectBadge = (achievement) => {
    addBadge(achievement);
  };

  const handleRemoveBadge = (achievementId) => {
    removeBadge(achievementId);
  };

  return (
    <div className="space-y-6">
      {/* Main Profile Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="p-10 relative overflow-hidden"
        style={{
          backgroundColor: themeColors.backgroundColor,
          border: `2px solid ${themeColors.borderColor}`,
          borderRadius: themeColors.borderRadius,
          boxShadow: currentTheme === 'default' 
            ? `0 0 15px rgba(255, 255, 255, 0.2), 4px 4px 0px 0px rgba(0,0,0,1)` 
            : `0 0 3px ${themeColors.primary}50, 1px 1px 0px 0px rgba(0,0,0,1)`
        }}
      >
        {/* Gradient overlay */}
        <div 
          className="absolute inset-0 pointer-events-none"
          style={{
            background: currentTheme === 'default' 
              ? 'linear-gradient(135deg, rgba(255,255,255,0.05), rgba(6,182,212,0.1))'
              : `linear-gradient(to bottom right, ${themeColors.secondary}15, ${themeColors.secondary}20)`,
            borderRadius: themeColors.borderRadius
          }} 
        />

        {/* Theme Dropdown - Top Right */}
        <div className="absolute top-4 right-4 z-20">
          <div className="relative">
            <button
              onClick={() => setShowStyleDropdown(!showStyleDropdown)}
              className="px-3 py-2 relative group cursor-pointer transition-all duration-300 font-mono font-bold overflow-hidden flex items-center gap-2"
              style={{
                backgroundColor: themeColors.backgroundColor,
                border: `2px solid ${themeColors.controlColor}`,
                borderRadius: themeColors.borderRadius,
                color: themeColors.controlColor,
                boxShadow: currentTheme === 'default' 
                  ? '0 0 10px rgba(255, 255, 255, 0.4), 2px 2px 0px 0px rgba(0,0,0,1)' 
                  : `0 0 6px ${themeColors.controlColor}50, 2px 2px 0px 0px rgba(0,0,0,1)`
              }}
            >
              <Palette size={14} />
              <span className="text-xs">THEME</span>
              <ChevronDown 
                size={12} 
                className={`transition-transform duration-200 ${showStyleDropdown ? 'rotate-180' : ''}`} 
              />
            </button>

            {/* Theme Dropdown Menu */}
            <AnimatePresence>
              {showStyleDropdown && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  className="absolute top-full right-0 mt-2 min-w-[200px] z-30 p-3"
                  style={{
                    backgroundColor: themeColors.backgroundColor,
                    border: `2px solid ${themeColors.controlColor}`,
                    borderRadius: themeColors.borderRadius,
                    boxShadow: currentTheme === 'default' 
                      ? '0 0 20px rgba(255, 255, 255, 0.4), 4px 4px 0px 0px rgba(0,0,0,1)' 
                      : `0 0 15px ${themeColors.controlColor}50, 4px 4px 0px 0px rgba(0,0,0,1)`
                  }}
                >
                  <div className="text-xs font-mono font-bold mb-2" style={{ color: themeColors.controlColor }}>
                    CHOOSE THEME:
                  </div>
                  <div className="space-y-2">
                    {Object.entries(themes).map(([key, theme]) => (
                      <button
                        key={key}
                        onClick={() => {
                          switchTheme(key);
                          setShowStyleDropdown(false);
                        }}
                        className={`w-full text-left px-3 py-2 text-xs font-mono transition-colors ${
                          currentTheme === key ? 'bg-gray-800' : 'hover:bg-gray-800'
                        }`}
                        style={{
                          color: currentTheme === key ? theme.controlColor : '#9CA3AF',
                          border: `1px solid ${currentTheme === key ? theme.controlColor : '#4B5563'}`,
                          borderRadius: theme.borderRadius
                        }}
                      >
                        {theme.name}
                      </button>
                    ))}
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </div>

        <div className="relative z-10 space-y-6">
          {/* Profile Picture and Basic Info */}
          <div className="flex items-start gap-8">
            <div className="flex-shrink-0">
              {/* Profile Picture */}
              <div className="relative">
                <div className="w-32 h-32 lg:w-40 lg:h-40 border-2 flex items-center justify-center relative overflow-hidden bg-gray-700"
                     style={{
                       borderColor: themeColors.borderColor,
                       borderRadius: themeColors.borderRadius,
                       boxShadow: currentTheme === 'default' 
                         ? '0 0 15px rgba(255, 255, 255, 0.3), 6px 6px 0px 0px rgba(0,0,0,1)' 
                         : `0 0 10px ${themeColors.secondary}30, 6px 6px 0px 0px rgba(0,0,0,1)`
                     }}>

                  {/* Custom Pixel Profile Image */}
                  <img 
                    src={PixelPageJenn}
                    alt="Pixel Profile Avatar"
                    className="w-full h-full object-cover relative z-10"
                    style={{ 
                      imageRendering: 'pixelated',
                      borderRadius: themeColors.borderRadius
                    }}
                  />

                  {/* Shimmer Overlay */}
                  <div 
                    className="absolute inset-0 opacity-20 z-20 pointer-events-none"
                    style={{
                      background: `linear-gradient(
                        90deg,
                        transparent 0%,
                        transparent 40%,
                        rgba(255, 255, 255, 0.6) 45%,
                        rgba(255, 255, 255, 0.6) 50%,
                        rgba(255, 255, 255, 0.6) 55%,
                        transparent 60%,
                        transparent 100%
                      )`,
                      animation: 'shimmer-slide 5s ease-in-out infinite',
                      backgroundSize: '200% 100%',
                      borderRadius: themeColors.borderRadius
                    }}
                  />

                  {/* Level decorations overlay */}
                  <div className="absolute inset-0 flex items-center justify-center z-30">
                    {/* Crown for high levels */}
                    {currentRank.level >= 8 && (
                      <Crown size={20} className="absolute -top-4 -right-3 text-yellow-400 drop-shadow-lg" />
                    )}
                    {/* Star effects for legendary levels */}
                    {currentRank.level >= 10 && (
                      <>
                        <Star size={16} className="absolute -top-2 -left-4 text-yellow-300 animate-pulse drop-shadow-lg" />
                        <Star size={14} className="absolute -bottom-2 -right-4 text-yellow-300 animate-pulse drop-shadow-lg" style={{ animationDelay: '0.5s' }} />
                      </>
                    )}
                  </div>

                  {/* Level ring around avatar for high levels */}
                  {currentRank.level >= 5 && (
                    <div className="absolute inset-0 border-2 opacity-60 animate-pulse z-40" 
                         style={{ 
                           borderColor: themeColors.secondary,
                           borderRadius: themeColors.borderRadius
                         }} />
                  )}
                </div>

                {/* Username */}
                <h2 className="text-xl font-bold text-white text-center mt-3">
                  {currentPlayer.username}
                </h2>
              </div>

              {/* Level Badge */}
              <div className="mt-4 text-center px-4 py-2 relative overflow-hidden"
                style={{
                  background: currentTheme === 'pink' 
                    ? 'linear-gradient(135deg, #a855f7 0%, #9333ea 50%, #a855f7 100%)' 
                    : 'linear-gradient(135deg, #06b6d4 0%, #0891b2 50%, #06b6d4 100%)',
                  border: currentTheme === 'pink' ? `2px solid #9333ea` : `2px solid #0891b2`,
                  borderRadius: themeColors.borderRadius,
                  boxShadow: currentTheme === 'pink' 
                    ? '0 0 15px rgba(168, 85, 247, 0.5), 4px 4px 0px 0px rgba(0,0,0,1)'
                    : '0 0 15px rgba(6, 182, 212, 0.5), 4px 4px 0px 0px rgba(0,0,0,1)'
                }}
              >
                <span className="font-mono font-bold text-lg text-white">
                  LVL {currentRank.level}
                </span>
              </div>

              {/* Online Status Indicator */}
              <div className="mt-2 flex items-center justify-center gap-2 px-3 py-1"
                   style={{
                     backgroundColor: themeColors.backgroundColor,
                     border: `1px solid ${themeColors.borderColor}`,
                     borderRadius: themeColors.borderRadius,
                     boxShadow: currentTheme === 'default' 
                       ? '0 0 8px rgba(255, 255, 255, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)' 
                       : '2px 2px 0px 0px rgba(0,0,0,1)'
                   }}>
                <button
                  onClick={() => setShowEasterEgg(true)}
                  className="w-2 h-2 bg-green-400 rounded-full animate-pulse cursor-pointer hover:bg-green-300 transition-colors" 
                  style={{ 
                    boxShadow: '0 0 6px rgba(34, 197, 94, 0.8)' 
                  }}
                  title=""
                />
                <span className="font-mono text-xs font-bold text-green-400">ONLINE</span>
              </div>
            </div>

            <div className="flex-1 space-y-4">
              {/* Player Title */}
              <div className="flex items-center gap-3 flex-wrap">
                <div className="px-5 py-3"
                  style={{
                    background: 'linear-gradient(135deg, #06b6d4 0%, #0891b2 50%, #06b6d4 100%)',
                    border: `4px solid #0891b2`,
                    borderRadius: themeColors.borderRadius,
                    boxShadow: '0 0 20px rgba(6, 182, 212, 0.6), 6px 6px 0px 0px rgba(0,0,0,1)'
                  }}
                >
                  <p className="text-xl font-mono font-bold text-white">
                    {currentRank.name}
                  </p>
                </div>

                {/* Profession Badge */}
                <ProfessionBadge highestSkill={highestSkill} />
              </div>

              {/* XP Progress Bar */}
              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span className="text-white font-mono">
                    {nextRank ? `Progress to ${nextRank.name}` : 'Max Level'}
                  </span>
                  <span className="text-white font-mono">
                    {totalXP} XP
                  </span>
                </div>
                <div 
                  className="w-full h-8 relative overflow-hidden"
                  style={{
                    backgroundColor: 'rgba(0, 0, 0, 0.6)',
                    border: `2px solid ${themeColors.borderColor}`,
                    borderRadius: themeColors.borderRadius
                  }}
                >
                  <motion.div
                    initial={{ width: 0 }}
                    animate={{ width: `${progressPercentage}%` }}
                    transition={{ duration: 2, ease: "easeOut" }}
                    className="h-full"
                    style={{
                      background: 'linear-gradient(90deg, #06b6d4, #a855f7, #06b6d4)',
                      borderRadius: themeColors.borderRadius
                    }}
                  />
                  <div className="absolute inset-0 flex items-center justify-center">
                    <span className="text-white font-mono font-bold text-sm">
                      {progressPercentage}%
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Favorite Badges Section */}
          <FavoriteBadgesSection
            selectedBadges={selectedBadges}
            onOpenBadgeSelector={openBadgeSelector}
            canAddMoreBadges={canAddMoreBadges}
            hasUnlockedAchievements={hasUnlockedAchievements}
          />

          {/* Log Stats Section */}
          <LogStatsSection
            totalTimeMinutes={totalTimeMinutes}
            totalSessions={totalSessions}
            achievementStats={achievementStats}
          />
        </div>
      </motion.div>

      {/* Task Stats Component */}
      <TaskStats 
        notes={notes}
        tasks={tasks}
        taskLists={taskLists}
      />

      {/* Badge Selector Modal */}
      <BadgeSelectorModal
        isOpen={showBadgeSelector}
        onClose={closeBadgeSelector}
        availableAchievements={getAvailableAchievements()}
        selectedBadges={selectedBadges}
        onSelectBadge={handleSelectBadge}
        onRemoveBadge={handleRemoveBadge}
        canAddMoreBadges={canAddMoreBadges}
      />

      {/* Profile Picture Editor Modal */}
      <AnimatePresence>
        {showProfilePicEditor && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center"
            onClick={() => setShowProfilePicEditor(false)}
          >
            <motion.div
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              className="bg-gray-900 p-8 rounded-xl border-2 border-cyan-400 max-w-md mx-4"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="text-center">
                <h3 className="text-xl font-bold text-cyan-400 mb-6">Choose Profile Picture</h3>
                <div className="grid grid-cols-3 gap-4 mb-6">
                  {defaultProfilePics.map((pic) => (
                    <button
                      key={pic.id}
                      onClick={() => changeProfilePic(pic.id)}
                      className={`w-16 h-16 rounded-lg border-2 overflow-hidden transition-all ${
                        selectedProfilePic === pic.id
                          ? 'border-cyan-400 scale-110'
                          : 'border-gray-600 hover:border-gray-400'
                      }`}
                    >
                      <img 
                        src={pic.url} 
                        alt={pic.name}
                        className="w-full h-full object-cover"
                        style={{ imageRendering: 'pixelated' }}
                      />
                    </button>
                  ))}
                </div>
                <button
                  onClick={() => setShowProfilePicEditor(false)}
                  className="px-6 py-2 bg-gray-700 hover:bg-gray-600 text-white rounded-lg transition-colors"
                >
                  Cancel
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Easter Egg Modal */}
      <AnimatePresence>
        {showEasterEgg && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center"
            onClick={() => setShowEasterEgg(false)}
          >
            <motion.div
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              className="bg-gray-900 p-8 rounded-xl border-4 border-cyan-400 max-w-md mx-4"
              onClick={(e) => e.stopPropagation()}
            >
              <div className="text-center">
                <div className="text-6xl mb-4">🎮</div>
                <h3 className="text-2xl font-bold text-cyan-400 mb-2">Easter Egg Found!</h3>
                <p className="text-white mb-4">
                  You've discovered the secret online status! You're truly a dedicated pixel warrior.
                </p>
                <button
                  onClick={() => setShowEasterEgg(false)}
                  className="px-6 py-3 bg-cyan-500 hover:bg-cyan-600 text-white rounded-lg font-bold transition-colors"
                >
                  Awesome! 🌟
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default HeroCard;
