import apiService from './api';
import { allAchievements } from '../data/achievements'; // Add this import

class BackendAchievementService {
  constructor() {
    this.allAchievements = [];
    this.playerAchievements = [];
    this.playerStats = {};
    this.loaded = false;
  }

  getUnlockedAchievements() {
    if (!this.loaded) {
      console.warn('âš ï¸ Backend achievement service not loaded yet');
      return [];
    }

    const unlockedIds = this.playerAchievements
      .filter(pa => pa.unlocked)
      .map(pa => pa.achievementId);

    const unlockedAchievements = this.allAchievements
      .filter(achievement => unlockedIds.includes(achievement.id))
      .map(achievement => {
        const playerAch = this.playerAchievements.find(pa => pa.achievementId === achievement.id);
        return {
          ...achievement,
          unlockedAt: playerAch?.unlockedAt || new Date().toISOString(),
          progress: playerAch?.progress || 100
        };
      });

    console.log('ðŸ” BACKEND SERVICE getUnlockedAchievements():');
    console.log('   - unlockedIds:', unlockedIds);
    console.log('   - unlockedAchievements:', unlockedAchievements);

    return unlockedAchievements;
  }

  getLockedAchievements() {
    if (!this.loaded) {
      console.warn('âš ï¸ Backend achievement service not loaded yet');
      return [];
    }

    const unlockedIds = this.playerAchievements
      .filter(pa => pa.unlocked)
      .map(pa => pa.achievementId);

    return this.allAchievements.filter(achievement => !unlockedIds.includes(achievement.id));
  }

  getInProgressAchievements() {
    if (!this.loaded) {
      console.warn('âš ï¸ Backend achievement service not loaded yet');
      return [];
    }

    const inProgressIds = this.playerAchievements
      .filter(pa => !pa.unlocked && pa.progress > 0)
      .map(pa => pa.achievementId);

    return this.allAchievements
      .filter(achievement => inProgressIds.includes(achievement.id))
      .map(achievement => {
        const playerAch = this.playerAchievements.find(pa => pa.achievementId === achievement.id);
        return {
          ...achievement,
          progress: playerAch?.progress || 0
        };
      });
  }

  async loadData(username = 'Jroc_182') {
    try {
      console.log('ðŸ”„ BACKEND SERVICE: Loading data for', username);

      const [achievements, playerAchievements, stats] = await Promise.all([
        apiService.getAllAchievements(),
        apiService.getPlayerAchievements(username),
        apiService.getAchievementPlayerStats(username)
      ]);

      console.log('ðŸ” BACKEND SERVICE: Raw data loaded:');
      console.log('   - achievements:', achievements?.length || 0);
      console.log('   - playerAchievements:', playerAchievements?.length || 0);
      console.log('   - stats:', stats);

      this.allAchievements = achievements;
      this.playerAchievements = playerAchievements;
      this.playerStats = stats;
      this.loaded = true;

      console.log('âœ… Backend achievement data loaded for', username);

      return { achievements, playerAchievements, stats };
    } catch (error) {
      console.error('âŒ Failed to load backend achievements:', error);

      // ðŸ”„ FALLBACK: If backend fails, try to use local data
      console.log('ðŸ”„ BACKEND FAILED: Attempting fallback to local achievements');
      this.allAchievements = allAchievements; // Now this should work
      this.playerAchievements = [];
      this.playerStats = {};
      this.loaded = true;

      throw error;
    }
  }

  isUnlocked(achievementId) {
    if (!this.loaded) return false;

    const playerAch = this.playerAchievements.find(pa => pa.achievementId === achievementId);
    return playerAch?.unlocked || false;
  }

  unlockAchievement(achievementId) {
    // Find or create player achievement
    let playerAch = this.playerAchievements.find(pa => pa.achievementId === achievementId);

    if (!playerAch) {
      playerAch = {
        achievementId,
        progress: 100,
        unlocked: true,
        unlockedAt: new Date().toISOString()
      };
      this.playerAchievements.push(playerAch);
    } else {
      playerAch.unlocked = true;
      playerAch.progress = 100;
      playerAch.unlockedAt = new Date().toISOString();
    }

    console.log(`ðŸ† Unlocked achievement: ${achievementId}`);
  }

  getStats() {
    if (!this.loaded) {
      return {
        total: 0,
        unlocked: 0,
        locked: 0,
        inProgress: 0,
        completionRate: 0
      };
    }

    const unlockedCount = this.getUnlockedAchievements().length;
    const totalCount = this.allAchievements.length;
    const inProgressCount = this.getInProgressAchievements().length;
    const lockedCount = totalCount - unlockedCount;

    return {
      total: totalCount,
      unlocked: unlockedCount,
      locked: lockedCount,
      inProgress: inProgressCount,
      completionRate: totalCount > 0 ? Math.round((unlockedCount / totalCount) * 100) : 0
    };
  }
}

// Export singleton instance
const backendAchievementService = new BackendAchievementService();
export default backendAchievementService;
