import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { CheckSquare, Plus, Clock, AlertCircle, Trash2 } from 'lucide-react';
import useTasks from '../../hooks/useTasks';

const TasksTab = ({ tabColor = '#0EA5E9' }) => {
  const { tasks, loading, error, createTask, toggleTask, deleteTask } = useTasks();
  const [newTask, setNewTask] = useState('');
  const [newTaskPriority, setNewTaskPriority] = useState('medium');

  const addTask = async () => {
    if (newTask.trim()) {
      try {
        await createTask({
          title: newTask,
          priority: newTaskPriority,
          completed: false
        });
        setNewTask('');
        setNewTaskPriority('medium');
      } catch (err) {
        console.error('Failed to add task:', err);
      }
    }
  };

  const handleToggleTask = async (taskId) => {
    try {
      await toggleTask(taskId);
    } catch (err) {
      console.error('Failed to toggle task:', err);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteTask(taskId);
    } catch (err) {
      console.error('Failed to delete task:', err);
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return '#EF4444';
      case 'medium': return '#F59E0B';
      case 'low': return '#10B981';
      default: return '#6B7280';
    }
  };

  const getPriorityIcon = (priority) => {
    switch (priority) {
      case 'high': return <AlertCircle size={16} />;
      case 'medium': return <Clock size={16} />;
      case 'low': return <CheckSquare size={16} />;
      default: return <Clock size={16} />;
    }
  };

  if (loading) {
    return (
      <div className="p-6 text-center">
        <div className="text-white">Loading tasks...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 text-center">
        <div className="text-red-400">Error loading tasks: {error}</div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-mono font-bold text-white flex items-center">
          <CheckSquare className="mr-3" style={{ color: tabColor }} />
          QUEST LOG
        </h2>
        <div className="text-sm font-mono text-gray-400">
          {tasks.filter(t => t.completed).length}/{tasks.length} Complete
        </div>
      </div>

      {/* Add New Task */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-gray-800 border-2 border-cyan-400 shadow-[4px_4px_0px_0px_rgba(0,0,0,1)] p-4"
        style={{
          boxShadow: `0 0 10px ${tabColor}33, 4px 4px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="flex gap-3">
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            placeholder="Enter new quest..."
            className="flex-1 bg-gray-900 border border-gray-600 text-white px-3 py-2 font-mono text-sm focus:outline-none focus:border-cyan-400"
            onKeyPress={(e) => e.key === 'Enter' && addTask()}
          />

          <select
            value={newTaskPriority}
            onChange={(e) => setNewTaskPriority(e.target.value)}
            className="bg-gray-900 border border-gray-600 text-white px-3 py-2 font-mono text-sm focus:outline-none focus:border-cyan-400"
          >
            <option value="low">Low</option>
            <option value="medium">Medium</option>
            <option value="high">High</option>
          </select>

          <button
            onClick={addTask}
            className="bg-cyan-600 hover:bg-cyan-700 text-white px-4 py-2 border-2 border-gray-600 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)] transition-all duration-200 hover:translate-x-[1px] hover:translate-y-[1px] hover:shadow-[1px_1px_0px_0px_rgba(0,0,0,1)]"
          >
            <Plus size={18} />
          </button>
        </div>
      </motion.div>

      {/* Tasks List */}
      <div className="space-y-3">
        {tasks.length === 0 ? (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-center py-8 text-gray-400 font-mono"
          >
            No quests yet. Create your first task above!
          </motion.div>
        ) : (
          tasks.map((task, index) => (
            <motion.div
              key={task.id}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.1 }}
              className={`bg-gray-800 border-2 ${task.completed ? 'border-green-500' : 'border-gray-600'} shadow-[2px_2px_0px_0px_rgba(0,0,0,1)] p-4 transition-all duration-200`}
              style={{
                boxShadow: task.completed 
                  ? '0 0 5px rgba(34, 197, 94, 0.3), 2px 2px 0px 0px rgba(0,0,0,1)'
                  : '2px 2px 0px 0px rgba(0,0,0,1)'
              }}
            >
              <div className="flex items-center gap-3">
                {/* Completion Checkbox */}
                <button
                  onClick={() => handleToggleTask(task.id)}
                  className={`w-6 h-6 border-2 flex items-center justify-center transition-all duration-200 ${
                    task.completed
                      ? 'bg-green-500 border-green-500 text-white'
                      : 'border-gray-500 hover:border-cyan-400'
                  }`}
                >
                  {task.completed && <CheckSquare size={16} />}
                </button>

                {/* Priority Indicator */}
                <div
                  className="p-1"
                  style={{ color: getPriorityColor(task.priority) }}
                  title={`${task.priority} priority`}
                >
                  {getPriorityIcon(task.priority)}
                </div>

                {/* Task Title */}
                <span
                  className={`flex-1 font-mono ${
                    task.completed
                      ? 'text-gray-400 line-through'
                      : 'text-white'
                  }`}
                >
                  {task.title}
                </span>

                {/* Priority Badge */}
                <span
                  className="px-2 py-1 text-xs font-mono font-bold border"
                  style={{
                    color: getPriorityColor(task.priority),
                    borderColor: getPriorityColor(task.priority),
                    backgroundColor: `${getPriorityColor(task.priority)}20`
                  }}
                >
                  {task.priority.toUpperCase()}
                </span>

                {/* Delete Button */}
                <button
                  onClick={() => handleDeleteTask(task.id)}
                  className="text-red-400 hover:text-red-300 p-1 transition-colors duration-200"
                  title="Delete task"
                >
                  <Trash2 size={16} />
                </button>
              </div>
            </motion.div>
          ))
        )}
      </div>

      {/* Stats Summary */}
      {tasks.length > 0 && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
          className="bg-gray-800 border border-gray-600 p-4 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)]"
        >
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <div className="text-sm font-mono text-gray-400">TOTAL</div>
              <div className="text-xl font-mono font-bold text-white">{tasks.length}</div>
            </div>
            <div>
              <div className="text-sm font-mono text-gray-400">COMPLETED</div>
              <div className="text-xl font-mono font-bold text-green-400">
                {tasks.filter(t => t.completed).length}
              </div>
            </div>
            <div>
              <div className="text-sm font-mono text-gray-400">PROGRESS</div>
              <div className="text-xl font-mono font-bold text-cyan-400">
                {tasks.length > 0 ? Math.round((tasks.filter(t => t.completed).length / tasks.length) * 100) : 0}%
              </div>
            </div>
          </div>
        </motion.div>
      )}
    </div>
  );
};

export default TasksTab;
