// components/ui/HeartProgressBar.jsx
import React from 'react';
import { motion } from 'framer-motion';
import { useTheme } from '../../contexts/ThemeContext';

const HeartProgressBar = ({ progress, size = 60, className = "" }) => {
  const { currentTheme } = useTheme();

  if (currentTheme !== 'pink') {
    // Fallback to regular progress bar for other themes
    return (
      <div className={`w-full h-4 bg-gray-700 rounded-full overflow-hidden ${className}`}>
        <motion.div
          initial={{ width: 0 }}
          animate={{ width: `${progress}%` }}
          className="h-full bg-gradient-to-r from-cyan-400 to-cyan-600"
          transition={{ duration: 1, ease: "easeOut" }}
        />
      </div>
    );
  }

  const heartPath = "M12,21.35l-1.45-1.32C5.4,15.36,2,12.28,2,8.5 C2,5.42,4.42,3,7.5,3c1.74,0,3.41,0.81,4.5,2.09C13.09,3.81,14.76,3,16.5,3 C19.58,3,22,5.42,22,8.5c0,3.78-3.4,6.86-8.55,11.54L12,21.35z";

  return (
    <div className={`relative ${className}`} style={{ width: size, height: size }}>
      {/* Background heart */}
      <svg
        width={size}
        height={size}
        viewBox="0 0 24 24"
        className="absolute inset-0"
      >
        <path
          d={heartPath}
          fill="rgba(75, 85, 99, 0.3)"
          stroke="rgba(244, 114, 182, 0.5)"
          strokeWidth="1"
        />
      </svg>

      {/* Progress heart with clip-path */}
      <motion.div
        initial={{ clipPath: 'inset(100% 0 0 0)' }}
        animate={{ clipPath: `inset(${100 - progress}% 0 0 0)` }}
        transition={{ duration: 1.5, ease: "easeOut" }}
        className="absolute inset-0"
      >
        <svg
          width={size}
          height={size}
          viewBox="0 0 24 24"
        >
          <defs>
            <linearGradient id={`heartGradient-${Math.random()}`} x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#f472b6" />
              <stop offset="50%" stopColor="#e879f9" />
              <stop offset="100%" stopColor="#f472b6" />
            </linearGradient>
          </defs>
          <path
            d={heartPath}
            fill={`url(#heartGradient-${Math.random()})`}
            className="drop-shadow-lg"
          />
        </svg>
      </motion.div>

      {/* Sparkle effect */}
      <motion.div
        animate={{
          opacity: [0.5, 1, 0.5],
          scale: [0.8, 1.1, 0.8]
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          ease: "easeInOut"
        }}
        className="absolute inset-0 pointer-events-none"
      >
        <svg
          width={size}
          height={size}
          viewBox="0 0 24 24"
        >
          <path
            d={heartPath}
            fill="none"
            stroke="rgba(248, 250, 252, 0.6)"
            strokeWidth="0.5"
            filter="blur(1px)"
          />
        </svg>
      </motion.div>
    </div>
  );
};

export default HeartProgressBar;
