import React from 'react';
import { motion } from 'framer-motion';
import { tierInfo } from '../../data/achievements';

const AchievementBadge = ({ achievement, size = 'large', isUnlocked = false, onClick, showTitle = true }) => {
  const tier = tierInfo[achievement.tier];
  const unlocked = isUnlocked || achievement.unlockedAt;

  // Badge styling based on tier (matching your image)
  const getBadgeStyle = (tierName) => {
    switch (tierName.toLowerCase()) {
      case 'common':
        return {
          borderColor: '#22D3EE', // Cyan
          glowColor: '#22D3EE',
          bgColor: 'rgba(34, 211, 238, 0.1)',
          textColor: '#22D3EE'
        };
      case 'uncommon':
        return {
          borderColor: '#A855F7', // Purple  
          glowColor: '#A855F7',
          bgColor: 'rgba(168, 85, 247, 0.1)',
          textColor: '#A855F7'
        };
      case 'rare':
        return {
          borderColor: '#F59E0B', // Orange/Gold
          glowColor: '#F59E0B', 
          bgColor: 'rgba(245, 158, 11, 0.1)',
          textColor: '#F59E0B'
        };
      case 'legendary':
        return {
          borderColor: '#DC2626', // Red
          glowColor: '#DC2626',
          bgColor: 'rgba(220, 38, 38, 0.1)', 
          textColor: '#DC2626'
        };
      default:
        return {
          borderColor: '#6B7280',
          glowColor: '#6B7280',
          bgColor: 'rgba(107, 114, 128, 0.1)',
          textColor: '#6B7280'
        };
    }
  };

  const style = getBadgeStyle(achievement.tier);

  return (
    <motion.div
      className="flex flex-col items-center cursor-pointer"
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      onClick={onClick}
    >
      {/* Main Badge Container */}
      <div
        className={`
          relative w-32 h-40 border-2 p-4 flex flex-col items-center justify-center
          text-center transition-all duration-300
          ${unlocked ? 'opacity-100' : 'opacity-60'}
        `}
        style={{
          borderColor: style.borderColor,
          backgroundColor: unlocked ? style.bgColor : 'rgba(31, 41, 55, 0.8)',
          boxShadow: unlocked 
            ? `0 0 20px ${style.glowColor}60, inset 0 0 20px ${style.glowColor}20`
            : 'none'
        }}
      >
        {/* Animated Glow Effect for Unlocked Badges */}
        {unlocked && (
          <motion.div
            className="absolute inset-0 border-2 opacity-40"
            style={{ borderColor: style.borderColor }}
            animate={{ 
              opacity: [0.2, 0.6, 0.2],
              scale: [1, 1.02, 1]
            }}
            transition={{ 
              duration: 2, 
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        )}

        {/* Badge Icon */}
        <div className="mb-2">
          {unlocked ? (
            <div 
              className="text-4xl mb-2"
              style={{ 
                color: style.textColor,
                filter: `drop-shadow(0 0 8px ${style.glowColor}80)`
              }}
            >
              {tier.emoji}
            </div>
          ) : (
            <div className="text-4xl mb-2 text-gray-600">
              🔒
            </div>
          )}
        </div>

        {/* Achievement Title */}
        {showTitle && (
          <div 
            className="font-mono font-bold text-xs leading-tight"
            style={{ color: unlocked ? style.textColor : '#9CA3AF' }}
          >
            {achievement.name}
          </div>
        )}

        {/* Achievement Description */}
        <div 
          className="font-mono text-xs mt-2 opacity-80 leading-tight"
          style={{ color: unlocked ? '#D1D5DB' : '#6B7280' }}
        >
          {achievement.description}
        </div>

        {/* Tier Badge at Bottom */}
        <div 
          className="absolute bottom-2 left-1/2 transform -translate-x-1/2 px-2 py-1 text-xs font-mono font-bold border"
          style={{
            backgroundColor: unlocked ? style.borderColor : '#4B5563',
            color: unlocked ? '#000' : '#9CA3AF',
            borderColor: unlocked ? style.borderColor : '#4B5563'
          }}
        >
          {tier.name.charAt(0)} {tier.name.toUpperCase()}
        </div>

        {/* Special Effects for High Tier Badges */}
        {unlocked && achievement.tier === 'legendary' && (
          <>
            {/* Rotating outer glow */}
            <motion.div
              className="absolute inset-0 border border-red-500 opacity-30"
              animate={{ rotate: 360 }}
              transition={{ duration: 8, repeat: Infinity, ease: "linear" }}
            />

            {/* Sparkle effects */}
            <motion.div
              className="absolute -top-1 -left-1 text-yellow-400 text-sm"
              animate={{ 
                opacity: [0, 1, 0],
                scale: [0.8, 1.2, 0.8]
              }}
              transition={{ 
                duration: 1.5, 
                repeat: Infinity,
                delay: 0
              }}
            >
              ✨
            </motion.div>
            <motion.div
              className="absolute -top-1 -right-1 text-yellow-400 text-sm"
              animate={{ 
                opacity: [0, 1, 0],
                scale: [0.8, 1.2, 0.8]
              }}
              transition={{ 
                duration: 1.5, 
                repeat: Infinity,
                delay: 0.5
              }}
            >
              ⭐
            </motion.div>
          </>
        )}

        {/* Rare Badge Effects */}
        {unlocked && achievement.tier === 'rare' && (
          <motion.div
            className="absolute -top-1 -right-1 text-yellow-400 text-sm"
            animate={{ 
              opacity: [0, 1, 0],
              rotate: [0, 360]
            }}
            transition={{ 
              duration: 2, 
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            ⭐
          </motion.div>
        )}
      </div>
    </motion.div>
  );
};

export default AchievementBadge;
