import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Trophy, Star, Zap, Crown, Lock, CheckCircle, Target, BarChart3 } from 'lucide-react';
import { allAchievements, achievementsByTier, tierInfo } from '../../data/achievements';
import achievementService from '../../services/achievementService';

const AchievementsTab = ({ username = 'Jroc_182', tabColor = '#F59E0B' }) => {
  const [selectedTier, setSelectedTier] = useState('all');
  const [achievementStats, setAchievementStats] = useState({});
  const [userStats, setUserStats] = useState({});

  useEffect(() => {
    // Load achievement statistics
    const stats = achievementService.getStats();
    setAchievementStats(stats);

    // Load user statistics for progress calculation
    const notes = JSON.parse(localStorage.getItem('notes') || '[]');
    const tasks = JSON.parse(localStorage.getItem('tasks') || '[]');
    const sessions = JSON.parse(localStorage.getItem('focusSessions') || '[]');

    // Calculate comprehensive user stats
    const calculatedStats = {
      // Note stats
      totalNotes: notes.length,
      totalWords: notes.reduce((sum, note) => sum + (note.content?.split(' ').length || 0), 0),
      uniqueTags: new Set(notes.flatMap(note => note.tags || [])).size,

      // Task stats  
      totalTasks: tasks.filter(t => t.completed).length,
      tasksCreated: tasks.length,

      // Focus stats
      totalSessions: sessions.length,
      totalFocusTime: sessions.reduce((sum, s) => sum + s.timeSpent, 0),
      maxSessionDuration: Math.max(...sessions.map(s => s.timeSpent), 0),

      // Level and XP
      level: Math.floor(stats.totalXP / 1000) + 1,
      totalXP: stats.totalXP
    };

    setUserStats(calculatedStats);
  }, []);

  const getTierIcon = (tier) => {
    switch (tier) {
      case 'legendary': return Crown;
      case 'rare': return Star;
      case 'uncommon': return Zap;
      default: return Trophy;
    }
  };

  const getFilteredAchievements = () => {
    if (selectedTier === 'all') return allAchievements;
    return achievementsByTier[selectedTier] || [];
  };

  const hexToRgb = (hex) => {
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? 
      `${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}` :
      '245, 158, 11';
  };

  const tabColorRgb = hexToRgb(tabColor);

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="mb-8"
      >
        <h1 className="font-mono text-3xl font-bold text-white mb-2 flex items-center gap-3">
          <div 
            className="w-6 h-6 border border-gray-600" 
            style={{ backgroundColor: tabColor }}
          />
          ACHIEVEMENT HALL
        </h1>
        <p className="text-gray-400 font-mono text-sm">
          Unlock achievements by completing tasks, writing notes, and focusing deeply.
        </p>
      </motion.div>

      {/* Achievement Statistics */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] relative"
        style={{
          borderColor: tabColor,
          boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="absolute inset-0 border-2 opacity-30 animate-pulse pointer-events-none" 
             style={{ borderColor: tabColor }} />

        <div className="relative z-10">
          <div className="border-b px-4 py-3"
               style={{ 
                 borderColor: tabColor,
                 backgroundColor: '#1A0E26'
               }}>
            <h3 className="text-lg font-mono font-bold text-white flex items-center">
              <BarChart3 className="mr-2" size={20} style={{ color: tabColor }} />
              ACHIEVEMENT PROGRESS
            </h3>
          </div>

          <div className="p-6">
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
              <div className="text-center">
                <div className="text-2xl font-mono font-bold text-green-400">
                  {achievementStats.unlocked || 0}
                </div>
                <div className="text-xs text-gray-400">UNLOCKED</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-mono font-bold text-blue-400">
                  {achievementStats.percentage || 0}%
                </div>
                <div className="text-xs text-gray-400">COMPLETION</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-mono font-bold text-yellow-400">
                  {achievementStats.totalXP || 0}
                </div>
                <div className="text-xs text-gray-400">TOTAL XP</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-mono font-bold text-purple-400">
                  {userStats.level || 1}
                </div>
                <div className="text-xs text-gray-400">LEVEL</div>
              </div>
            </div>

            {/* Tier Breakdown */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {Object.entries(tierInfo).map(([tier, info]) => {
                const TierIcon = getTierIcon(tier);
                const tierCount = achievementStats.byTier?.[tier] || 0;
                const tierTotal = achievementsByTier[tier]?.length || 0;

                return (
                  <div 
                    key={tier}
                    className="bg-gray-900 border border-gray-600 p-3 text-center"
                    style={{
                      borderColor: selectedTier === tier ? info.color : '#4B5563',
                      backgroundColor: selectedTier === tier ? `${info.color}10` : 'transparent'
                    }}
                  >
                    <TierIcon size={24} style={{ color: info.color }} className="mx-auto mb-2" />
                    <div className="text-sm font-mono font-bold text-white">
                      {tierCount}/{tierTotal}
                    </div>
                    <div className="text-xs text-gray-400">
                      {info.name}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </motion.div>

      {/* Tier Filter */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        className="flex flex-wrap gap-3"
      >
        <button
          onClick={() => setSelectedTier('all')}
          className={`px-4 py-2 border-2 font-mono font-bold transition-all ${
            selectedTier === 'all'
              ? 'border-white text-white bg-white bg-opacity-10'
              : 'border-gray-600 text-gray-400 hover:border-gray-500'
          }`}
        >
          ALL ({allAchievements.length})
        </button>

        {Object.entries(tierInfo).map(([tier, info]) => {
          const tierCount = achievementsByTier[tier]?.length || 0;
          return (
            <button
              key={tier}
              onClick={() => setSelectedTier(tier)}
              className={`px-4 py-2 border-2 font-mono font-bold transition-all ${
                selectedTier === tier
                  ? `text-white bg-opacity-10`
                  : 'border-gray-600 text-gray-400 hover:border-gray-500'
              }`}
              style={{
                borderColor: selectedTier === tier ? info.color : '#4B5563',
                backgroundColor: selectedTier === tier ? `${info.color}20` : 'transparent',
                color: selectedTier === tier ? info.color : undefined
              }}
            >
              {info.emoji} {info.name} ({tierCount})
            </button>
          );
        })}
      </motion.div>

      {/* Achievement Grid */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2 }}
        className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4"
      >
        {getFilteredAchievements().map((achievement, index) => {
          const isUnlocked = achievementService.isUnlocked(achievement.id);
          const progress = achievementService.getAchievementProgress(achievement.id, userStats);
          const tier = tierInfo[achievement.tier];
          const TierIcon = getTierIcon(achievement.tier);

          return (
            <motion.div
              key={achievement.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.05 }}
              className={`bg-gray-900 border-2 p-4 relative transition-all duration-300 hover:scale-105 ${
                isUnlocked ? 'border-opacity-80' : 'border-opacity-40'
              }`}
              style={{
                borderColor: tier.color,
                boxShadow: isUnlocked 
                  ? `0 0 15px ${tier.color}40, 2px 2px 0px 0px rgba(0,0,0,1)`
                  : '2px 2px 0px 0px rgba(0,0,0,1)'
              }}
            >
              {/* Unlock Status Overlay */}
              {!isUnlocked && (
                <div className="absolute inset-0 bg-gray-900 bg-opacity-60 flex items-center justify-center z-20">
                  <Lock size={32} className="text-gray-600" />
                </div>
              )}

              {/* Achievement Content */}
              <div className="relative z-10">
                <div className="flex items-center gap-3 mb-3">
                  <div 
                    className={`w-12 h-12 border-2 flex items-center justify-center ${
                      isUnlocked ? 'border-opacity-80' : 'border-opacity-40'
                    }`}
                    style={{ 
                      borderColor: tier.color,
                      backgroundColor: `${tier.color}${isUnlocked ? '30' : '10'}`
                    }}
                  >
                    {isUnlocked ? (
                      <CheckCircle size={20} style={{ color: tier.color }} />
                    ) : (
                      <TierIcon size={20} style={{ color: tier.color, opacity: 0.6 }} />
                    )}
                  </div>

                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-1">
                      <span className="text-xs font-mono" style={{ color: tier.color }}>
                        {tier.emoji} {tier.name.toUpperCase()}
                      </span>
                    </div>
                    <div className={`font-mono font-bold text-sm ${
                      isUnlocked ? 'text-white' : 'text-gray-500'
                    }`}>
                      {achievement.name}
                    </div>
                  </div>
                </div>

                <div className={`text-xs font-mono mb-3 ${
                  isUnlocked ? 'text-gray-300' : 'text-gray-600'
                }`}>
                  {achievement.description}
                </div>

                {/* Progress Bar (for locked achievements) */}
                {!isUnlocked && progress > 0 && (
                  <div className="mb-3">
                    <div className="flex justify-between items-center mb-1">
                      <span className="text-xs font-mono text-gray-500">Progress</span>
                      <span className="text-xs font-mono text-gray-500">
                        {Math.round(progress * 100)}%
                      </span>
                    </div>
                    <div className="w-full bg-gray-700 h-2 border border-gray-600">
                      <div 
                        className="h-full transition-all duration-500"
                        style={{ 
                          width: `${progress * 100}%`,
                          backgroundColor: tier.color,
                          opacity: 0.7
                        }}
                      />
                    </div>
                  </div>
                )}

                <div className="flex justify-between items-center">
                  <div 
                    className={`text-xs font-mono font-bold ${
                      isUnlocked ? '' : 'opacity-60'
                    }`}
                    style={{ color: tier.color }}
                  >
                    +{achievement.xpReward} XP
                  </div>

                  {isUnlocked && (
                    <div className="text-xs font-mono text-gray-500">
                      UNLOCKED ✓
                    </div>
                  )}
                </div>
              </div>

              {/* Glow effect for unlocked achievements */}
              {isUnlocked && (
                <div 
                  className="absolute inset-0 opacity-10 animate-pulse pointer-events-none"
                  style={{ backgroundColor: tier.color }}
                />
              )}
            </motion.div>
          );
        })}
      </motion.div>
    </div>
  );
};

export default AchievementsTab;
