import React from 'react';
import { motion } from 'framer-motion';
import { TrendingUp, Award, Target, Zap } from 'lucide-react';

const SkillDashboard = ({ categories, tabColor }) => {

  const getCurrentLevel = (totalXP) => {
    let level = 1;
    let xpNeeded = 0;
    const base = 60;

    while (level <= 10) {
      xpNeeded += Math.floor(base * Math.pow(1.4, level - 1));
      if (totalXP < xpNeeded) break;
      level++;
    }

    return Math.min(level, 10);
  };

  const getProgressColor = (level) => {
    if (level === 10) return '#FFD700';
    const blueStart = { r: 59, g: 130, b: 246 };
    const purpleEnd = { r: 168, g: 85, b: 247 };
    const progress = (level - 1) / 9;
    const r = Math.round(blueStart.r + (purpleEnd.r - blueStart.r) * progress);
    const g = Math.round(blueStart.g + (purpleEnd.g - blueStart.g) * progress);
    const b = Math.round(blueStart.b + (purpleEnd.b - blueStart.b) * progress);
    return `rgb(${r}, ${g}, ${b})`;
  };

  // Find recently leveled or close to leveling skills
  const recentMilestones = categories
    .filter(cat => getCurrentLevel(cat.xp || 0) >= 5)
    .slice(0, 3);

  const closeToLeveling = categories
    .filter(cat => {
      const level = getCurrentLevel(cat.xp || 0);
      if (level >= 10) return false;

      // Check if within 30 XP of next level
      const currentXP = cat.xp || 0;
      const nextLevelXP = Math.floor(60 * Math.pow(1.4, level));
      return (nextLevelXP - currentXP) <= 30;
    })
    .slice(0, 2);

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">

      {/* Recent Milestones */}
      <motion.div
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        className="bg-gray-800 border-2 border-yellow-500 p-4 relative overflow-hidden"
        style={{ boxShadow: '0 0 15px rgba(234, 179, 8, 0.3)' }}
      >
        <div className="flex items-center gap-3 mb-2">
          <Award className="text-yellow-500" size={20} />
          <span className="font-mono font-bold text-white text-sm">RECENT MILESTONES</span>
        </div>
        <div className="space-y-2">
          {recentMilestones.length > 0 ? (
            recentMilestones.map((cat, index) => (
              <motion.div
                key={cat.id}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: index * 0.1 }}
                className="flex items-center justify-between text-xs"
              >
                <span className="text-gray-300">
                  {cat.name.toUpperCase()} SKILL
                </span>
                <span 
                  className="font-bold"
                  style={{ color: getProgressColor(getCurrentLevel(cat.xp || 0)) }}
                >
                  LVL {getCurrentLevel(cat.xp || 0)}
                </span>
              </motion.div>
            ))
          ) : (
            <div className="text-gray-500 text-xs">No milestones yet!</div>
          )}
        </div>
      </motion.div>

      {/* Close to Leveling */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        className="bg-gray-800 border-2 border-blue-500 p-4 relative overflow-hidden"
        style={{ boxShadow: '0 0 15px rgba(59, 130, 246, 0.3)' }}
      >
        <div className="flex items-center gap-3 mb-2">
          <Target className="text-blue-500" size={20} />
          <span className="font-mono font-bold text-white text-sm">CLOSE TO LEVEL UP</span>
        </div>
        <div className="space-y-2">
          {closeToLeveling.length > 0 ? (
            closeToLeveling.map((cat, index) => (
              <motion.div
                key={cat.id}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: index * 0.1 }}
                className="flex items-center justify-between text-xs"
              >
                <span className="text-gray-300">
                  {cat.name.toUpperCase()}
                </span>
                <div className="flex items-center gap-2">
                  <div className="w-12 h-1 bg-gray-700 relative">
                    <div 
                      className="h-full bg-blue-500"
                      style={{ width: '80%' }}
                    />
                  </div>
                  <span className="text-blue-500 font-bold">80%</span>
                </div>
              </motion.div>
            ))
          ) : (
            <div className="text-gray-500 text-xs">Keep grinding!</div>
          )}
        </div>
      </motion.div>

      {/* Overall Progress */}
      <motion.div
        initial={{ opacity: 0, x: 20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ delay: 0.2 }}
        className="bg-gray-800 border-2 border-purple-500 p-4 relative overflow-hidden"
        style={{ boxShadow: '0 0 15px rgba(168, 85, 247, 0.3)' }}
      >
        <div className="flex items-center gap-3 mb-2">
          <TrendingUp className="text-purple-500" size={20} />
          <span className="font-mono font-bold text-white text-sm">SKILL OVERVIEW</span>
        </div>
        <div className="space-y-2">
          <div className="flex justify-between text-xs">
            <span className="text-gray-300">Active Skills</span>
            <span className="text-purple-500 font-bold">{categories.length}</span>
          </div>
          <div className="flex justify-between text-xs">
            <span className="text-gray-300">Max Level Skills</span>
            <span className="text-yellow-500 font-bold">
              {categories.filter(cat => getCurrentLevel(cat.xp || 0) === 10).length}
            </span>
          </div>
          <div className="flex justify-between text-xs">
            <span className="text-gray-300">Total XP</span>
            <span className="text-purple-500 font-bold">
              {categories.reduce((sum, cat) => sum + (cat.xp || 0), 0)}
            </span>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default SkillDashboard;
