                              {/* Connecting lines */}
                              {nodeLevel === 4 && isUnlocked && currentLevel > nodeLevel && (
                                <div 
                                  className="absolute top-1/2 -right-8 w-16 h-0.5 transform -translate-y-1/2 z-10"
                                  style={{ 
                                    backgroundColor: progressColor,
                                    boxShadow: `0 0 4px ${progressColor}`
                                  }}
                                />
