                                  <motion.div
                                    animate={{ 
                                      scale: [1, 1.2, 1],
                                      rotate: [0, 180, 360]
                                    }}
                                    transition={{ duration: 2, repeat: Infinity }}
                                    style={{ color: isUnlocked ? '#000000' : '#6B7280' }}
                                  >
                                    <Trophy size={20} />
                                  </motion.div>
                                ) : isUnlocked ? (
                                  <Zap size={16} style={{ color: '#000000' }} />
                                ) : (
                                  <div className="font-mono font-bold text-sm text-gray-400">
                                    {nodeLevel}
                                  </div>
                                )}
                              </div>

                              {/* Hover tooltip */}
                              {hoveredNode?.category === category.name && hoveredNode?.level === nodeLevel && (
                                <motion.div
                                  initial={{ opacity: 0, y: 10 }}
                                  animate={{ opacity: 1, y: 0 }}
                                  className="absolute bottom-full mb-3 left-1/2 transform -translate-x-1/2 bg-black border-2 px-3 py-2 rounded whitespace-nowrap z-50"
                                  style={{ borderColor: progressColor }}
                                >
                                  <div 
                                    className="text-sm font-mono font-bold"
                                    style={{ color: progressColor }}
                                  >
                                    Level {nodeLevel}
                                  </div>
                                  <div className="text-xs text-gray-300">
                                    {getNodeDescription(category, nodeLevel)}
                                  </div>
                                </motion.div>
                              )}
                            </motion.div>
                          );
                        })}
                      </div>
                    </div>

                    {/* Row 3 - 3 hexagons */}
                    <div className="flex justify-center">
                      <div className="flex space-x-8">
                        {[6, 7, 8].map(nodeLevel => {
                          const isUnlocked = currentLevel >= nodeLevel;
                          const isCurrent = currentLevel === nodeLevel && currentLevel < 10;
                          const isMilestone = nodeLevel === 5 || nodeLevel === 10;

                          return (
                            <motion.div
                              key={nodeLevel}
                              className="relative w-16 h-16 flex items-center justify-center cursor-pointer"
                              initial={{ scale: 0, rotate: -180 }}
                              animate={{ scale: 1, rotate: 0 }}
                              transition={{ delay: nodeLevel * 0.1, type: "spring" }}
                              whileHover={{ scale: 1.1 }}
                              onHoverStart={() => setHoveredNode({ category: category.name, level: nodeLevel })}
                              onHoverEnd={() => setHoveredNode(null)}
                            >
                              {/* Connecting lines */}
                              {nodeLevel < 8 && isUnlocked && currentLevel > nodeLevel && (
                                <div 
                                  className="absolute top-1/2 -right-4 w-8 h-0.5 transform -translate-y-1/2 z-10"
                                  style={{ 
                                    backgroundColor: progressColor,
                                    boxShadow: `0 0 4px ${progressColor}`
                                  }}
                                />
                              )}

                              <HexagonShape 
                                size={64}
                                color={isUnlocked ? progressColor : '#4B5563'}
                                filled={isUnlocked}
                                glowing={isUnlocked && isCurrent}
                              />

                              <div className={`relative z-20 ${isCurrent ? 'animate-pulse' : ''}`}>
                                {isMilestone && isUnlocked ? (
                                  <motion.div
                                    animate={{ rotate: nodeLevel === 10 ? [0, 360] : 0 }}
                                    transition={{ duration: 3, repeat: Infinity }}
                                    style={{ color: isUnlocked ? '#000000' : '#6B7280' }}
                                  >
                                    {nodeLevel === 10 ? <Crown size={20} /> : <Trophy size={20} />}
                                  </motion.div>
                                ) : isUnlocked ? (
                                  <Zap size={16} style={{ color: '#000000' }} />
                                ) : (
                                  <div className="font-mono font-bold text-sm text-gray-400">
                                    {nodeLevel}
                                  </div>
                                )}
                              </div>

                              {/* Hover tooltip */}
                              {hoveredNode?.category === category.name && hoveredNode?.level === nodeLevel && (
                                <motion.div
                                  initial={{ opacity: 0, y: 10 }}
                                  animate={{ opacity: 1, y: 0 }}
                                  className="absolute bottom-full mb-3 left-1/2 transform -translate-x-1/2 bg-black border-2 px-3 py-2 rounded whitespace-nowrap z-50"
                                  style={{ borderColor: progressColor }}
                                >
                                  <div 
                                    className="text-sm font-mono font-bold"
                                    style={{ color: progressColor }}
                                  >
                                    Level {nodeLevel}
                                  </div>
                                  <div className="text-xs text-gray-300">
                                    {getNodeDescription(category, nodeLevel)}
                                  </div>
                                </motion.div>
                              )}
                            </motion.div>
                          );
                        })}
                      </div>
                    </div>

                    {/* Row 4 - 2 hexagons (final levels) */}
                    <div className="flex justify-center">
                      <div className="flex space-x-16">
                        {[9, 10].map(nodeLevel => {
                          const isUnlocked = currentLevel >= nodeLevel;
                          const isCurrent = currentLevel === nodeLevel && currentLevel < 10;
                          const isMilestone = nodeLevel === 5 || nodeLevel === 10;

                          return (
                            <motion.div
                              key={nodeLevel}
                              className="relative w-20 h-20 flex items-center justify-center cursor-pointer"
                              initial={{ scale: 0, rotate: -180 }}
                              animate={{ scale: 1, rotate: 0 }}
                              transition={{ delay: nodeLevel * 0.1, type: "spring" }}
                              whileHover={{ scale: 1.1 }}
                              onHoverStart={() => setHoveredNode({ category: category.name, level: nodeLevel })}
                              onHoverEnd={() => setHoveredNode(null)}
                            >
                              {/* Connecting lines */}
                              {nodeLevel === 9 && isUnlocked && currentLevel > nodeLevel && (
                                <div 
                                  className="absolute top-1/2 -right-8 w-16 h-1 transform -translate-y-1/2 z-10"
                                  style={{ 
                                    backgroundColor: progressColor,
                                    boxShadow: `0 0 6px ${progressColor}`
                                  }}
                                />
                              )}

                              <HexagonShape 
                                size={nodeLevel === 10 ? 80 : 72}
                                color={isUnlocked ? (nodeLevel === 10 ? '#FFD700' : progressColor) : '#4B5563'}
                                filled={isUnlocked}
                                glowing={isUnlocked}
                              />

                              <div className={`relative z-20 ${isCurrent ? 'animate-pulse' : ''}`}>
                                {isMilestone && isUnlocked ? (
                                  <motion.div
                                    animate={{ 
                                      rotate: nodeLevel === 10 ? [0, 360] : [0, 180, 360],
                                      scale: nodeLevel === 10 ? [1, 1.2, 1] : [1, 1.1, 1]
                                    }}
                                    transition={{ 
                                      duration: nodeLevel === 10 ? 4 : 2, 
                                      repeat: Infinity 
                                    }}
                                    style={{ color: isUnlocked ? '#000000' : '#6B7280' }}
                                  >
