                                  className="absolute bottom-full mb-3 left-1/2 transform -translate-x-1/2 bg-black border-2 px-3 py-2 rounded whitespace-nowrap z-50"
                                  style={{ borderColor: nodeLevel === 10 ? '#FFD700' : progressColor }}
                                >
                                  <div 
                                    className="text-sm font-mono font-bold"
                                    style={{ color: nodeLevel === 10 ? '#FFD700' : progressColor }}
                                  >
                                    Level {nodeLevel} {nodeLevel === 10 ? '- MASTERY' : ''}
                                  </div>
                                  <div className="text-xs text-gray-300">
                                    {getNodeDescription(category, nodeLevel)}
                                  </div>
                                </motion.div>
                              )}
                            </motion.div>
                          );
                        })}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Progress section */}
                <div className="mt-12 space-y-4">
                  <div className="flex justify-between text-sm font-mono">
                    <span className="text-gray-400">
                      {currentLevel < 10 ? `NEXT LEVEL: ${currentLevel + 1}` : 'MASTERY ACHIEVED'}
                    </span>
                    <span style={{ color: progressColor }}>
                      {currentLevel < 10 ? `${progress.current}/${progress.max} XP` : 'COMPLETED'}
                    </span>
                  </div>

                  {/* Progress bar */}
                  <div className="relative">
                    <div 
                      className="w-full h-4 border-2 bg-gray-900"
                      style={{ borderColor: progressColor }}
                    >
                      <div 
                        className="h-full transition-all duration-1000 relative overflow-hidden"
                        style={{ 
                          width: `${progress.percentage}%`,
                          background: `linear-gradient(90deg, ${progressColor}, ${progressColor}CC)`
                        }}
                      >
                        {/* Scan line */}
                        <motion.div 
                          className="absolute inset-0 opacity-60"
                          style={{
                            background: 'linear-gradient(90deg, transparent 0%, rgba(255,255,255,0.8) 50%, transparent 100%)'
                          }}
                          animate={{ x: ['-100%', '200%'] }}
                          transition={{ 
                            duration: 3,
                            repeat: Infinity,
                            repeatType: 'loop',
                            ease: 'linear'
                          }}
                        />
                      </div>
                    </div>
                  </div>

                  {/* Stats */}
                  <div className="grid grid-cols-2 gap-4 text-center">
                    <div>
                      <div 
                        className="text-3xl font-mono font-bold"
                        style={{ color: progressColor }}
                      >
                        {category.xp || 0}
                      </div>
                      <div className="text-sm text-gray-400 font-mono">TOTAL XP</div>
                    </div>
                    <div>
                      <div 
                        className="text-3xl font-mono font-bold"
                        style={{ color: progressColor }}
                      >
                        {Math.floor((category.xp || 0) / 60)}h {(category.xp || 0) % 60}m
                      </div>
                      <div className="text-sm text-gray-400 font-mono">TIME GRINDED</div>
                    </div>
                  </div>
                </div>
              </div>
            </motion.div>
          );
        })}
    </div>
  );
};

export default HexagonalSkillTree;
