import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Target, CheckCircle, AlertTriangle, Clock, Calendar, TrendingUp, Award, Zap } from 'lucide-react';

const TaskStats = ({ tasks, taskLists }) => {
  const [stats, setStats] = useState({
    totalTasks: 0,
    completedTasks: 0,
    completionRate: 0,
    overdueTasks: 0,
    dueSoonTasks: 0,
    highPriorityTasks: 0,
    averageCompletionTime: 0,
    productivityScore: 0,
    streakDays: 0,
    totalProjects: 0
  });

  useEffect(() => {
    calculateStats();
  }, [tasks, taskLists]);

  const calculateStats = () => {
    if (!tasks || tasks.length === 0) {
      setStats({
        totalTasks: 0,
        completedTasks: 0,
        completionRate: 0,
        overdueTasks: 0,
        dueSoonTasks: 0,
        highPriorityTasks: 0,
        averageCompletionTime: 0,
        productivityScore: 0,
        streakDays: 0,
        totalProjects: taskLists ? taskLists.length : 0
      });
      return;
    }

    // Basic stats
    const totalTasks = tasks.length;
    const completedTasks = tasks.filter(t => t.completed).length;
    const completionRate = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;
    const overdueTasks = tasks.filter(t => t.overdue && !t.completed).length;
    const dueSoonTasks = tasks.filter(t => t.dueSoon && !t.completed).length;
    const highPriorityTasks = tasks.filter(t => t.priority === 'high' && !t.completed).length;

    // Calculate productivity score (0-100)
    let productivityScore = 0;
    if (totalTasks > 0) {
      const completionBonus = completionRate * 0.4; // 40% weight
      const overdueDeduction = Math.min((overdueTasks / totalTasks) * 30, 30); // Max 30 point deduction
      const highPriorityBonus = Math.min((completedTasks / totalTasks) * 20, 20); // Max 20 point bonus
      const consistencyBonus = Math.min(totalTasks * 2, 40); // Max 40 points for having tasks

      productivityScore = Math.max(0, Math.round(completionBonus + highPriorityBonus + consistencyBonus - overdueDeduction));
    }

    // Mock streak calculation (you can implement real streak logic later)
    const streakDays = calculateStreak();

    // Average completion time (mock for now)
    const averageCompletionTime = completedTasks > 0 ? Math.round(2.5 * Math.random() + 1.5) : 0;

    setStats({
      totalTasks,
      completedTasks,
      completionRate,
      overdueTasks,
      dueSoonTasks,
      highPriorityTasks,
      averageCompletionTime,
      productivityScore,
      streakDays,
      totalProjects: taskLists ? taskLists.length : 0
    });
  };

  const calculateStreak = () => {
    // Mock streak calculation - you can implement real logic based on task completion dates
    const completedTasks = tasks.filter(t => t.completed);
    if (completedTasks.length === 0) return 0;

    // Simple mock: longer streaks for more completed tasks
    if (completedTasks.length >= 20) return 14;
    if (completedTasks.length >= 15) return 10;
    if (completedTasks.length >= 10) return 7;
    if (completedTasks.length >= 5) return 4;
    return completedTasks.length > 0 ? 2 : 0;
  };

  const getProductivityGrade = (score) => {
    if (score >= 90) return { grade: 'S', color: '#FFD700' }; // Gold
    if (score >= 80) return { grade: 'A', color: '#10B981' }; // Green
    if (score >= 70) return { grade: 'B', color: '#3B82F6' }; // Blue
    if (score >= 60) return { grade: 'C', color: '#F59E0B' }; // Orange
    return { grade: 'D', color: '#EF4444' }; // Red
  };

  const productivity = getProductivityGrade(stats.productivityScore);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.2 }}
      className="bg-gray-800 border-2 border-cyan-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 relative"
      style={{
        boxShadow: '0 0 20px rgba(34, 211, 238, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
      }}
    >
      <div className="absolute inset-0 border-2 border-cyan-400 opacity-50 animate-pulse pointer-events-none" />

      <h3 className="text-lg font-mono font-bold text-white flex items-center mb-4">
        <div className="w-4 h-4 bg-purple-400 mr-2" />
        QUEST STATISTICS
      </h3>

      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
        {/* Completion Rate */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between">
            <div>
              <div className="text-xs font-mono text-gray-400">SUCCESS RATE</div>
              <div className="text-xl font-mono font-bold text-green-400">{stats.completionRate}%</div>
            </div>
            <CheckCircle className="text-green-400" size={20} />
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* Productivity Score */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between">
            <div>
              <div className="text-xs font-mono text-gray-400">GRADE</div>
              <div className="text-xl font-mono font-bold" style={{ color: productivity.color }}>
                {productivity.grade} ({stats.productivityScore})
              </div>
            </div>
            <Award className="text-yellow-400" size={20} />
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* Active Streak */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between">
            <div>
              <div className="text-xs font-mono text-gray-400">STREAK</div>
              <div className="text-xl font-mono font-bold text-orange-400">{stats.streakDays} days</div>
            </div>
            <TrendingUp className="text-orange-400" size={20} />
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* High Priority Tasks */}
        <div className="bg-gray-900 border border-red-500 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(239, 68, 68, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between">
            <div>
              <div className="text-xs font-mono text-gray-400">URGENT</div>
              <div className="text-xl font-mono font-bold text-red-400">{stats.highPriorityTasks}</div>
            </div>
            <AlertTriangle className="text-red-400" size={20} />
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* Total Projects */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between">
            <div>
              <div className="text-xs font-mono text-gray-400">PROJECTS</div>
              <div className="text-xl font-mono font-bold text-blue-400">{stats.totalProjects}</div>
            </div>
            <Target className="text-blue-400" size={20} />
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* Due Soon Tasks */}
        <div className="bg-gray-900 border border-yellow-500 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(245, 158, 11, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between">
            <div>
              <div className="text-xs font-mono text-gray-400">DUE SOON</div>
              <div className="text-xl font-mono font-bold text-yellow-400">{stats.dueSoonTasks}</div>
            </div>
            <Clock className="text-yellow-400" size={20} />
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* Average Completion Time */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between">
            <div>
              <div className="text-xs font-mono text-gray-400">AVG TIME</div>
              <div className="text-xl font-mono font-bold text-purple-400">
                {stats.averageCompletionTime > 0 ? `${stats.averageCompletionTime}d` : 'N/A'}
              </div>
            </div>
            <Calendar className="text-purple-400" size={20} />
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>

        {/* Energy/XP from Tasks */}
        <div className="bg-gray-900 border border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between">
            <div>
              <div className="text-xs font-mono text-gray-400">QUEST XP</div>
              <div className="text-xl font-mono font-bold text-cyan-400">
                {stats.completedTasks * 25}
              </div>
            </div>
            <Zap className="text-cyan-400" size={20} />
          </div>
          <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
        </div>
      </div>

      {/* Quick Performance Indicators */}
      <div className="mt-6 pt-4 border-t border-gray-700">
        <div className="flex flex-wrap items-center justify-between gap-4">
          <div className="flex items-center gap-2">
            <span className="text-sm font-mono text-gray-400">PERFORMANCE:</span>
            <span 
              className="px-2 py-1 text-xs font-mono font-bold border"
              style={{
                color: productivity.color,
                borderColor: productivity.color,
                backgroundColor: `${productivity.color}20`
              }}
            >
              {productivity.grade} RANK
            </span>
          </div>

          {stats.overdueTasks > 0 && (
            <div className="flex items-center gap-2 text-red-400">
              <AlertTriangle size={14} />
              <span className="text-xs font-mono">{stats.overdueTasks} OVERDUE QUEST{stats.overdueTasks > 1 ? 'S' : ''}</span>
            </div>
          )}

          {stats.streakDays >= 7 && (
            <div className="flex items-center gap-2 text-orange-400">
              <TrendingUp size={14} />
              <span className="text-xs font-mono">WEEK+ STREAK!</span>
            </div>
          )}
        </div>
      </div>
    </motion.div>
  );
};

export default TaskStats;
