// components/ui/CrystalShineBar.jsx
import React from "react";
import { motion } from "framer-motion";

export default function CrystalShineBar({ progress = 42, height = 16, radius = 9999, className = "" }) {
  const clamped = Math.max(0, Math.min(100, progress));

  // NEW: scale internals based on height
  const shineWidth = Math.max(80, height * 4);       // wider shine on taller bars
  const facetSizeA = Math.max(18, Math.floor(height * 1.1));
  const facetSizeB = Math.max(22, Math.floor(height * 1.3));
  const topSparkleHeight = Math.max(2, Math.round(height * 0.12));

  return (
    <div
      className={`relative w-full overflow-hidden border border-pink-300/40 bg-pink-200/10 backdrop-blur-[1px] ${className}`}
      style={{
        height,
        borderRadius: radius,
        // beefier inner glow as bar gets taller
        boxShadow: `inset 0 0 ${Math.round(height * 0.75)}px rgba(255, 182, 193, .15)`
      }}
    >
      <div
        className="h-full relative"
        style={{
          width: `${clamped}%`,
          borderRadius: radius,
          backgroundImage:
            "linear-gradient(90deg, #f9a8d4 0%, #f0abfc 35%, #e879f9 65%, #fb7185 100%)",
          boxShadow:
            "inset 0 0 20px rgba(255,255,255,.25), inset 0 -6px 12px rgba(255, 105, 180, .25)",
          transition: "width 300ms ease",
        }}
      >
        {/* Facets scaled */}
        <div
          className="absolute inset-0 opacity-40 pointer-events-none mix-blend-screen"
          style={{
            backgroundImage: `
              linear-gradient(135deg, rgba(255,255,255,.15) 0 10%, transparent 10% 50%),
              linear-gradient(45deg, rgba(255,255,255,.08) 0 12%, transparent 12% 50%)
            `,
            backgroundSize: `${facetSizeA}px ${facetSizeA}px, ${facetSizeB}px ${facetSizeB}px`,
            backgroundPosition: "0 0, 6px 6px",
          }}
        />

        {/* Shine scaled */}
        <motion.div
          className="absolute top-0 bottom-0 pointer-events-none"
          style={{
            width: shineWidth,
            borderRadius: Math.min(12, radius),
            background:
              "linear-gradient(90deg, rgba(255,255,255,0) 0%, rgba(255,255,255,.75) 45%, rgba(255,255,255,0) 100%)",
            filter: "blur(2px)",
            transform: "skewX(-20deg)",
          }}
          initial={{ x: "-20%" }}
          animate={{ x: "120%" }}
          transition={{ duration: 1.8, repeat: Infinity, ease: "easeInOut" }}
        />

        {/* Top sparkle line scaled */}
        <motion.div
          className="absolute left-0 right-0"
          style={{
            top: Math.round(height * 0.12),
            height: topSparkleHeight,
            background:
              "linear-gradient(90deg, transparent, rgba(255,255,255,.7), transparent)",
          }}
          initial={{ opacity: 0.2 }}
          animate={{ opacity: [0.2, 0.8, 0.2] }}
          transition={{ duration: 2.4, repeat: Infinity, ease: "easeInOut" }}
        />
      </div>
    </div>
  );
}
