import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Crown, Star, Trophy, X, ChevronDown, Palette, Award, Camera, Code, Zap, Monitor, 
  Rocket, Users, PenTool, BookOpen, GraduationCap, Microscope, Dumbbell, Music, 
  Briefcase, TrendingUp, Shield, Target, Timer, CheckSquare, Tag, Calendar, 
  FileText, Lightbulb, Coffee, Brain, Heart, Flame, Diamond, Sparkles
} from 'lucide-react';
import PixelPageJenn from '../../assets/icons/PixelPageJenn.PNG';
import TaskStats from './TaskStats';
import { getRankByXP, getNextRank } from '../../data/ranks';
import achievementService from '../../services/achievementService';
import { useTheme } from '../../contexts/ThemeContext';
import { allAchievements, tierInfo } from '../../data/achievements';
import { defaultProfilePics, getSelectedProfilePic, saveProfilePicSelection, getProfilePicById } from '../../data/profilePics';

const HeroCard = ({ player, notes = [], tasks = [], taskLists = [] }) => {
  const [showEasterEgg, setShowEasterEgg] = useState(false);
  const [showStyleDropdown, setShowStyleDropdown] = useState(false);
  const [showBadgeSelector, setShowBadgeSelector] = useState(false);
  const [showProfilePicEditor, setShowProfilePicEditor] = useState(false);
  const [selectedBadges, setSelectedBadges] = useState(() => {
    const saved = localStorage.getItem('selectedProfileBadges');
    return saved ? JSON.parse(saved) : [];
  });
  const [selectedProfilePic, setSelectedProfilePic] = useState(() => getSelectedProfilePic());
  const { currentTheme, themes, switchTheme, getThemeColors } = useTheme();

  const themeColors = getThemeColors();

  // Convert icon string to actual Lucide icon component
  const getIconComponent = (iconString) => {
    const iconMap = {
      'BookOpen': BookOpen,
      'PenTool': PenTool,
      'CheckSquare': CheckSquare,
      'Target': Target,
      'Timer': Timer,
      'Tag': Tag,
      'Calendar': Calendar,
      'FileText': FileText,
      'Code': Code,
      'Monitor': Monitor,
      'Lightbulb': Lightbulb,
      'Coffee': Coffee,
      'Brain': Brain,
      'Heart': Heart,
      'Flame': Flame,
      'Star': Star,
      'Award': Award,
      'Trophy': Trophy,
      'Crown': Crown,
      'Shield': Shield,
      'Diamond': Diamond,
      'Sparkles': Sparkles,
      'Zap': Zap,
      'Rocket': Rocket
    };
    return iconMap[iconString] || Star; // Default to Star if icon not found
  };

  // Badge management functions
  const getUnlockedAchievements = () => {
    const unlockedIds = achievementService.getUnlockedAchievements();

    // Convert IDs to actual achievement objects
    const unlockedAchievements = unlockedIds.map(id => 
      allAchievements.find(achievement => achievement.id === id)
    ).filter(Boolean); // Remove any undefined entries

    // Debug log to see what's happening with achievements
    console.log('🏆 Badge Selector Debug:', {
      unlockedAchievementIds: unlockedIds,
      unlockedAchievements: unlockedAchievements.map(a => ({ id: a.id, name: a.name, tier: a.tier })),
      allAchievementsCount: allAchievements.length,
      firstFewAchievements: allAchievements.slice(0, 3).map(a => ({ id: a.id, name: a.name }))
    });

    // If no achievements are unlocked, let's unlock some for testing
    if (unlockedAchievements.length === 0) {
      console.log('🚀 No achievements unlocked, unlocking some for testing...');
      // Unlock some varied achievements for testing the badge system
      const testAchievements = [
        'first_scroll',      // Common tier - first note
        'word_warrior',      // Common tier - write words
        'task_rookie',       // Common tier - first task
        'tag_apprentice',    // Uncommon tier - use tags
        'verbose_victor'     // Uncommon tier - long notes
      ];

      testAchievements.forEach(id => {
        const achievement = allAchievements.find(a => a.id === id);
        if (achievement) {
          console.log(`🎯 Unlocking test achievement: ${id} (${achievement.name})`);
          achievementService.unlockAchievement(id);
        } else {
          console.warn(`⚠️ Could not find achievement with id: ${id}`);
        }
      });

      // Get the newly unlocked achievements
      const newUnlockedIds = achievementService.getUnlockedAchievements();
      return newUnlockedIds.map(id => 
        allAchievements.find(achievement => achievement.id === id)
      ).filter(Boolean);
    }

    return unlockedAchievements;
  };

  const toggleBadgeSelection = (achievementId) => {
    setSelectedBadges(prev => {
      let newSelection;
      if (prev.includes(achievementId)) {
        // Remove badge
        newSelection = prev.filter(id => id !== achievementId);
      } else if (prev.length < 3) {
        // Add badge (max 3)
        newSelection = [...prev, achievementId];
      } else {
        // Replace oldest badge
        newSelection = [...prev.slice(1), achievementId];
      }
      localStorage.setItem('selectedProfileBadges', JSON.stringify(newSelection));
      return newSelection;
    });
  };

  // Profile pic management
  const changeProfilePic = (picId) => {
    setSelectedProfilePic(picId);
    saveProfilePicSelection(picId);
    setShowProfilePicEditor(false);
  };

  const getCurrentProfilePicData = () => {
    return getProfilePicById(selectedProfilePic);
  };

  // Get highest skill from tracker data
  const getHighestSkill = () => {
    try {
      const categories = JSON.parse(localStorage.getItem('focusCategories') || '[]');
      if (categories.length === 0) {
        console.log('🔍 No focus categories found in localStorage');
        return null;
      }

      // Debug log the raw categories data
      console.log('🔍 Raw focus categories from localStorage:', categories);

      // Define skill professions based on level ranges - using Lucide React components
      const getProfessionForSkill = (skillName, level) => {
        const skillProfessions = {
          'Programming': {
            1: { name: 'Code Newbie', icon: Code },
            3: { name: 'Programmer', icon: Monitor },
            5: { name: 'Senior Dev', icon: Rocket },
            8: { name: 'Tech Lead', icon: Crown },
            10: { name: 'Code Architect', icon: Crown },
            15: { name: 'Code Wizard', icon: Crown }
          },
          'Writing': {
            1: { name: 'Word Smith', icon: PenTool },
            3: { name: 'Scribe', icon: PenTool },
            5: { name: 'Author', icon: BookOpen },
            8: { name: 'Novelist', icon: BookOpen },
            10: { name: 'Wordmaster', icon: Crown },
            15: { name: 'Literary Sage', icon: Crown }
          },
          'Art': {
            1: { name: 'Doodler', icon: Palette },
            3: { name: 'Artist', icon: Palette },
            5: { name: 'Creator', icon: Award },
            8: { name: 'Master Artist', icon: Trophy },
            10: { name: 'Art Director', icon: Crown },
            15: { name: 'Visionary', icon: Crown }
          },
          'Learning': {
            1: { name: 'Student', icon: BookOpen },
            3: { name: 'Scholar', icon: GraduationCap },
            5: { name: 'Researcher', icon: Microscope },
            8: { name: 'Expert', icon: Trophy },
            10: { name: 'Mentor', icon: Crown },
            15: { name: 'Sage', icon: Crown }
          },
          'Fitness': {
            1: { name: 'Trainee', icon: Dumbbell },
            3: { name: 'Athlete', icon: Dumbbell },
            5: { name: 'Competitor', icon: Trophy },
            8: { name: 'Champion', icon: Crown },
            10: { name: 'Legend', icon: Crown },
            15: { name: 'Titan', icon: Crown }
          },
          'Music': {
            1: { name: 'Listener', icon: Music },
            3: { name: 'Player', icon: Music },
            5: { name: 'Musician', icon: Music },
            8: { name: 'Composer', icon: Music },
            10: { name: 'Maestro', icon: Crown },
            15: { name: 'Virtuoso', icon: Crown }
          },
          'Business': {
            1: { name: 'Intern', icon: Briefcase },
            3: { name: 'Professional', icon: TrendingUp },
            5: { name: 'Manager', icon: Users },
            8: { name: 'Executive', icon: Briefcase },
            10: { name: 'CEO', icon: Crown },
            15: { name: 'Mogul', icon: Crown }
          }
        };

        // Find the appropriate profession for the level
        const professions = skillProfessions[skillName] || skillProfessions['Learning'];

        // Debug log to see what's happening
        console.log(`🔍 Skill Profession Debug for ${skillName}:`, {
          skillName,
          level,
          availableProfessions: Object.keys(skillProfessions),
          selectedProfessions: professions,
          foundMatch: !!skillProfessions[skillName]
        });

        const levelKeys = Object.keys(professions).map(Number).sort((a, b) => b - a);

        console.log(`🔍 Checking level thresholds for ${skillName} (level ${level}):`, levelKeys);

        for (const levelThreshold of levelKeys) {
          console.log(`🔍 Checking if ${level} >= ${levelThreshold}...`);
          if (level >= levelThreshold) {
            console.log(`✅ Match found! Using profession for level ${levelThreshold}:`, professions[levelThreshold]);
            return professions[levelThreshold];
          }
        }

        console.log(`🔍 No match found, defaulting to level 1 profession:`, professions[1]);
        return professions[1]; // Default to level 1 profession
      };

      // Calculate level for each skill (using more aggressive system)
      const skillsWithLevels = categories.map(category => {
        // Fix the level calculation - use simpler system
        const currentXP = category.xp || 0;

        // Every 100 XP = 1 level, but with exponential scaling
        let level = 1;
        let xpNeeded = 100; // XP needed for level 2
        let accumulatedXP = 0;

        while (currentXP >= accumulatedXP + xpNeeded) {
          accumulatedXP += xpNeeded;
          level++;
          xpNeeded += 50; // Each level requires 50 more XP than the previous gap
        }

        const profession = getProfessionForSkill(category.name, level);

        // Debug log the skill calculation
        console.log(`🎯 Skill Debug - ${category.name}: XP=${currentXP}, Level=${level}, Profession=${profession.name}`);

        return {
          ...category,
          level,
          profession
        };
      });

      // Find the highest level skill
      const highestSkill = skillsWithLevels.reduce((highest, current) => 
        current.level > highest.level ? current : highest
      );

      console.log('🏆 Highest skill determined:', {
        name: highestSkill.name,
        level: highestSkill.level,
        profession: highestSkill.profession.name,
        xp: highestSkill.xp
      });

      return highestSkill.level > 1 ? highestSkill : null;
    } catch (error) {
      console.error('Error getting highest skill:', error);
      return null;
    }
  };

  // Calculate total XP from all sources
  const calculateTotalXP = () => {
    let totalXP = 0;
    let breakdown = { notes: 0, achievements: 0, focus: 0, missions: 0 };

    // 1. Achievement XP (from unlocked achievements)
    const achievementXP = achievementStats.totalXP || 0;
    totalXP += achievementXP;
    breakdown.achievements = achievementXP;

    // 2. Notes XP
    let notesXP = 0;
    for (const note of notes) {
      // Base XP per note
      notesXP += 10;

      // Bonus XP for content length
      const wordCount = note.content ? note.content.split(/\s+/).length : 0;
      notesXP += Math.min(Math.floor(wordCount / 10), 50);

      // Bonus XP for using tags
      const tags = note.tagsString ? note.tagsString.split(',').filter(tag => tag.trim()) : [];
      notesXP += tags.length * 5;

      // Bonus XP for longer titles
      if (note.title && note.title.length > 20) {
        notesXP += 5;
      }
    }
    totalXP += notesXP;
    breakdown.notes = notesXP;

    // 3. Focus Timer XP (from tracking sessions)
    let focusXP = 0;
    try {
      const categories = JSON.parse(localStorage.getItem('focusCategories') || '[]');
      focusXP = categories.reduce((sum, category) => sum + (category.xp || 0), 0);

      // Debug check for focus XP inflation
      if (focusXP > 200) {
        console.warn('🚨 FOCUS XP IS TOO HIGH!', focusXP, 'Categories:', categories);
        console.warn('⚠️ TEMPORARILY CAPPING FOCUS XP FROM', focusXP, 'TO 100');
        focusXP = 100; // Temporary cap until we can fix the focus timer XP system
      }

      totalXP += focusXP;
    } catch (error) {
      console.error('Error calculating focus XP:', error);
    }
    breakdown.focus = focusXP;

    // 4. Mission Completion XP (2 XP per completed task)
    const completedTasks = tasks.filter(task => task.completed);
    const missionXP = completedTasks.length * 2;
    totalXP += missionXP;
    breakdown.missions = missionXP;

    // DEBUGGING: Log detailed breakdown
    console.log('🐛 DETAILED XP BREAKDOWN:');
    console.log('Notes XP:', breakdown.notes, '(from', notes.length, 'notes)');
    console.log('Achievement XP:', breakdown.achievements, '(raw achievement stats:', achievementStats, ')');
    console.log('Focus XP:', breakdown.focus);
    console.log('Mission XP:', breakdown.missions, '(from', completedTasks.length, 'completed tasks)');
    console.log('TOTAL XP:', totalXP);

    // Check if achievement XP is still inflated (old data in localStorage)
    if (breakdown.achievements > 500) {
      console.error('🚨 ACHIEVEMENT XP IS STILL TOO HIGH!', achievementStats);
      console.warn('⚠️ TEMPORARILY CAPPING ACHIEVEMENT XP FROM', breakdown.achievements, 'TO 200');
      breakdown.achievements = 200;
      totalXP = breakdown.notes + 200 + breakdown.focus + breakdown.missions;
    }

    return totalXP;
  };

