import React, { useState, useEffect } from 'react';
import { useFocusTimer, useFocusSessions } from '../../hooks'; // Fix import path
import './FocusTimerWidget.css'; // We'll create this file

const FocusTimerWidget = ({ username, className = '' }) => {
  const [showSessionSelector, setShowSessionSelector] = useState(false);
  const [selectedSessionId, setSelectedSessionId] = useState(null);

  const { 
    sessions, 
    loading: sessionsLoading, 
    createSession,
    getActiveSessions 
  } = useFocusSessions(username);

  const {
    activeSession,
    timeRemaining,
    isRunning,
    isPaused,
    currentPhase,
    currentCycle,
    startSession,
    stopSession,
    togglePause,
    formatTime,
    progress
  } = useFocusTimer(username);

  const activeSessions = getActiveSessions();

  // Quick session creation
  const createQuickSession = async (preset) => {
    const sessionData = {
      name: preset.name,
      description: preset.description,
      workDuration: preset.workDuration,
      breakDuration: preset.breakDuration,
      cycles: preset.cycles,
      category: preset.category,
      colorCode: preset.colorCode
    };

    try {
      const newSession = await createSession(sessionData);
      startSession(newSession);
    } catch (err) {
      console.error('Error creating quick session:', err);
    }
  };

  // Quick session presets
  const quickPresets = [
    {
      name: 'Pomodoro',
      description: 'Classic 25-minute focus session',
      workDuration: 25,
      breakDuration: 5,
      cycles: 1,
      category: 'WORK',
      colorCode: '#EF4444'
    },
    {
      name: 'Deep Work',
      description: 'Extended 45-minute focus session',
      workDuration: 45,
      breakDuration: 10,
      cycles: 1,
      category: 'WORK',
      colorCode: '#8B5CF6'
    },
    {
      name: 'Quick Focus',
      description: 'Short 15-minute burst',
      workDuration: 15,
      breakDuration: 3,
      cycles: 1,
      category: 'WORK',
      colorCode: '#10B981'
    }
  ];

  // Handle session selection
  const handleStartSession = () => {
    const session = activeSessions.find(s => s.id.toString() === selectedSessionId);
    if (session) {
      startSession(session);
      setShowSessionSelector(false);
    }
  };

  if (sessionsLoading) {
    return (
      <div className={`focus-timer-widget loading ${className}`}>
        <div className="loading-spinner">‚è≥ Loading focus sessions...</div>
      </div>
    );
  }

  return (
    <div className={`focus-timer-widget ${className}`}>
      {!activeSession ? (
        // Timer Idle State
        <div className="timer-idle">
          <div className="widget-header">
            <h3>üéØ Focus Timer</h3>
            <p>Ready to boost your productivity?</p>
          </div>

          {!showSessionSelector ? (
            <div className="quick-start-options">
              {/* Quick Presets */}
              <div className="quick-presets">
                <h4>Quick Start</h4>
                <div className="preset-buttons">
                  {quickPresets.map((preset, index) => (
                    <button
                      key={index}
                      className="preset-btn"
                      onClick={() => createQuickSession(preset)}
                      style={{ borderColor: preset.colorCode }}
                    >
                      <div className="preset-name">{preset.name}</div>
                      <div className="preset-duration">{preset.workDuration}m</div>
                    </button>
                  ))}
                </div>
              </div>

              {/* Existing Sessions */}
              {activeSessions.length > 0 && (
                <div className="existing-sessions">
                  <h4>Your Sessions</h4>
                  <button 
                    className="show-sessions-btn"
                    onClick={() => setShowSessionSelector(true)}
                  >
                    Choose from {activeSessions.length} sessions
                  </button>
                </div>
              )}
            </div>
          ) : (
            // Session Selector
            <div className="session-selector">
              <div className="selector-header">
                <h4>Select a Focus Session</h4>
                <button 
                  className="back-btn"
                  onClick={() => setShowSessionSelector(false)}
                >
                  ‚Üê Back
                </button>
              </div>

              <div className="session-list">
                {activeSessions.map(session => (
                  <div 
                    key={session.id}
                    className={`session-item ${selectedSessionId === session.id.toString() ? 'selected' : ''}`}
                    onClick={() => setSelectedSessionId(session.id.toString())}
                  >
                    <div className="session-info">
                      <div className="session-name">{session.name}</div>
                      <div className="session-details">
                        {session.workDuration}m work ‚Ä¢ {session.breakDuration}m break ‚Ä¢ {session.cycles} cycles
                      </div>
                      <div className="session-category">{session.category}</div>
                    </div>
                    <div 
                      className="session-color" 
                      style={{ backgroundColor: session.colorCode }}
                    />
                  </div>
                ))}
              </div>

              <button 
                className="start-selected-btn"
                onClick={handleStartSession}
                disabled={!selectedSessionId}
              >
                Start Selected Session
              </button>
            </div>
          )}
        </div>
      ) : (
        // Timer Active State
        <div className="timer-active">
          <div className="session-info">
            <h3 className="session-name">{activeSession.name}</h3>
            <div className="session-meta">
              <span className="phase-indicator">
                {currentPhase === 'work' ? '‚ö° Focus Time' : 
                 currentPhase === 'break' ? '‚òï Short Break' : 
                 'üåü Long Break'}
              </span>
              <span className="cycle-indicator">
                Cycle {currentCycle} of {activeSession.cycles}
              </span>
            </div>
          </div>

          <div className="timer-display">
            <div className="time-remaining">{formatTime()}</div>
            <div className="progress-ring">
              <svg className="progress-svg" viewBox="0 0 120 120">
                <circle
                  className="progress-background"
                  cx="60"
                  cy="60"
                  r="50"
                />
                <circle
                  className="progress-fill"
                  cx="60"
                  cy="60"
                  r="50"
                  style={{
                    strokeDasharray: `${2 * Math.PI * 50}`,
                    strokeDashoffset: `${2 * Math.PI * 50 * (1 - progress / 100)}`
                  }}
                />
              </svg>
            </div>
          </div>

          <div className="timer-controls">
            <button 
              className={`control-btn ${isPaused ? 'resume' : 'pause'}`}
              onClick={togglePause}
            >
              {isPaused ? '‚ñ∂Ô∏è Resume' : '‚è∏Ô∏è Pause'}
            </button>
            <button 
              className="control-btn stop"
              onClick={stopSession}
            >
              üõë Stop
            </button>
          </div>

          {isPaused && (
            <div className="pause-overlay">
              <div className="pause-message">‚è∏Ô∏è Timer Paused</div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default FocusTimerWidget;
