import React from 'react';
import { motion } from 'framer-motion';
import { FileText, Archive, Trophy, User } from 'lucide-react';

const TabNavigation = ({ activeTab, onTabChange }) => {
  const tabs = [
    { 
      id: 'notes', 
      label: 'MISSIONS', 
      icon: FileText, 
      color: '#22D3EE',
      description: 'Active Operations' 
    },
    { 
      id: 'library', 
      label: 'VAULT', 
      icon: Archive, 
      color: '#8B5CF6',
      description: 'Data Archives' 
    },
    { 
      id: 'achievements', 
      label: 'ACHIEVEMENTS', 
      icon: Trophy, 
      color: '#10B981',
      description: 'Mission Honors' 
    },
    { 
      id: 'profile', 
      label: 'PROFILE', 
      icon: User, 
      color: '#F59E0B',
      description: 'Operator Status' 
    }
  ];

  return (
    <div className="pixel-tab-navigation">
      <div className="tab-container">
        {tabs.map((tab) => {
          const IconComponent = tab.icon;
          const isActive = activeTab === tab.id;

          return (
            <motion.button
              key={tab.id}
              className={`pixel-tab ${isActive ? 'active' : ''}`}
              onClick={() => onTabChange(tab.id)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              style={{
                '--tab-color': tab.color,
                '--tab-shadow': isActive 
                  ? `0 0 20px ${tab.color}60, 0 8px 16px rgba(192, 192, 192, 0.4)` 
                  : `0 0 5px ${tab.color}20`
              }}
              title={tab.description}
            >
              <div className="tab-content">
                <IconComponent size={16} className="tab-icon" />
                <span className="tab-label">{tab.label}</span>
              </div>

              {isActive && (
                <motion.div
                  className="tab-indicator"
                  layoutId="activeTab"
                  initial={false}
                  transition={{ type: "spring", stiffness: 300, damping: 30 }}
                />
              )}

              {/* Enhanced glow effect */}
              {isActive && (
                <motion.div
                  className="absolute inset-0 pointer-events-none"
                  style={{
                    background: `radial-gradient(ellipse at center, ${tab.color}15 0%, transparent 70%)`,
                  }}
                  animate={{ opacity: [0.3, 0.6, 0.3] }}
                  transition={{ duration: 2, repeat: Infinity }}
                />
              )}
            </motion.button>
          );
        })}
      </div>
    </div>
  );
};

export default TabNavigation;
