import React from 'react';

/**
 * Profession Badge Component
 * Displays the user's highest skill profession and level
 */
const ProfessionBadge = ({ highestSkill, className = "" }) => {
  if (!highestSkill) {
    return null;
  }

  const IconComponent = highestSkill.profession?.icon;

  return (
    <div className={`bg-gradient-to-r from-yellow-600/20 to-orange-600/20 border border-yellow-500/30 rounded-lg p-3 text-center ${className}`}>
      <div className="flex items-center justify-center gap-2 mb-1">
        {IconComponent && <IconComponent className="text-yellow-400" size={18} />}
        <span className="text-yellow-400 font-bold text-sm">
          {highestSkill.profession?.name || `Level ${highestSkill.level}`}
        </span>
      </div>
      <div className="text-xs text-gray-400">
        {highestSkill.name} â€¢ Level {highestSkill.level}
      </div>
    </div>
  );
};

export default ProfessionBadge;
