import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Trophy, Star, Zap, Crown, Lock, CheckCircle, Target, BarChart3, Award, Grid, List } from 'lucide-react';
import { allAchievements, achievementsByTier, tierInfo } from '../../data/achievements';
import achievementService from '../../services/achievementService';
import AchievementBadge from '../ui/AchievementBadge';

const AchievementsTab = ({ username = 'Jroc_182', tabColor = '#F59E0B' }) => {
  const [selectedTier, setSelectedTier] = useState('all');
  const [achievementStats, setAchievementStats] = useState({});
  const [userStats, setUserStats] = useState({});
  const [selectedAchievement, setSelectedAchievement] = useState(null);
  const [viewMode, setViewMode] = useState('badges'); // 'badges' or 'list'

  useEffect(() => {
    loadAchievementData();
  }, []);

  const loadAchievementData = () => {
    const stats = achievementService.getStats();
    setAchievementStats(stats);

    // Load comprehensive user stats
    const notes = JSON.parse(localStorage.getItem('notes') || '[]');
    const tasks = JSON.parse(localStorage.getItem('tasks') || '[]');
    const sessions = JSON.parse(localStorage.getItem('focusSessions') || '[]');

    const calculatedStats = {
      totalNotes: notes.length,
      totalWords: notes.reduce((sum, note) => sum + (note.content?.split(' ').length || 0), 0),
      uniqueTags: new Set(notes.flatMap(note => note.tags || [])).size,
      totalTasks: tasks.filter(t => t.completed).length,
      tasksCreated: tasks.length,
      totalSessions: sessions.length,
      totalFocusTime: sessions.reduce((sum, s) => sum + s.timeSpent, 0),
      level: Math.floor(stats.totalXP / 1000) + 1,
      totalXP: stats.totalXP
    };

    setUserStats(calculatedStats);
  };

  const unlockedAchievements = allAchievements.filter(a => achievementService.isUnlocked(a.id));
  const lockedAchievements = allAchievements.filter(a => !achievementService.isUnlocked(a.id));

  const getFilteredAchievements = (achievements) => {
    if (selectedTier === 'all') return achievements;
    return achievements.filter(a => a.tier === selectedTier);
  };

  const hexToRgb = (hex) => {
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? 
      `${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}` :
      '245, 158, 11';
  };

  const tabColorRgb = hexToRgb(tabColor);

  return (
    <div className="p-6 space-y-6">
      {/* Enhanced Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="mb-8"
      >
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-3">
            <div 
              className="w-6 h-6 border border-gray-600" 
              style={{ backgroundColor: tabColor }}
            />
            <h1 className="font-mono text-3xl font-bold text-white">
              BADGE COLLECTION
            </h1>
          </div>

          {/* View Mode Toggle */}
          <div className="flex items-center gap-2">
            <button
              onClick={() => setViewMode('badges')}
              className={`p-2 border-2 transition-all ${
                viewMode === 'badges'
                  ? 'border-white text-white bg-white bg-opacity-10'
                  : 'border-gray-600 text-gray-400 hover:border-gray-500'
              }`}
            >
              <Grid size={20} />
            </button>
            <button
              onClick={() => setViewMode('list')}
              className={`p-2 border-2 transition-all ${
                viewMode === 'list'
                  ? 'border-white text-white bg-white bg-opacity-10'
                  : 'border-gray-600 text-gray-400 hover:border-gray-500'
              }`}
            >
              <List size={20} />
            </button>
          </div>
        </div>

        <p className="text-gray-400 font-mono text-sm">
          Showcase your achievements and track your progress through the digital realm.
        </p>
      </motion.div>

      {/* Collection Progress - Remove XP Display */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] relative mb-6"
        style={{
          borderColor: tabColor,
          boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="absolute inset-0 border-2 opacity-30 animate-pulse pointer-events-none" 
             style={{ borderColor: tabColor }} />

        <div className="relative z-10">
          <div className="border-b px-4 py-3"
               style={{ 
                 borderColor: tabColor,
                 backgroundColor: `${tabColor}20`
               }}>
            <h3 className="text-lg font-mono font-bold text-white flex items-center">
              <BarChart3 className="mr-2" size={20} style={{ color: tabColor }} />
              COLLECTION PROGRESS
            </h3>
          </div>

          <div className="p-6">
            <div className="grid grid-cols-3 gap-6 mb-6">
              <div className="text-center">
                <motion.div 
                  className="text-3xl font-mono font-bold text-green-400 mb-1"
                  animate={{ scale: [1, 1.05, 1] }}
                  transition={{ duration: 2, repeat: Infinity }}
                >
                  {achievementStats.unlocked || 0}
                </motion.div>
                <div className="text-xs text-gray-400 font-mono">BADGES EARNED</div>
              </div>
              <div className="text-center">
                <motion.div 
                  className="text-3xl font-mono font-bold text-blue-400 mb-1"
                  animate={{ scale: [1, 1.05, 1] }}
                  transition={{ duration: 2, repeat: Infinity, delay: 0.5 }}
                >
                  {achievementStats.percentage || 0}%
                </motion.div>
                <div className="text-xs text-gray-400 font-mono">COMPLETION</div>
              </div>
              <div className="text-center">
                <motion.div 
                  className="text-3xl font-mono font-bold text-purple-400 mb-1"
                  animate={{ scale: [1, 1.05, 1] }}
                  transition={{ duration: 2, repeat: Infinity, delay: 1 }}
                >
                  {105}
                </motion.div>
                <div className="text-xs text-gray-400 font-mono">TOTAL BADGES</div>
              </div>
            </div>

            {/* Enhanced Tier Breakdown */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {Object.entries(tierInfo).map(([tier, info]) => {
                const tierCount = achievementStats.byTier?.[tier] || 0;
                const tierTotal = achievementsByTier[tier]?.length || 0;
                const isSelected = selectedTier === tier;

                return (
                  <motion.div 
                    key={tier}
                    className="bg-gray-900 border-2 p-4 text-center cursor-pointer transition-all relative overflow-hidden"
                    style={{
                      borderColor: isSelected ? info.color : '#4B5563',
                      backgroundColor: isSelected ? `${info.color}15` : 'transparent',
                      boxShadow: isSelected ? `0 0 15px ${info.color}40` : 'none'
                    }}
                    onClick={() => setSelectedTier(isSelected ? 'all' : tier)}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    {isSelected && (
                      <div 
                        className="absolute inset-0 opacity-10 animate-pulse"
                        style={{ backgroundColor: info.color }}
                      />
                    )}

                    <div className="relative z-10">
                      <div style={{ color: info.color }} className="text-3xl mb-2">
                        {info.emoji}
                      </div>
                      <div className="text-lg font-mono font-bold text-white mb-1">
                        {tierCount}/{tierTotal}
                      </div>
                      <div className="text-xs text-gray-400 font-mono">
                        {info.name}
                      </div>

                      {/* Progress bar */}
                      <div className="mt-2 w-full bg-gray-700 h-1">
                        <div 
                          className="h-full transition-all duration-500"
                          style={{ 
                            width: `${tierTotal > 0 ? (tierCount / tierTotal) * 100 : 0}%`,
                            backgroundColor: info.color
                          }}
                        />
                      </div>
                    </div>
                  </motion.div>
                );
              })}
            </div>
          </div>
        </div>
      </motion.div>

      {/* Progress Metrics Section (Matching Your Image) */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-gray-800 border-2 border-purple-500 p-6 mb-8"
        style={{
          boxShadow: '0 0 20px rgba(168, 85, 247, 0.3)'
        }}
      >
        <h2 className="text-lg font-mono font-bold text-white mb-6 flex items-center">
          <Trophy className="mr-2" size={20} style={{ color: tabColor }} />
          PROGRESS METRICS
        </h2>

        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {/* Completed */}
          <div className="bg-gray-900 border-2 border-green-500 p-6 text-center"
               style={{ boxShadow: '0 0 15px rgba(34, 197, 94, 0.3)' }}>
            <div className="text-green-400 mb-2">
              <CheckCircle size={32} className="mx-auto" />
            </div>
            <div className="text-green-400 font-mono text-sm mb-2">COMPLETED</div>
            <div className="text-3xl font-mono font-bold text-green-400">{unlockedAchievements.length}</div>
          </div>

          {/* In Progress */}
          <div className="bg-gray-900 border-2 border-blue-500 p-6 text-center"
               style={{ boxShadow: '0 0 15px rgba(59, 130, 246, 0.3)' }}>
            <div className="text-blue-400 mb-2">
              <Target size={32} className="mx-auto" />
            </div>
            <div className="text-blue-400 font-mono text-sm mb-2">IN PROGRESS</div>
            <div className="text-3xl font-mono font-bold text-blue-400">
              {lockedAchievements.filter(a => {
                const progress = achievementService.getAchievementProgress(a.id, userStats);
                return progress > 0 && progress < 1;
              }).length}
            </div>
          </div>

          {/* Locked */}
          <div className="bg-gray-900 border-2 border-gray-500 p-6 text-center"
               style={{ boxShadow: '0 0 15px rgba(107, 114, 128, 0.3)' }}>
            <div className="text-gray-400 mb-2">
              <Lock size={32} className="mx-auto" />
            </div>
            <div className="text-gray-400 font-mono text-sm mb-2">LOCKED</div>
            <div className="text-3xl font-mono font-bold text-gray-400">
              {lockedAchievements.filter(a => {
                const progress = achievementService.getAchievementProgress(a.id, userStats);
                return progress === 0;
              }).length}
            </div>
          </div>

          {/* Total */}
          <div className="bg-gray-900 border-2 border-yellow-500 p-6 text-center"
               style={{ boxShadow: '0 0 15px rgba(234, 179, 8, 0.3)' }}>
            <div className="text-yellow-400 mb-2">
              <Trophy size={32} className="mx-auto" />
            </div>
            <div className="text-yellow-400 font-mono text-sm mb-2">TOTAL</div>
            <div className="text-3xl font-mono font-bold text-yellow-400">{allAchievements.length}</div>
          </div>
        </div>
      </motion.div>

      {/* My Achievement Badges Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] relative"
        style={{
          borderColor: tabColor,
          boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="absolute inset-0 border-2 opacity-30 animate-pulse pointer-events-none" 
             style={{ borderColor: tabColor }} />

        <div className="relative z-10">
          <div className="border-b px-4 py-3"
               style={{ 
                 borderColor: tabColor,
                 backgroundColor: `${tabColor}20`
               }}>
            <h3 className="text-lg font-mono font-bold text-white flex items-center">
              <Trophy className="mr-2" size={20} style={{ color: tabColor }} />
              MY ACHIEVEMENT BADGES ({getFilteredAchievements(unlockedAchievements).length})
            </h3>
          </div>

          <div className="p-6">
            {getFilteredAchievements(unlockedAchievements).length > 0 ? (
              <div className={viewMode === 'badges' 
                ? "grid grid-cols-4 md:grid-cols-6 lg:grid-cols-8 gap-6" 
                : "space-y-3"
              }>
                <AnimatePresence>
                  {getFilteredAchievements(unlockedAchievements).map((achievement, index) => (
                    <motion.div 
                      key={achievement.id}
                      initial={{ opacity: 0, scale: 0.8 }}
                      animate={{ opacity: 1, scale: 1 }}
                      exit={{ opacity: 0, scale: 0.8 }}
                      transition={{ delay: index * 0.05 }}
                      className={viewMode === 'badges' 
                        ? "flex flex-col items-center gap-3"
                        : "flex items-center gap-4 bg-gray-900 border border-gray-600 p-3"
                      }
                    >
                      <AchievementBadge 
                        achievement={achievement}
                        size={viewMode === 'badges' ? 'medium' : 'small'}
                        isUnlocked={true}
                        onClick={() => setSelectedAchievement(achievement)}
                      />

                      {viewMode === 'badges' ? (
                        <div className="text-xs font-mono text-center text-gray-300 max-w-[90px]">
                          <div className="truncate font-bold">{achievement.name}</div>
                          <div className="text-gray-500 text-[10px] mt-1">
                            {tierInfo[achievement.tier].name}
                          </div>
                        </div>
                      ) : (
                        <div className="flex-1">
                          <div className="font-mono font-bold text-sm text-white">
                            {achievement.name}
                          </div>
                          <div className="text-xs text-gray-400 font-mono">
                            {achievement.description}
                          </div>
                          <div className="text-xs font-mono mt-1" style={{ color: tierInfo[achievement.tier].color }}>
                            {tierInfo[achievement.tier].emoji} {tierInfo[achievement.tier].name}
                          </div>
                        </div>
                      )}
                    </motion.div>
                  ))}
                </AnimatePresence>
              </div>
            ) : (
              <motion.div 
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="text-center py-12"
              >
                <motion.div
                  animate={{ 
                    rotate: [0, 10, -10, 0],
                    scale: [1, 1.1, 1]
                  }}
                  transition={{ 
                    duration: 2, 
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                >
                  <Trophy size={64} className="text-gray-600 mx-auto mb-4" />
                </motion.div>
                <div className="text-gray-400 font-mono text-xl font-bold mb-3">
                  NO BADGES EARNED YET
                </div>
                <div className="text-gray-500 font-mono text-sm max-w-md mx-auto">
                  Complete activities like writing notes, focusing on tasks, and reaching milestones to earn your first achievement badges!
                </div>
              </motion.div>
            )}
          </div>
        </div>
      </motion.div>

      {/* Locked Achievements Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2 }}
        className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] relative"
        style={{
          borderColor: tabColor,
          boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="absolute inset-0 border-2 opacity-30 animate-pulse pointer-events-none" 
             style={{ borderColor: tabColor }} />

        <div className="relative z-10">
          <div className="border-b px-4 py-3"
               style={{ 
                 borderColor: tabColor,
                 backgroundColor: `${tabColor}20`
               }}>
            <h3 className="text-lg font-mono font-bold text-white flex items-center">
              <Lock className="mr-2" size={20} style={{ color: tabColor }} />
              LOCKED ACHIEVEMENTS ({getFilteredAchievements(lockedAchievements).length})
            </h3>
          </div>

          <div className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              <AnimatePresence>
                {getFilteredAchievements(lockedAchievements).map((achievement, index) => {
                  const progress = achievementService.getAchievementProgress(achievement.id, userStats);
                  const tier = tierInfo[achievement.tier];

                  return (
                    <motion.div
                      key={achievement.id}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, y: 20 }}
                      transition={{ delay: index * 0.05 }}
                      className="bg-gray-900 border border-gray-600 p-4 relative transition-all duration-300 hover:border-gray-500 cursor-pointer group"
                      onClick={() => setSelectedAchievement(achievement)}
                    >
                      <div className="flex items-center gap-3 mb-3">
                        <AchievementBadge 
                          achievement={achievement}
                          size="small"
                          showGlow={false}
                          isUnlocked={false}
                        />

                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <span className="text-xs font-mono" style={{ color: tier.color }}>
                              {tier.emoji} {tier.name.toUpperCase()}
                            </span>
                          </div>
                          <div className="font-mono font-bold text-sm text-gray-300 group-hover:text-white transition-colors">
                            {achievement.name}
                          </div>
                        </div>
                      </div>

                      <div className="text-xs font-mono mb-3 text-gray-400">
                        {achievement.description}
                      </div>

                      {/* Enhanced Progress Bar */}
                      {progress > 0 && (
                        <div className="mb-3">
                          <div className="flex justify-between items-center mb-2">
                            <span className="text-xs font-mono text-gray-500">Progress</span>
                            <span className="text-xs font-mono font-bold" style={{ color: tier.color }}>
                              {Math.round(progress * 100)}%
                            </span>
                          </div>
                          <div className="w-full bg-gray-700 h-2 border border-gray-600 relative overflow-hidden">
                            <motion.div 
                              className="h-full transition-all duration-500 relative"
                              style={{ 
                                width: `${progress * 100}%`,
                                backgroundColor: tier.color,
                                opacity: 0.8
                              }}
                              initial={{ width: 0 }}
                              animate={{ width: `${progress * 100}%` }}
                              transition={{ duration: 1, delay: index * 0.1 }}
                            >
                              {/* Animated shine effect on progress bar */}
                              <div 
                                className="absolute inset-0 opacity-50"
                                style={{
                                  background: `linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent)`
                                }}
                              />
                            </motion.div>
                          </div>
                        </div>
                      )}

                      {progress === 0 && (
                        <div className="text-xs font-mono text-gray-600 flex items-center gap-1">
                          <Target size={12} />
                          Not started
                        </div>
                      )}
                    </motion.div>
                  );
                })}
              </AnimatePresence>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Enhanced Achievement Detail Modal */}
      <AnimatePresence>
        {selectedAchievement && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50 p-4"
            onClick={() => setSelectedAchievement(null)}
          >
            <motion.div
              initial={{ scale: 0.8, opacity: 0, y: 50 }}
              animate={{ scale: 1, opacity: 1, y: 0 }}
              exit={{ scale: 0.8, opacity: 0, y: 50 }}
              className="bg-gray-800 border-2 shadow-[12px_12px_0px_0px_rgba(0,0,0,1)] p-8 max-w-lg mx-4 relative"
              style={{
                borderColor: tierInfo[selectedAchievement.tier].color,
                boxShadow: `0 0 40px ${tierInfo[selectedAchievement.tier].color}60, 12px 12px 0px 0px rgba(0,0,0,1)`
              }}
              onClick={(e) => e.stopPropagation()}
            >
              {/* Background glow */}
              <div 
                className="absolute inset-0 opacity-5 animate-pulse"
                style={{ backgroundColor: tierInfo[selectedAchievement.tier].color }}
              />

              <div className="relative z-10 text-center">
                <AchievementBadge 
                  achievement={selectedAchievement}
                  size="xlarge"
                  showGlow={achievementService.isUnlocked(selectedAchievement.id)}
                  isUnlocked={achievementService.isUnlocked(selectedAchievement.id)}
                />

                <h3 className="font-mono font-bold text-2xl text-white mt-6 mb-3">
                  {selectedAchievement.name}
                </h3>

                <p className="text-gray-400 font-mono text-sm mb-6 leading-relaxed">
                  {selectedAchievement.description}
                </p>

                <div className="flex justify-center gap-6 text-sm font-mono mb-6">
                  <div className="text-center">
                    <div style={{ color: tierInfo[selectedAchievement.tier].color }} className="text-2xl mb-1">
                      {tierInfo[selectedAchievement.tier].emoji}
                    </div>
                    <div className="text-gray-400">
                      {tierInfo[selectedAchievement.tier].name}
                    </div>
                  </div>
                  <div className="text-center">
                    <div className="text-2xl mb-1 text-blue-400">
                      📋
                    </div>
                    <div className="text-gray-400">
                      {selectedAchievement.category.toUpperCase()}
                    </div>
                  </div>
                </div>

                {achievementService.isUnlocked(selectedAchievement.id) ? (
                  <motion.div 
                    className="text-green-400 font-mono text-lg font-bold"
                    animate={{ scale: [1, 1.05, 1] }}
                    transition={{ duration: 1.5, repeat: Infinity }}
                  >
                    ✓ BADGE EARNED
                  </motion.div>
                ) : (
                  <div className="text-yellow-400 font-mono text-sm">
                    🔒 KEEP PROGRESSING TO UNLOCK
                  </div>
                )}
              </div>

              <button
                onClick={() => setSelectedAchievement(null)}
                className="absolute top-4 right-4 text-gray-400 hover:text-white text-xl font-bold transition-colors"
              >
                ✕
              </button>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default AchievementsTab;
