import React from 'react';
import { motion } from 'framer-motion';
import { Calendar, TrendingUp } from 'lucide-react';

const ProductivityHeatmap = ({ notes = [], tasks = [] }) => {
  // Generate last 30 days
  const generateDays = () => {
    const days = [];
    const today = new Date();

    for (let i = 29; i >= 0; i--) {
      const date = new Date(today);
      date.setDate(date.getDate() - i);
      days.push(date);
    }
    return days;
  };

  // Calculate activity for each day
  const getActivityForDay = (date) => {
    const dayStart = new Date(date);
    dayStart.setHours(0, 0, 0, 0);
    const dayEnd = new Date(date);
    dayEnd.setHours(23, 59, 59, 999);

    const dayNotes = notes.filter(note => {
      const noteDate = new Date(note.createdAt || note.updatedAt);
      return noteDate >= dayStart && noteDate <= dayEnd;
    }).length;

    const dayTasks = tasks.filter(task => {
      const taskDate = new Date(task.createdAt || task.updatedAt);
      return task.completed && taskDate >= dayStart && taskDate <= dayEnd;
    }).length;

    return dayNotes + dayTasks;
  };

  // Get intensity level (0-4)
  const getIntensity = (activity) => {
    if (activity === 0) return 0;
    if (activity <= 2) return 1;
    if (activity <= 4) return 2;
    if (activity <= 7) return 3;
    return 4;
  };

  // Get color based on intensity
  const getColor = (intensity) => {
    switch (intensity) {
      case 0: return '#374151'; // gray-700
      case 1: return '#065f46'; // emerald-800
      case 2: return '#059669'; // emerald-600
      case 3: return '#10b981'; // emerald-500
      case 4: return '#6ee7b7'; // emerald-300
      default: return '#374151';
    }
  };

  const days = generateDays();
  const weekdays = ['S', 'M', 'T', 'W', 'T', 'F', 'S'];

  return (
    <div className="bg-gray-800 border-2 border-emerald-400 shadow-[4px_4px_0px_0px_rgba(0,0,0,1)] p-6 relative"
         style={{
           boxShadow: '0 0 15px rgba(16, 185, 129, 0.2), 4px 4px 0px 0px rgba(0,0,0,1)'
         }}>
      <div className="absolute inset-0 border-2 border-emerald-400 opacity-30 animate-pulse pointer-events-none" />

      <h2 className="text-lg font-mono font-bold text-white flex items-center mb-4">
        <div className="w-4 h-4 bg-emerald-400 mr-2" />
        PRODUCTIVITY HEATMAP
      </h2>

      <div className="space-y-3">
        {/* Weekday labels */}
        <div className="grid grid-cols-7 gap-1 text-xs font-mono text-gray-400 text-center">
          {weekdays.map(day => (
            <div key={day}>{day}</div>
          ))}
        </div>

        {/* Heatmap grid */}
        <div className="grid grid-cols-7 gap-1">
          {days.map((date, index) => {
            const activity = getActivityForDay(date);
            const intensity = getIntensity(activity);
            const color = getColor(intensity);

            return (
              <motion.div
                key={index}
                initial={{ opacity: 0, scale: 0.5 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: index * 0.02 }}
                className="w-4 h-4 border border-gray-600 cursor-pointer hover:border-emerald-400 transition-colors"
                style={{ backgroundColor: color }}
                title={`${date.toLocaleDateString()}: ${activity} activities`}
              />
            );
          })}
        </div>

        {/* Legend */}
        <div className="flex items-center justify-between text-xs font-mono text-gray-400">
          <span>Less</span>
          <div className="flex gap-1">
            {[0, 1, 2, 3, 4].map(level => (
              <div
                key={level}
                className="w-3 h-3 border border-gray-600"
                style={{ backgroundColor: getColor(level) }}
              />
            ))}
          </div>
          <span>More</span>
        </div>
      </div>
    </div>
  );
};

export default ProductivityHeatmap;
