import React, { useState } from 'react';
import { motion } from 'framer-motion';

const HiddenButton = () => {
  const [isHovered, setIsHovered] = useState(false);
  const [isFound, setIsFound] = useState(false);

  const handleClick = () => {
    setIsFound(true);
    // TODO: Open easter egg page
    alert('Easter egg found! Secret page coming soon...');
    console.log('Easter egg discovered!');
  };

  return (
    <motion.div
      className="absolute bottom-2 right-2 w-4 h-4 cursor-pointer z-10"
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
      onClick={handleClick}
      whileHover={{ scale: 1.5 }}
      whileTap={{ scale: 0.8 }}
      animate={{
        // Subtle pulsing animation to hint at interactivity
        opacity: [0.3, 0.7, 0.3],
      }}
      transition={{
        duration: 3,
        repeat: Infinity,
        ease: "easeInOut"
      }}
      style={{
        background: isHovered || isFound 
          ? 'linear-gradient(45deg, #ff6b6b, #feca57, #48dbfb, #ff9ff3)' 
          : 'rgba(255, 255, 255, 0.4)', // Made more visible
        borderRadius: '2px',
        boxShadow: isHovered || isFound 
          ? '0 0 12px rgba(255, 107, 107, 0.8)' 
          : '0 0 4px rgba(255, 255, 255, 0.3)', // Added subtle glow
        transition: 'all 0.3s ease',
        border: isHovered ? '1px solid rgba(255, 255, 255, 0.8)' : '1px solid rgba(255, 255, 255, 0.2)'
      }}
      title={isHovered ? "Secret discovered!" : ""}
    >
      {/* Tiny pixel that's more visible */}
      <div 
        className="w-full h-full flex items-center justify-center"
        style={{
          background: isFound 
            ? 'radial-gradient(circle, #fff 40%, transparent 40%)'
            : isHovered 
              ? 'radial-gradient(circle, #fff 60%, transparent 60%)'
              : 'radial-gradient(circle, rgba(255,255,255,0.6) 30%, transparent 30%)'
        }}
      >
        {/* Add a tiny "?" hint when hovered */}
        {isHovered && (
          <span className="text-xs font-bold text-black">?</span>
        )}
      </div>
    </motion.div>
  );
};

export default HiddenButton;
