import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence, useMotionValue, useTransform } from 'framer-motion';
import { X, BookOpen, Star, Trophy } from 'lucide-react';

// ===== Game Configuration =====
const GAME_WIDTH = 360;
const GAME_HEIGHT = 540;
const PLAYER_X = 56;
const PLAYER_SIZE = 28;
const PIPE_WIDTH = 52;
const GAP_HEIGHT = 155;
const PIPE_SPACING = 260;
const PIPE_SPEED = 2.6;
const GRAVITY = 0.45;
const JUMP_FORCE = -7.6;

function randGapTop() {
  const margin = 44;
  const min = margin;
  const max = GAME_HEIGHT - GAP_HEIGHT - margin;
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

function clamp(v, lo, hi) {
  return Math.max(lo, Math.min(hi, v));
}

function aabb(ax, ay, aw, ah, bx, by, bw, bh) {
  return ax < bx + bw && ax + aw > bx && ay < by + bh && ay + ah > by;
}

const FlappyNotebook = ({ onScore, onGameOver, icon = 'book', theme = 'neon' }) => {
  const velocity = useRef(0);
  const [playerY, setPlayerY] = useState(GAME_HEIGHT / 2);
  const playerYRef = useRef(GAME_HEIGHT / 2);
  const [running, setRunning] = useState(true);
  const [score, setScore] = useState(0);
  const [frame, setFrame] = useState(0);
  const [flash, setFlash] = useState(false);
  const pipesRef = useRef([]);

  const reset = () => {
    velocity.current = 0;
    playerYRef.current = GAME_HEIGHT / 2;
    setPlayerY(GAME_HEIGHT / 2);
    setScore(0);
    pipesRef.current = new Array(3).fill(0).map((_, i) => ({
      x: GAME_WIDTH + i * PIPE_SPACING,
      gapTop: randGapTop(),
      scored: false,
    }));
  };

  useEffect(() => {
    reset();
    setRunning(true);

    const key = (e) => {
      if (e.code === 'Space' || e.code === 'ArrowUp') {
        e.preventDefault();
        jump();
      }
    };
    const click = () => jump();

    window.addEventListener('keydown', key);
    window.addEventListener('pointerdown', click);

    let raf = 0;
    const tick = () => {
      raf = requestAnimationFrame(tick);
      if (!running) return;

      // Physics
      velocity.current += GRAVITY;
      const newY = clamp(playerYRef.current + velocity.current, 0, GAME_HEIGHT - PLAYER_SIZE);
      playerYRef.current = newY;
      setPlayerY(newY);

      // Move pipes
      pipesRef.current.forEach((p) => (p.x -= PIPE_SPEED));

      // Recycle pipes and scoring
      for (const p of pipesRef.current) {
        if (p.x + PIPE_WIDTH < 0) {
          const far = Math.max(...pipesRef.current.map((q) => q.x));
          p.x = far + PIPE_SPACING;
          p.gapTop = randGapTop();
          p.scored = false;
        }
        const playerCenterX = PLAYER_X + PLAYER_SIZE / 2;
        const pipeCenterX = p.x + PIPE_WIDTH / 2;
        if (!p.scored && pipeCenterX < playerCenterX) {
          p.scored = true;
          setScore((s) => {
            const ns = s + 1;
            onScore && onScore(ns);
            return ns;
          });
        }
      }

      // Collision detection
      const px = PLAYER_X;
      const py = newY;
      let dead = py <= 0 || py + PLAYER_SIZE >= GAME_HEIGHT;
      if (!dead) {
        for (const p of pipesRef.current) {
          const topHit = aabb(px, py, PLAYER_SIZE, PLAYER_SIZE, p.x, 0, PIPE_WIDTH, p.gapTop);
          const bottomY = p.gapTop + GAP_HEIGHT;
          const bottomHit = aabb(px, py, PLAYER_SIZE, PLAYER_SIZE, p.x, bottomY, PIPE_WIDTH, GAME_HEIGHT - bottomY);
          if (topHit || bottomHit) { 
            dead = true; 
            break; 
          }
        }
      }

      if (dead) {
        setRunning(false);
        setFlash(true);
        setTimeout(() => setFlash(false), 120);
        onGameOver && onGameOver(score);
        setTimeout(() => { 
          reset(); 
          setRunning(true); 
        }, 900);
      }

      setFrame((f) => f + 1);
    };

    tick();
    return () => {
      cancelAnimationFrame(raf);
      window.removeEventListener('keydown', key);
      window.removeEventListener('pointerdown', click);
    };
  }, [running, onScore, onGameOver]);

  const jump = () => {
    if (!running) return;
    velocity.current = JUMP_FORCE;
  };

  // Tilt effect for the player
  const vy = useMotionValue(0);
  useEffect(() => { 
    vy.set(velocity.current); 
  }, [frame, vy]);
  const tilt = useTransform(vy, [-6, 10], [-22, 38]);

  const themeStyles = {
    container: {
      position: 'relative',
      overflow: 'hidden',
      width: GAME_WIDTH,
      height: GAME_HEIGHT,
      border: '2px solid rgba(255,255,255,0.8)',
      borderRadius: 16,
      boxShadow: '0 0 20px rgba(255,255,255,0.3), inset 0 0 20px rgba(255,255,255,0.1)',
      cursor: 'pointer',
      userSelect: 'none',
      background: 'rgba(0,0,0,0.2)', // Transparent black to show shooting stars
      backdropFilter: 'blur(2px)'
    },
    scanlines: {
      position: 'absolute',
      inset: 0,
      pointerEvents: 'none',
      backgroundImage: 'repeating-linear-gradient(transparent, transparent 22px, rgba(255,255,255,0.06) 23px)'
    },
    hud: {
      position: 'absolute',
      top: 8,
      left: 0,
      right: 0,
      textAlign: 'center',
      color: '#fff',
      fontFamily: 'ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace',
      textShadow: '0 0 10px rgba(255,255,255,0.8)'
    },
    ribbon: {
      position: 'absolute',
      left: 8,
      top: 8,
      padding: '2px 6px',
      borderRadius: 6,
      border: '1px solid rgba(255,255,255,0.6)',
      background: 'rgba(0,0,0,0.4)',
      color: 'rgba(255,255,255,0.9)',
      fontFamily: 'monospace',
      fontSize: 12,
      boxShadow: '0 0 10px rgba(255,255,255,0.2)'
    },
    scorePill: {
      display: 'inline-flex',
      alignItems: 'center',
      gap: 8,
      padding: '4px 10px',
      borderRadius: 9999,
      border: '1px solid rgba(255,255,255,0.6)',
      background: 'rgba(0,0,0,0.4)',
      boxShadow: '0 0 15px rgba(255,255,255,0.3)'
    },
    gameOverPill: {
      display: 'inline-flex',
      alignItems: 'center',
      gap: 8,
      padding: '4px 10px',
      borderRadius: 9999,
      border: '1px solid rgba(255,255,255,0.6)',
      background: 'rgba(0,0,0,0.5)',
      boxShadow: '0 0 15px rgba(255,255,255,0.3)'
    },
    pipeTop: {
      position: 'absolute',
      left: 0,
      top: 0,
      width: PIPE_WIDTH,
      border: '2px solid rgba(255,255,255,0.8)',
      background: 'rgba(0,0,0,0.3)',
      boxShadow: '0 0 15px rgba(255,255,255,0.4), inset 0 0 10px rgba(255,255,255,0.1)'
    },
    pipeBottom: {
      position: 'absolute',
      left: 0,
      border: '2px solid rgba(255,255,255,0.8)',
      width: PIPE_WIDTH,
      background: 'rgba(0,0,0,0.3)',
      boxShadow: '0 0 15px rgba(255,255,255,0.4), inset 0 0 10px rgba(255,255,255,0.1)'
    },
    playerWrap: {
      position: 'absolute',
      left: PLAYER_X,
      top: playerY,
      width: PLAYER_SIZE,
      height: PLAYER_SIZE,
      transform: `translateY(-${PLAYER_SIZE / 2}px)`,
      color: '#00ffff', // Cyan color for the book icon
      filter: 'drop-shadow(0 0 20px rgba(0, 255, 255, 1))', // Strong cyan glow
      textShadow: '0 0 15px rgba(0, 255, 255, 0.8)'
    },
    controls: {
      display: 'flex',
      alignItems: 'center',
      gap: 8,
      color: 'rgba(255,255,255,0.9)',
      fontFamily: 'monospace',
      fontSize: 12
    },
    kbd: {
      border: '1px solid rgba(255,255,255,0.6)',
      background: 'rgba(0,0,0,0.5)',
      borderRadius: 6,
      padding: '2px 6px',
      boxShadow: '0 0 8px rgba(255,255,255,0.2)'
    }
  };

  const IconEl = icon === 'star' ? Star : icon === 'trophy' ? Trophy : BookOpen;

  return (
    <div style={{ display: 'grid', placeItems: 'center', gap: 8, userSelect: 'none' }}>
      <div
        role="button"
        style={themeStyles.container}
        onClick={jump}
      >
        {/* Flash effect on collision */}
        <AnimatePresence>
          {flash && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 0.6 }}
              exit={{ opacity: 0 }}
              style={{
                position: 'absolute',
                inset: 0,
                background: 'rgba(248,113,113,0.6)',
                mixBlendMode: 'screen'
              }}
            />
          )}
        </AnimatePresence>

        {/* Scanlines overlay */}
        <div style={themeStyles.scanlines} />

        {/* Pipes */}
        {pipesRef.current.map((p, i) => (
          <React.Fragment key={i}>
            <motion.div
              style={{
                ...themeStyles.pipeTop,
                x: p.x,
                height: p.gapTop
              }}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            />
            <motion.div
              style={{
                ...themeStyles.pipeBottom,
                x: p.x,
                top: p.gapTop + GAP_HEIGHT,
                height: GAME_HEIGHT - (p.gapTop + GAP_HEIGHT)
              }}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            />
          </React.Fragment>
        ))}

        {/* Player with cyan glow */}
        <motion.div style={{ ...themeStyles.playerWrap, rotate: tilt }}>
          <IconEl 
            style={{ 
              width: PLAYER_SIZE, 
              height: PLAYER_SIZE,
              filter: 'drop-shadow(0 0 15px rgba(0, 255, 255, 0.8))'
            }} 
          />
        </motion.div>

        {/* HUD */}
        <div style={themeStyles.hud}>
          {running ? (
            <div style={themeStyles.scorePill}>
              <span>Score:</span>
              <span style={{ fontVariantNumeric: 'tabular-nums' }}>{score}</span>
            </div>
          ) : (
            <div style={themeStyles.gameOverPill}>Game Over • click/tap to restart</div>
          )}
        </div>
      </div>

      <div style={themeStyles.controls}>
        <kbd style={themeStyles.kbd}>Space</kbd>
        <span>or click/tap to flap</span>
      </div>
    </div>
  );
};

const SecretPage = ({ isOpen, onClose }) => {
  const [highScore, setHighScore] = useState(() => {
    const saved = localStorage.getItem('flappyNotebookHighScore');
    return saved ? parseInt(saved, 10) : 0;
  });

  const handleScore = (score) => {
    if (score > highScore) {
      setHighScore(score);
      localStorage.setItem('flappyNotebookHighScore', score.toString());
    }
  };

  const handleGameOver = (finalScore) => {
    console.log(`Game Over! Final Score: ${finalScore}`);
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          className="fixed inset-0 z-50 flex items-start justify-center pt-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          style={{
            // Remove custom background to let global CSS star background show through
            background: 'transparent'
          }}
        >
          {/* Twinkling Accent Elements */}
          <div className="absolute inset-0 pointer-events-none overflow-hidden">
            {/* Bright colored twinkling particles */}
            {[...Array(25)].map((_, i) => (
              <motion.div
                key={i}
                className="absolute rounded-full"
                style={{
                  width: Math.random() * 4 + 2,
                  height: Math.random() * 4 + 2,
                  left: `${Math.random() * 100}%`,
                  top: `${Math.random() * 100}%`,
                  background: [
                    '#ff00ff', // Magenta
                    '#00ffff', // Cyan  
                    '#ffff00', // Yellow
                    '#ff0080', // Hot Pink
                    '#80ff00', // Lime
                    '#0080ff', // Blue
                  ][Math.floor(Math.random() * 6)]
                }}
                animate={{
                  opacity: [0.3, 1, 0.3],
                  scale: [0.8, 1.5, 0.8],
                  boxShadow: [
                    '0 0 5px currentColor',
                    '0 0 20px currentColor',
                    '0 0 5px currentColor'
                  ]
                }}
                transition={{
                  duration: Math.random() * 2 + 1,
                  repeat: Infinity,
                  delay: Math.random() * 2,
                  ease: "easeInOut"
                }}
              />
            ))}

            {/* Floating neon orbs */}
            {[...Array(8)].map((_, i) => (
              <motion.div
                key={`orb-${i}`}
                className="absolute rounded-full"
                style={{
                  width: 8,
                  height: 8,
                  left: `${Math.random() * 100}%`,
                  top: `${Math.random() * 100}%`,
                  background: i % 2 === 0 ? '#00ffff' : '#ff00ff',
                  boxShadow: `0 0 15px ${i % 2 === 0 ? '#00ffff' : '#ff00ff'}`
                }}
                animate={{
                  y: [0, -30, 0],
                  x: [0, Math.random() * 40 - 20, 0],
                  opacity: [0.4, 0.8, 0.4],
                }}
                transition={{
                  duration: Math.random() * 4 + 3,
                  repeat: Infinity,
                  delay: Math.random() * 2,
                  ease: "easeInOut"
                }}
              />
            ))}
          </div>

          {/* Main Game Container - Made Wider */}
          <motion.div
            className="relative flex flex-col items-center justify-center p-12" // Increased padding
            initial={{ scale: 0.8, y: -50 }}
            animate={{ scale: 1, y: 0 }}
            exit={{ scale: 0.8, y: -50 }}
            style={{
              background: 'rgba(0, 0, 0, 0.3)', // Transparent black
              border: '2px solid rgba(255, 255, 255, 0.8)', // White border
              borderRadius: '12px',
              backdropFilter: 'blur(4px)',
              boxShadow: '0 0 30px rgba(255, 255, 255, 0.3), inset 0 0 20px rgba(255, 255, 255, 0.1)', // White glow
              minWidth: '600px', // Make container wider
              maxWidth: '90vw' // Responsive max width
            }}
          >
            {/* Close Button */}
            <button
              onClick={onClose}
              className="absolute top-4 right-4 z-10 p-2 rounded-lg transition-all duration-200 hover:scale-105"
              style={{
                background: 'rgba(0, 0, 0, 0.5)',
                border: '1px solid rgba(255, 255, 255, 0.6)',
                color: 'rgba(255, 255, 255, 0.9)',
                boxShadow: '0 0 10px rgba(255, 255, 255, 0.2)'
              }}
            >
              <X size={24} />
            </button>

            {/* Header */}
            <div className="text-center mb-8"> {/* Increased margin */}
              <motion.h1
                className="text-5xl font-bold font-mono mb-4" // Increased size and margin
                style={{
                  background: 'linear-gradient(45deg, #00ffff, #ff00ff, #00ffff)',
                  backgroundSize: '200% 200%',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text',
                  textShadow: '0 0 20px rgba(0, 255, 255, 0.5)',
                  filter: 'drop-shadow(0 0 10px rgba(255, 0, 255, 0.3))'
                }}
                animate={{
                  backgroundPosition: ['0% 50%', '100% 50%', '0% 50%']
                }}
                transition={{ duration: 3, repeat: Infinity, ease: "linear" }}
              >
                🎮 SECRET ARCADE 🎮
              </motion.h1>

              {/* High Score Display */}
              <div 
                className="inline-flex items-center gap-3 px-6 py-3 rounded-lg font-mono text-base" // Increased size
                style={{
                  background: 'rgba(0, 0, 0, 0.4)',
                  border: '1px solid rgba(255, 255, 255, 0.6)',
                  color: 'rgba(255, 255, 255, 0.9)',
                  boxShadow: '0 0 15px rgba(255, 255, 255, 0.2)'
                }}
              >
                <span>You found the hidden game! High Score:</span>
                <motion.span 
                  className="font-bold text-xl" // Larger high score text
                  style={{
                    color: '#00ffff',
                    textShadow: '0 0 10px rgba(0, 255, 255, 0.8)'
                  }}
                  animate={{
                    textShadow: [
                      '0 0 10px rgba(0, 255, 255, 0.8)',
                      '0 0 20px rgba(0, 255, 255, 1)',
                      '0 0 10px rgba(0, 255, 255, 0.8)'
                    ]
                  }}
                  transition={{ duration: 2, repeat: Infinity }}
                >
                  {highScore}
                </motion.span>
              </div>
            </div>

            {/* Game Component */}
            <FlappyNotebook
              icon="book"
              theme="neon"
              onScore={handleScore}
              onGameOver={handleGameOver}
            />

            {/* Enhanced Corner Accents */}
            <div className="absolute top-0 left-0 w-full h-full pointer-events-none overflow-hidden rounded-lg">
              {/* Animated corner accents */}
              <motion.div 
                className="absolute top-3 left-3 w-6 h-6 border-l-2 border-t-2 rounded-tl-lg"
                style={{
                  borderColor: '#00ffff',
                }}
                animate={{
                  boxShadow: [
                    '0 0 10px rgba(0, 255, 255, 0.5)',
                    '0 0 20px rgba(0, 255, 255, 0.8)',
                    '0 0 10px rgba(0, 255, 255, 0.5)'
                  ]
                }}
                transition={{ duration: 2, repeat: Infinity }}
              />
              <motion.div 
                className="absolute top-3 right-3 w-6 h-6 border-r-2 border-t-2 rounded-tr-lg"
                style={{
                  borderColor: '#ff00ff',
                }}
                animate={{
                  boxShadow: [
                    '0 0 10px rgba(255, 0, 255, 0.5)',
                    '0 0 20px rgba(255, 0, 255, 0.8)',
                    '0 0 10px rgba(255, 0, 255, 0.5)'
                  ]
                }}
                transition={{ duration: 2, repeat: Infinity, delay: 0.5 }}
              />
              <motion.div 
                className="absolute bottom-3 left-3 w-6 h-6 border-l-2 border-b-2 rounded-bl-lg"
                style={{
                  borderColor: '#ff00ff',
                }}
                animate={{
                  boxShadow: [
                    '0 0 10px rgba(255, 0, 255, 0.5)',
                    '0 0 20px rgba(255, 0, 255, 0.8)',
                    '0 0 10px rgba(255, 0, 255, 0.5)'
                  ]
                }}
                transition={{ duration: 2, repeat: Infinity, delay: 1 }}
              />
              <motion.div 
                className="absolute bottom-3 right-3 w-6 h-6 border-r-2 border-b-2 rounded-br-lg"
                style={{
                  borderColor: '#00ffff',
                }}
                animate={{
                  boxShadow: [
                    '0 0 10px rgba(0, 255, 255, 0.5)',
                    '0 0 20px rgba(0, 255, 255, 0.8)',
                    '0 0 10px rgba(0, 255, 255, 0.5)'
                  ]
                }}
                transition={{ duration: 2, repeat: Infinity, delay: 1.5 }}
              />
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default SecretPage;
