import React from 'react';
import { motion } from 'framer-motion';
import HeroCard from '../profile/HeroCard';
import { usePlayer } from '../../hooks/usePlayer';
import useNotes from '../../hooks/useNotes';
import useTasks from '../../hooks/useTasks';
import { useTheme } from '../../contexts/ThemeContext';

const ProfileTab = ({ username = 'user', tabColor = '#A78BFA' }) => {
  const { player } = usePlayer();
  const { notes } = useNotes();
  const { tasks, taskLists } = useTasks();
  const { currentTheme, getThemeColors } = useTheme();
  const themeColors = getThemeColors();

  return (
    <div className="p-6">
      {/* Enhanced Header - Using Theme System */}
      <motion.div 
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="mb-8"
      >
        <div className="flex items-center gap-3 mb-3">
          <div 
            className="w-6 h-6 border border-gray-600" 
            style={{ backgroundColor: themeColors.secondary }}
          />
          <h1 className="font-mono text-3xl font-bold text-white">
            PLAYER PROFILE
          </h1>
        </div>

        <div className="space-y-2">
          <p className="text-gray-400 font-mono text-sm">
            Your player rank, mission and log statistics.
          </p>
        </div>
      </motion.div>

      {/* Enhanced Hero Card with Theme System */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        className="p-6 relative overflow-hidden"
        style={{
          backgroundColor: themeColors.backgroundColor,
          border: `2px solid ${themeColors.borderColor}`,
          borderRadius: themeColors.borderRadius,
          boxShadow: currentTheme === 'default' 
            ? '0 0 15px rgba(255, 255, 255, 0.2), 4px 4px 0px 0px rgba(0,0,0,1)' 
            : `0 0 3px ${themeColors.primary}30, 4px 4px 0px 0px rgba(0,0,0,1)`
        }}
      >
        {/* Gradient overlay with theme system */}
        <div className="absolute inset-0 pointer-events-none"
             style={{
               background: currentTheme === 'default' 
                 ? 'linear-gradient(135deg, rgba(255,255,255,0.05), rgba(6,182,212,0.1))'
                 : `linear-gradient(to bottom right, ${themeColors.secondary}15, ${themeColors.secondary}20)`,
               borderRadius: themeColors.borderRadius
             }} />

        <div className="relative z-10">
          <HeroCard 
            player={player} 
            notes={notes} 
            tasks={tasks} 
            taskLists={taskLists} 
          />
        </div>
      </motion.div>
    </div>
  );
};

export default ProfileTab;
