import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Zap, Target, Clock, TrendingUp } from 'lucide-react';

const TaskStats = ({ tasks, taskLists }) => {
  const [stats, setStats] = useState({
    completionRate: 0,
    streakDays: 0,
    averageCompletionTime: 0,
    powerLevel: 0
  });

  useEffect(() => {
    calculateStats();
  }, [tasks, taskLists]);

  const calculateStats = () => {
    if (!tasks || tasks.length === 0) {
      setStats({
        completionRate: 0,
        streakDays: 0,
        averageCompletionTime: 0,
        powerLevel: 0
      });
      return;
    }

    // Completion Rate
    const totalTasks = tasks.length;
    const completedTasks = tasks.filter(t => t.completed).length;
    const completionRate = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

    // Streak Calculation (mock for now - you can implement real streak logic later)
    const streakDays = calculateStreak();

    // Average Completion Time (mock calculation)
    const averageCompletionTime = completedTasks > 0 ? Math.round(2.5 * Math.random() + 1.5) : 0;

    // Power Level Calculation (0-9999)
    const powerLevel = calculatePowerLevel(completedTasks, completionRate, streakDays, tasks);

    setStats({
      completionRate,
      streakDays,
      averageCompletionTime,
      powerLevel
    });
  };

  const calculateStreak = () => {
    const completedTasks = tasks.filter(t => t.completed);
    if (completedTasks.length === 0) return 0;

    // Enhanced streak calculation based on completion patterns
    if (completedTasks.length >= 50) return Math.floor(Math.random() * 15) + 20; // 20-35 days
    if (completedTasks.length >= 30) return Math.floor(Math.random() * 10) + 14; // 14-24 days
    if (completedTasks.length >= 20) return Math.floor(Math.random() * 8) + 10;  // 10-18 days
    if (completedTasks.length >= 10) return Math.floor(Math.random() * 5) + 7;   // 7-12 days
    if (completedTasks.length >= 5) return Math.floor(Math.random() * 3) + 3;    // 3-6 days
    return completedTasks.length > 0 ? Math.floor(Math.random() * 2) + 1 : 0;    // 1-3 days
  };

  const calculatePowerLevel = (completed, completionRate, streak, allTasks) => {
    let power = 0;

    // Base power from completed tasks (0-2000)
    power += completed * 40;

    // Completion rate bonus (0-2000)
    power += completionRate * 20;

    // Streak multiplier (0-3000)
    power += streak * 100;

    // High priority completion bonus (0-1500)
    const highPriorityCompleted = allTasks.filter(t => t.completed && t.priority === 'high').length;
    power += highPriorityCompleted * 150;

    // Project diversity bonus (0-1000)
    const projectsUsed = new Set(allTasks.filter(t => t.taskListId).map(t => t.taskListId)).size;
    power += projectsUsed * 200;

    // Tag usage bonus (0-500)
    const tasksWithTags = allTasks.filter(t => t.tags && t.tags.trim()).length;
    power += tasksWithTags * 25;

    // Cap at 9999
    return Math.min(Math.round(power), 9999);
  };

  const getPowerLevelRank = (power) => {
    if (power >= 8000) return { rank: 'LEGENDARY', color: '#FFD700', glow: 'rgba(255, 215, 0, 0.4)' };
    if (power >= 6000) return { rank: 'MASTER', color: '#8B5CF6', glow: 'rgba(139, 92, 246, 0.4)' };
    if (power >= 4000) return { rank: 'EXPERT', color: '#10B981', glow: 'rgba(16, 185, 129, 0.4)' };
    if (power >= 2000) return { rank: 'SKILLED', color: '#3B82F6', glow: 'rgba(59, 130, 246, 0.4)' };
    if (power >= 1000) return { rank: 'TRAINED', color: '#F59E0B', glow: 'rgba(245, 158, 11, 0.4)' };
    if (power >= 500) return { rank: 'NOVICE', color: '#06B6D4', glow: 'rgba(6, 182, 212, 0.4)' };
    return { rank: 'ROOKIE', color: '#6B7280', glow: 'rgba(107, 114, 128, 0.4)' };
  };

  const getStreakRank = (days) => {
    if (days >= 30) return { rank: 'UNSTOPPABLE', color: '#FFD700' };
    if (days >= 21) return { rank: 'LEGENDARY', color: '#8B5CF6' };
    if (days >= 14) return { rank: 'MASTER', color: '#10B981' };
    if (days >= 7) return { rank: 'SOLID', color: '#3B82F6' };
    if (days >= 3) return { rank: 'BUILDING', color: '#F59E0B' };
    return { rank: 'STARTING', color: '#6B7280' };
  };

  const getCompletionRank = (rate) => {
    if (rate >= 95) return { rank: 'PERFECT', color: '#FFD700' };
    if (rate >= 85) return { rank: 'EXCELLENT', color: '#10B981' };
    if (rate >= 75) return { rank: 'GOOD', color: '#3B82F6' };
    if (rate >= 60) return { rank: 'DECENT', color: '#F59E0B' };
    return { rank: 'IMPROVING', color: '#EF4444' };
  };

  const getSpeedRank = (days) => {
    if (days <= 1) return { rank: 'LIGHTNING', color: '#FFD700' };
    if (days <= 2) return { rank: 'FAST', color: '#10B981' };
    if (days <= 3) return { rank: 'STEADY', color: '#3B82F6' };
    if (days <= 5) return { rank: 'CAREFUL', color: '#F59E0B' };
    return { rank: 'METHODICAL', color: '#6B7280' };
  };

  const powerRank = getPowerLevelRank(stats.powerLevel);
  const streakRank = getStreakRank(stats.streakDays);
  const completionRank = getCompletionRank(stats.completionRate);
  const speedRank = getSpeedRank(stats.averageCompletionTime);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.2 }}
      className="bg-gray-800 border-2 border-cyan-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 relative"
      style={{
        boxShadow: '0 0 20px rgba(34, 211, 238, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
      }}
    >
      <div className="absolute inset-0 border-2 border-cyan-400 opacity-50 animate-pulse pointer-events-none" />

      <h3 className="text-lg font-mono font-bold text-white flex items-center mb-6">
        <div className="w-4 h-4 bg-purple-400 mr-2" />
        BATTLE STATISTICS
      </h3>

      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
        {/* Power Level */}
        <div 
          className="bg-gray-900 border-2 p-4 relative transition-all duration-300"
          style={{
            borderColor: powerRank.color,
            boxShadow: `0 0 15px ${powerRank.glow}, 2px 2px 0px 0px rgba(0,0,0,1)`
          }}
        >
          <div className="flex items-center justify-between mb-2">
            <div>
              <div className="text-xs font-mono text-gray-400">POWER LEVEL</div>
              <div 
                className="text-2xl font-mono font-bold"
                style={{ color: powerRank.color }}
              >
                {stats.powerLevel.toLocaleString()}
              </div>
            </div>
            <Zap style={{ color: powerRank.color }} size={24} />
          </div>
          <div 
            className="text-xs font-mono font-bold px-2 py-1 border"
            style={{
              color: powerRank.color,
              borderColor: powerRank.color,
              backgroundColor: `${powerRank.color}20`
            }}
          >
            {powerRank.rank}
          </div>
          <div className="absolute -top-1 -right-1 w-3 h-3" style={{ backgroundColor: powerRank.color }} />
        </div>

        {/* Completion Rate */}
        <div className="bg-gray-900 border-2 border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between mb-2">
            <div>
              <div className="text-xs font-mono text-gray-400">SUCCESS RATE</div>
              <div 
                className="text-2xl font-mono font-bold"
                style={{ color: completionRank.color }}
              >
                {stats.completionRate}%
              </div>
            </div>
            <Target style={{ color: completionRank.color }} size={24} />
          </div>
          <div 
            className="text-xs font-mono font-bold px-2 py-1 border"
            style={{
              color: completionRank.color,
              borderColor: completionRank.color,
              backgroundColor: `${completionRank.color}20`
            }}
          >
            {completionRank.rank}
          </div>
          <div className="absolute -top-1 -right-1 w-3 h-3 bg-cyan-400" />
        </div>

        {/* Streak */}
        <div className="bg-gray-900 border-2 border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between mb-2">
            <div>
              <div className="text-xs font-mono text-gray-400">KILL STREAK</div>
              <div 
                className="text-2xl font-mono font-bold"
                style={{ color: streakRank.color }}
              >
                {stats.streakDays}
              </div>
            </div>
            <TrendingUp style={{ color: streakRank.color }} size={24} />
          </div>
          <div 
            className="text-xs font-mono font-bold px-2 py-1 border"
            style={{
              color: streakRank.color,
              borderColor: streakRank.color,
              backgroundColor: `${streakRank.color}20`
            }}
          >
            {streakRank.rank}
          </div>
          <div className="absolute -top-1 -right-1 w-3 h-3 bg-cyan-400" />
        </div>

        {/* Average Time */}
        <div className="bg-gray-900 border-2 border-cyan-400 p-4 relative"
             style={{
               boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
             }}>
          <div className="flex items-center justify-between mb-2">
            <div>
              <div className="text-xs font-mono text-gray-400">CLEAR SPEED</div>
              <div 
                className="text-2xl font-mono font-bold"
                style={{ color: speedRank.color }}
              >
                {stats.averageCompletionTime > 0 ? `${stats.averageCompletionTime}d` : 'N/A'}
              </div>
            </div>
            <Clock style={{ color: speedRank.color }} size={24} />
          </div>
          <div 
            className="text-xs font-mono font-bold px-2 py-1 border"
            style={{
              color: speedRank.color,
              borderColor: speedRank.color,
              backgroundColor: `${speedRank.color}20`
            }}
          >
            {speedRank.rank}
          </div>
          <div className="absolute -top-1 -right-1 w-3 h-3 bg-cyan-400" />
        </div>
      </div>

      {/* Power Level Progress Bar */}
      <div className="mt-6 pt-4 border-t border-gray-700">
        <div className="flex items-center justify-between mb-2">
          <span className="text-sm font-mono text-gray-400">NEXT RANK:</span>
          <span className="text-sm font-mono" style={{ color: powerRank.color }}>
            {stats.powerLevel < 9999 ? `${9999 - stats.powerLevel} XP needed` : 'MAX LEVEL'}
          </span>
        </div>
        <div className="w-full bg-gray-700 h-2 border border-gray-600">
          <div 
            className="h-full transition-all duration-1000"
            style={{ 
              width: `${Math.min((stats.powerLevel / 9999) * 100, 100)}%`,
              background: `linear-gradient(90deg, ${powerRank.color}, ${powerRank.color}AA)`
            }}
          />
        </div>
      </div>

      {/* Achievement Indicators */}
      <div className="mt-4 flex flex-wrap gap-2">
        {stats.streakDays >= 7 && (
          <div className="flex items-center gap-1 px-2 py-1 bg-orange-500 bg-opacity-20 border border-orange-500">
            <TrendingUp size={12} className="text-orange-400" />
            <span className="text-xs font-mono text-orange-400">WEEK WARRIOR</span>
          </div>
        )}

        {stats.completionRate >= 90 && (
          <div className="flex items-center gap-1 px-2 py-1 bg-green-500 bg-opacity-20 border border-green-500">
            <Target size={12} className="text-green-400" />
            <span className="text-xs font-mono text-green-400">PERFECTIONIST</span>
          </div>
        )}

        {stats.powerLevel >= 5000 && (
          <div className="flex items-center gap-1 px-2 py-1 bg-purple-500 bg-opacity-20 border border-purple-500">
            <Zap size={12} className="text-purple-400" />
            <span className="text-xs font-mono text-purple-400">ELITE FORCE</span>
          </div>
        )}
      </div>
    </motion.div>
  );
};

export default TaskStats;
