        type: "idle",
        lastActivity: "Completed workout session",
        duration: "45m",
        started: "1h ago",
        icon: Dumbbell,
        color: "#ef4444"
      },
      level: 15,
      lastSeen: "Online",
      recentAchievements: [
        { name: "Iron Will", icon: "ðŸ’ª", earnedAt: "1 hour ago" },
        { name: "Consistency King", icon: "ðŸ‘‘", earnedAt: "Today" }
