        totalXP: 2100,
        focusTime: "156h",
        streak: 12
      }
    },
    {
      id: 5,
      username: "MusicMaestro_Jake",
      avatar: "/Avatars/avatar5.png",
      status: "offline",
      activity: {
        type: "offline",
        lastActivity: "Music practice",
