import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Plus, Search, FileText } from 'lucide-react';
import PixelButton from '../PixelButton';
import PixelInput from '../PixelInput';
import NoteCard from '../notes/NoteCard';
import NoteModal from '../notes/NoteModal';
import useNotes from '../../hooks/useNotes';
import useFolders from '../../hooks/useFolders';
import useNotebooks from '../../hooks/useNotebooks';

const NotesTab = () => {
  const { notes, loading, error, deleteNote, createNote, updateNote } = useNotes();
  const { folders } = useFolders();
  const { notebooks } = useNotebooks();
  const [searchTerm, setSearchTerm] = useState('');
  const [isCreating, setIsCreating] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [activeNote, setActiveNote] = useState(null);
  const [isCreateNoteModalOpen, setIsCreateNoteModalOpen] = useState(false);

  // Filter notes based on search
  const filteredNotes = searchTerm
    ? notes.filter(note =>
        note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        note.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (note.tags && Array.isArray(note.tags) && note.tags.some(tag =>
          tag.toLowerCase().includes(searchTerm.toLowerCase())
        ))
      )
    : notes;

  const handleDeleteNote = async (note) => {
    if (window.confirm('Are you sure you want to delete this note?')) {
      try {
        await deleteNote(note.id);
      } catch (err) {
        console.error('Failed to delete note:', err);
      }
    }
  };

  const handleCreateNote = () => {
    setIsCreateNoteModalOpen(true);
  };

  const handleEditNote = (note) => {
    setActiveNote(note);
    setIsCreateNoteModalOpen(true);
  };

  const handleCreateNoteSubmit = async (noteData) => {
    try {
      console.log('NotesTab: Creating note:', noteData);
      if (activeNote) {
        await updateNote(activeNote.id, noteData);
      } else {
        await createNote(noteData);
      }
      setActiveNote(null);
      setIsCreateNoteModalOpen(false);
      console.log('Note saved successfully from NotesTab!');
    } catch (error) {
      console.error('Failed to save note:', error);
      alert('Failed to save note: ' + error.message);
    }
  };

  const handleSaveNote = async (noteData) => {
    try {
      console.log('NotesTab: Saving note data:', noteData);

      if (activeNote && activeNote.id) {
        console.log('NotesTab: Updating existing note:', activeNote.id);
        await updateNote(activeNote.id, noteData);
      } else {
        console.log('NotesTab: Creating new note');
        await createNote(noteData);
      }

      setIsEditing(false);
      setActiveNote(null);

      console.log('NotesTab: Note saved successfully');
    } catch (error) {
      console.error('NotesTab: Failed to save note:', error);
      alert('Failed to save note: ' + error.message);
    }
  };

  return (
    <div className="notes-tab-container p-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="font-mono text-3xl font-bold text-white mb-2 flex items-center gap-3">
          <div className="w-6 h-6 bg-purple-400 border border-gray-600" />
          NOTES ARCHIVE
        </h1>
        <p className="text-gray-400 font-mono text-sm">
          Access and manage your data entries collection
        </p>
      </div>

      {/* Compact Command Interface */}
      <div className="bg-gray-800 border-2 border-cyan-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-4 mb-6 relative"
           style={{
             boxShadow: '0 0 20px rgba(34, 211, 238, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
           }}>
        <div className="absolute inset-0 border-2 border-cyan-400 opacity-30 animate-pulse pointer-events-none" />

        {/* Terminal Header */}
        <div className="flex items-center mb-4">
          <span className="font-mono font-bold text-white text-2xl">NOTES INTERFACE</span>
        </div>

        {/* Compact Layout */}
        <div className="flex flex-col lg:flex-row gap-4 items-stretch lg:items-center justify-between">
          {/* Create Button - Single button */}
          <div className="flex-shrink-0">
            <button
              onClick={handleCreateNote}
              className="bg-gray-900 border-2 border-cyan-400 px-4 py-2 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] font-mono font-bold text-cyan-400"
              style={{
                boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
              }}
            >
              <div className="flex items-center justify-center gap-2">
                <Plus size={16} />
                <FileText size={18} />
                <span className="text-sm text-cyan-400">NEW ENTRY</span>
              </div>
              <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
            </button>
          </div>

          {/* Enhanced Search Terminal */}
          <div className="bg-gray-900 border border-cyan-400 p-4 relative flex-1 lg:max-w-md flex flex-col"
               style={{
                 boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
               }}>
            {/* Search Protocol Header */}
            <div className="flex items-center gap-2 mb-3">
              <div className="w-3 h-3 bg-cyan-400" />
              <span className="font-mono text-sm text-cyan-400 font-bold">SEARCH PROTOCOL</span>
            </div>

            {/* Search Input */}
            <div className="relative mb-3">
              <Search size={16} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-cyan-400" />
              <input
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search notes archive..."
                className="w-full pl-10 pr-3 py-2 bg-gray-800 border border-gray-600 text-white font-mono text-sm focus:border-cyan-400 focus:outline-none transition-colors"
                style={{ color: '#fff' }}
              />
            </div>

            {/* Search Stats */}
            <div className="text-xs font-mono text-gray-400">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <span>{notes.length} TOTAL ENTRIES</span>
                  <span>{filteredNotes.length} DISPLAYED</span>
                </div>
                <div className="text-cyan-400">
                  {searchTerm ? 'FILTERING...' : 'READY'}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-gray-800 border-2 border-red-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-4 mb-6"
          style={{
            boxShadow: '0 0 20px rgba(239, 68, 68, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
          }}
        >
          <div className="font-mono text-red-400">
            ERROR: {error}
          </div>
        </motion.div>
      )}

      {/* Content Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-gray-800 border-2 border-cyan-400 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 relative"
        style={{
          boxShadow: '0 0 20px rgba(34, 211, 238, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
        }}
      >
        <div className="absolute inset-0 border-2 border-cyan-400 opacity-50 animate-pulse pointer-events-none" />

        <h3 className="text-lg font-mono font-bold text-white flex items-center mb-2">
          DATA ENTRIES ({filteredNotes.length})
        </h3>
        <p className="text-sm font-mono text-gray-400 mb-4">
          {searchTerm 
            ? `Search results for "${searchTerm}"` 
            : "All your notes and data entries"
          }
        </p>

        {/* Notes Grid - LibraryTab Style */}
        {loading ? (
          <div className="text-center py-8 font-mono text-white">
            <motion.div
              animate={{ rotate: 360 }}
              transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
              className="inline-block mb-4"
            >
              <FileText size={32} className="text-cyan-400" />
            </motion.div>
            <div>Loading notes archive...</div>
          </div>
        ) : filteredNotes.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
            {filteredNotes.map(note => {
              const getTags = (tags) => {
                if (!tags) return [];
                if (Array.isArray(tags)) return tags;
                if (typeof tags === 'string') return tags.split(',').map(tag => tag.trim());
                return [];
              };

              const tagsArray = getTags(note.tags);
              const noteColor = note.color || note.colorCode || '#4ADE80';

              const hexToRgb = (hex) => {
                if (!hex || !hex.startsWith('#')) return '74, 222, 128';
                const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
                return result ? 
                  `${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}` :
                  '74, 222, 128';
              };

              const rgbColor = hexToRgb(noteColor);

              return (
                <motion.div
                  key={note.id || note.filename || note.title}
                  className="bg-gray-900 border-2 p-4 cursor-pointer group relative transition-all duration-300"
                  style={{
                    borderColor: noteColor,
                    boxShadow: `0 0 10px rgba(${rgbColor}, 0.4), 2px 2px 0px 0px rgba(0,0,0,1)`,
                  }}
                  whileHover={{ 
                    scale: 1.02, 
                    y: -2,
                    boxShadow: `0 0 20px rgba(${rgbColor}, 0.6), 2px 2px 0px 0px rgba(0,0,0,1)`
                  }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => handleEditNote(note)}
                >
                  <div className="flex items-start justify-between mb-3">
                    <FileText size={24} style={{ color: noteColor }} />
                    <div className="text-xs font-mono text-gray-400 bg-gray-700 px-2 py-1 border border-gray-600">
                      {tagsArray.length} TAGS
                    </div>
                  </div>
                  <h4 className="font-mono font-bold text-white mb-2 truncate" title={note.title}>
                    {note.title}
                  </h4>
                  <p className="text-xs text-gray-400 mb-3">
                    {note.content && note.content.length > 100 
                      ? `${note.content.substring(0, 100)}...` 
                      : note.content}
                  </p>

                  {/* Tags */}
                  {tagsArray.length > 0 && (
                    <div className="flex flex-wrap gap-1 mb-2">
                      {tagsArray.slice(0, 2).map((tag, index) => (
                        <span
                          key={index}
                          className="px-2 py-1 bg-gray-700 border border-gray-600 text-xs font-mono text-cyan-400"
                        >
                          {tag}
                        </span>
                      ))}
                      {tagsArray.length > 2 && (
                        <span className="text-xs text-gray-500 font-mono px-2 py-1">
                          +{tagsArray.length - 2}
                        </span>
                      )}
                    </div>
                  )}

                  {/* Edit button */}
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      handleEditNote(note);
                    }}
                    className="absolute bottom-2 right-2 p-1.5 bg-gray-700 hover:bg-gray-600 rounded text-cyan-400 opacity-0 group-hover:opacity-100 transition-opacity"
                    title="Modify entry"
                  >
                    <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2-2v-7" />
                      <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z" />
                    </svg>
                  </button>
                </motion.div>
              );
            })}
          </div>
        ) : (
          <div className="bg-gray-900 border border-gray-600 p-8 text-center">
            {searchTerm ? (
              <div>
                <FileText size={48} className="text-gray-500 mx-auto mb-3" />
                <h4 className="font-mono text-lg font-bold mb-2 text-white">No entries found</h4>
                <p className="text-gray-400 mb-4 font-mono">Try a different search term or create a new entry</p>
                <div className="flex gap-2 justify-center">
                  <button
                    onClick={() => setSearchTerm('')}
                    className="bg-gray-900 border-2 border-cyan-400 px-4 py-2 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] font-mono font-bold text-cyan-400"
                    style={{
                      boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
                    }}
                  >
                    <span>Clear Search</span>
                    <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
                  </button>
                  <button
                    onClick={handleCreateNote}
                    className="bg-gray-900 border-2 border-cyan-400 px-4 py-2 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] font-mono font-bold text-cyan-400"
                    style={{
                      boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)'
                    }}
                  >
                    <div className="flex items-center gap-2">
                      <Plus size={16} />
                      <span>Create Entry</span>
                    </div>
                    <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
                  </button>
                </div>
              </div>
            ) : (
              <div>
                <FileText size={48} className="text-gray-500 mx-auto mb-3" />
                <h4 className="font-mono text-lg font-bold mb-2 text-white">No entries detected</h4>
                <p className="text-gray-400 mb-4 font-mono">Initialize your first data entry to begin your digital archive</p>
                <button
                  onClick={handleCreateNote}
                  className="bg-gray-900 border-2 border-cyan-400 px-6 py-3 relative group cursor-pointer transition-all duration-300 hover:border-cyan-300 hover:shadow-[0_0_15px_rgba(34,211,238,0.3)] font-mono font-bold text-cyan-400"
                  style={{
                    boxShadow: '0 0 5px rgba(34, 211, 238, 0.2), 4px 4px 0px 0px rgba(0,0,0,1)'
                  }}
                >
                  <div className="flex items-center gap-2">
                    <Plus size={18} />
                    <span>CREATE FIRST ENTRY</span>
                  </div>
                  <div className="absolute inset-0 bg-cyan-400 opacity-0 group-hover:opacity-10 transition-opacity" />
                  {/* Corner accent */}
                  <div className="absolute -top-1 -right-1 w-2 h-2 bg-purple-400" />
                </button>
              </div>
            )}
          </div>
        )}
      </motion.div>

      {/* Modals */}
      <NoteModal
        isOpen={isCreateNoteModalOpen}
        onClose={() => {
          setIsCreateNoteModalOpen(false);
          setActiveNote(null);
        }}
        onSave={handleCreateNoteSubmit}  
        folders={folders}
        notebooks={notebooks}
        existingNote={activeNote}
      />
      <NoteModal
        isOpen={isEditing}
        onClose={() => {
          setIsEditing(false);
          setActiveNote(null);
        }}
        onSave={handleSaveNote}  
        folders={folders}
        notebooks={notebooks}
        existingNote={activeNote}
      />
    </div>
  );
};

export default NotesTab;
