import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Trophy, Star, Zap, Crown, Lock, CheckCircle, Target, BarChart3 } from 'lucide-react';
import { allAchievements, achievementsByTier, tierInfo } from '../../data/achievements';
import achievementService from '../../services/achievementService';
import AchievementBadge from '../ui/AchievementBadge';

const AchievementsTab = ({ username = 'Jroc_182', tabColor = '#F59E0B' }) => {
  const [selectedTier, setSelectedTier] = useState('all');
  const [achievementStats, setAchievementStats] = useState({});
  const [userStats, setUserStats] = useState({});
  const [selectedAchievement, setSelectedAchievement] = useState(null);

  useEffect(() => {
    loadAchievementData();
  }, []);

  const loadAchievementData = () => {
    const stats = achievementService.getStats();
    setAchievementStats(stats);

    // Load user stats for progress calculation
    const notes = JSON.parse(localStorage.getItem('notes') || '[]');
    const tasks = JSON.parse(localStorage.getItem('tasks') || '[]');
    const sessions = JSON.parse(localStorage.getItem('focusSessions') || '[]');

    const calculatedStats = {
      totalNotes: notes.length,
      totalWords: notes.reduce((sum, note) => sum + (note.content?.split(' ').length || 0), 0),
      uniqueTags: new Set(notes.flatMap(note => note.tags || [])).size,
      totalTasks: tasks.filter(t => t.completed).length,
      tasksCreated: tasks.length,
      totalSessions: sessions.length,
      totalFocusTime: sessions.reduce((sum, s) => sum + s.timeSpent, 0),
      level: Math.floor(stats.totalXP / 1000) + 1,
      totalXP: stats.totalXP
    };

    setUserStats(calculatedStats);
  };

  const unlockedAchievements = allAchievements.filter(a => achievementService.isUnlocked(a.id));
  const lockedAchievements = allAchievements.filter(a => !achievementService.isUnlocked(a.id));

  const getFilteredAchievements = (achievements) => {
    if (selectedTier === 'all') return achievements;
    return achievements.filter(a => a.tier === selectedTier);
  };

  const hexToRgb = (hex) => {
    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? 
      `${parseInt(result[1], 16)}, ${parseInt(result[2], 16)}, ${parseInt(result[3], 16)}` :
      '245, 158, 11';
  };

  const tabColorRgb = hexToRgb(tabColor);

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="mb-8"
      >
        <h1 className="font-mono text-3xl font-bold text-white mb-2 flex items-center gap-3">
          <div 
            className="w-6 h-6 border border-gray-600" 
            style={{ backgroundColor: tabColor }}
          />
          BADGE COLLECTION
        </h1>
        <p className="text-gray-400 font-mono text-sm">
          Showcase your achievements and track your progress through the digital realm.
        </p>
      </motion.div>

      {/* Achievement Statistics - Remove XP display here */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] relative mb-6"
        style={{
          borderColor: tabColor,
          boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="absolute inset-0 border-2 opacity-30 animate-pulse pointer-events-none" 
             style={{ borderColor: tabColor }} />

        <div className="relative z-10">
          <div className="border-b px-4 py-3"
               style={{ 
                 borderColor: tabColor,
                 backgroundColor: '#1A0E26'
               }}>
            <h3 className="text-lg font-mono font-bold text-white flex items-center">
              <BarChart3 className="mr-2" size={20} style={{ color: tabColor }} />
              COLLECTION PROGRESS
            </h3>
          </div>

          <div className="p-6">
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-6">
              <div className="text-center">
                <div className="text-2xl font-mono font-bold text-green-400">
                  {achievementStats.unlocked || 0}
                </div>
                <div className="text-xs text-gray-400">BADGES EARNED</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-mono font-bold text-blue-400">
                  {achievementStats.percentage || 0}%
                </div>
                <div className="text-xs text-gray-400">COMPLETION</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-mono font-bold text-yellow-400">
                  {userStats.level || 1}
                </div>
                <div className="text-xs text-gray-400">PLAYER LEVEL</div>
              </div>
            </div>

            {/* Tier Breakdown */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {Object.entries(tierInfo).map(([tier, info]) => {
                const tierCount = achievementStats.byTier?.[tier] || 0;
                const tierTotal = achievementsByTier[tier]?.length || 0;

                return (
                  <div 
                    key={tier}
                    className="bg-gray-900 border border-gray-600 p-3 text-center cursor-pointer transition-all hover:border-opacity-80"
                    style={{
                      borderColor: selectedTier === tier ? info.color : '#4B5563',
                      backgroundColor: selectedTier === tier ? `${info.color}10` : 'transparent'
                    }}
                    onClick={() => setSelectedTier(selectedTier === tier ? 'all' : tier)}
                  >
                    <div style={{ color: info.color }} className="text-2xl mb-2">
                      {info.emoji}
                    </div>
                    <div className="text-sm font-mono font-bold text-white">
                      {tierCount}/{tierTotal}
                    </div>
                    <div className="text-xs text-gray-400">
                      {info.name}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </motion.div>

      {/* My Achievement Badges Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] relative"
        style={{
          borderColor: tabColor,
          boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="absolute inset-0 border-2 opacity-30 animate-pulse pointer-events-none" 
             style={{ borderColor: tabColor }} />

        <div className="relative z-10">
          <div className="border-b px-4 py-3"
               style={{ 
                 borderColor: tabColor,
                 backgroundColor: '#1A0E26'
               }}>
            <h3 className="text-lg font-mono font-bold text-white flex items-center">
              <Trophy className="mr-2" size={20} style={{ color: tabColor }} />
              MY ACHIEVEMENT BADGES ({unlockedAchievements.length})
            </h3>
          </div>

          <div className="p-6">
            {getFilteredAchievements(unlockedAchievements).length > 0 ? (
              <div className="grid grid-cols-4 md:grid-cols-6 lg:grid-cols-8 gap-4">
                {getFilteredAchievements(unlockedAchievements).map((achievement) => (
                  <div key={achievement.id} className="flex flex-col items-center gap-2">
                    <AchievementBadge 
                      achievement={achievement}
                      size="medium"
                      onClick={() => setSelectedAchievement(achievement)}
                    />
                    <div className="text-xs font-mono text-center text-gray-300 max-w-[80px] truncate">
                      {achievement.name}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-8">
                <Trophy size={48} className="text-gray-600 mx-auto mb-3" />
                <div className="text-gray-400 font-mono text-lg font-bold mb-2">
                  NO BADGES EARNED YET
                </div>
                <div className="text-gray-500 font-mono text-sm">
                  Complete activities to earn your first achievement badges!
                </div>
              </div>
            )}
          </div>
        </div>
      </motion.div>

      {/* Locked Achievements Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2 }}
        className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] relative"
        style={{
          borderColor: tabColor,
          boxShadow: `0 0 20px rgba(${tabColorRgb}, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)`
        }}
      >
        <div className="absolute inset-0 border-2 opacity-30 animate-pulse pointer-events-none" 
             style={{ borderColor: tabColor }} />

        <div className="relative z-10">
          <div className="border-b px-4 py-3"
               style={{ 
                 borderColor: tabColor,
                 backgroundColor: '#1A0E26'
               }}>
            <h3 className="text-lg font-mono font-bold text-white flex items-center">
              <Lock className="mr-2" size={20} style={{ color: tabColor }} />
              LOCKED ACHIEVEMENTS ({lockedAchievements.length})
            </h3>
          </div>

          <div className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {getFilteredAchievements(lockedAchievements).map((achievement) => {
                const progress = achievementService.getAchievementProgress(achievement.id, userStats);
                const tier = tierInfo[achievement.tier];

                return (
                  <div
                    key={achievement.id}
                    className="bg-gray-900 border border-gray-600 p-4 relative transition-all duration-300 hover:border-gray-500 cursor-pointer"
                    onClick={() => setSelectedAchievement(achievement)}
                  >
                    <div className="flex items-center gap-3 mb-3">
                      <AchievementBadge 
                        achievement={achievement}
                        size="small"
                        showGlow={false}
                      />

                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <span className="text-xs font-mono" style={{ color: tier.color }}>
                            {tier.emoji} {tier.name.toUpperCase()}
                          </span>
                        </div>
                        <div className="font-mono font-bold text-sm text-gray-300">
                          {achievement.name}
                        </div>
                      </div>
                    </div>

                    <div className="text-xs font-mono mb-3 text-gray-400">
                      {achievement.description}
                    </div>

                    {/* Progress Bar */}
                    {progress > 0 && (
                      <div className="mb-3">
                        <div className="flex justify-between items-center mb-1">
                          <span className="text-xs font-mono text-gray-500">Progress</span>
                          <span className="text-xs font-mono text-gray-500">
                            {Math.round(progress * 100)}%
                          </span>
                        </div>
                        <div className="w-full bg-gray-700 h-2 border border-gray-600">
                          <div 
                            className="h-full transition-all duration-500"
                            style={{ 
                              width: `${progress * 100}%`,
                              backgroundColor: tier.color,
                              opacity: 0.7
                            }}
                          />
                        </div>
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </motion.div>

      {/* Achievement Detail Modal */}
      {selectedAchievement && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
          onClick={() => setSelectedAchievement(null)}
        >
          <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.8, opacity: 0 }}
            className="bg-gray-800 border-2 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 max-w-md mx-4 relative"
            style={{
              borderColor: tierInfo[selectedAchievement.tier].color,
              boxShadow: `0 0 30px ${tierInfo[selectedAchievement.tier].color}40, 8px 8px 0px 0px rgba(0,0,0,1)`
            }}
            onClick={(e) => e.stopPropagation()}
          >
            <div className="text-center">
              <AchievementBadge 
                achievement={selectedAchievement}
                size="large"
                showGlow={achievementService.isUnlocked(selectedAchievement.id)}
              />

              <h3 className="font-mono font-bold text-xl text-white mt-4 mb-2">
                {selectedAchievement.name}
              </h3>

              <p className="text-gray-400 font-mono text-sm mb-4">
                {selectedAchievement.description}
              </p>

              <div className="flex justify-center gap-4 text-xs font-mono">
                <div className="text-center">
                  <div style={{ color: tierInfo[selectedAchievement.tier].color }}>
                    {tierInfo[selectedAchievement.tier].emoji}
                  </div>
                  <div className="text-gray-400">
                    {tierInfo[selectedAchievement.tier].name}
                  </div>
                </div>
              </div>

              {achievementService.isUnlocked(selectedAchievement.id) && (
                <div className="mt-4 text-green-400 font-mono text-sm">
                  ✓ BADGE EARNED
                </div>
              )}
            </div>

            <button
              onClick={() => setSelectedAchievement(null)}
              className="absolute top-2 right-2 text-gray-400 hover:text-white"
            >
              ✕
            </button>
          </motion.div>
        </motion.div>
      )}
    </div>
  );
};

export default AchievementsTab;
