import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { BookOpen, Briefcase, Palette, Code, Target, Calendar, Heart, Search, PenTool, Plus, Star } from 'lucide-react';
import { useTheme } from '../../contexts/ThemeContext';

const SkillTreeWidget = () => {
  const [hoveredSkill, setHoveredSkill] = useState(null);
  const { currentTheme, getThemeColors } = useTheme();
  const themeColors = getThemeColors();

  // Get categories from localStorage
  const getCategories = () => {
    try {
      return JSON.parse(localStorage.getItem('focusCategories') || '[]');
    } catch (error) {
      return [];
    }
  };

  // XP calculation for levels (same as HexagonalSkillTree)
  const calculateXPRequired = (level) => {
    const base = 60; // 1 hour for level 1
    return Math.floor(base * Math.pow(1.4, level - 1));
  };

  // Get current level from total XP
  const getCurrentLevel = (totalXP) => {
    let level = 1;
    let xpNeeded = 0;

    while (level <= 10) {
      xpNeeded += calculateXPRequired(level);
      if (totalXP < xpNeeded) break;
      level++;
    }

    return Math.min(level, 10);
  };

  // Get XP progress for current level
  const getLevelProgress = (totalXP) => {
    const currentLevel = getCurrentLevel(totalXP);
    if (currentLevel === 10) return { current: 100, max: 100, percentage: 100 };

    let xpUsed = 0;
    for (let i = 1; i < currentLevel; i++) {
      xpUsed += calculateXPRequired(i);
    }

    const currentLevelXP = totalXP - xpUsed;
    const maxLevelXP = calculateXPRequired(currentLevel);
    const percentage = Math.floor((currentLevelXP / maxLevelXP) * 100);

    return { current: currentLevelXP, max: maxLevelXP, percentage };
  };

  const getIconComponent = (iconName) => {
    const iconMap = {
      'BookOpen': BookOpen,
      'Briefcase': Briefcase,
      'Palette': Palette,
      'PenTool': PenTool,
      'Code': Code,
      'Target': Target,
      'Calendar': Calendar,
      'Heart': Heart,
      'Search': Search,
      'Plus': Plus
    };
    return iconMap[iconName] || Plus;
  };

  // Get branch color
  const getBranchColor = (categoryName, customColor = null) => {
    if (customColor) return customColor;

    const colorMap = {
      'scholar': '#FF1493',
      'profession': '#00FFFF',
      'artisan': '#8A2BE2',
      'scribe': '#FF6347',
      'programming': '#00FF7F',
      'literacy': '#FFD700',
      'strategist': '#FF4500',
      'mindfulness': '#40E0D0',
      'knowledge': '#DA70D6'
    };

    return colorMap[categoryName.toLowerCase()] || '#6B7280';
  };

  const categories = getCategories().filter(cat => (cat.xp || 0) >= 0);
  const sortedCategories = categories.sort((a, b) => {
    const order = ['scholar', 'profession', 'artisan', 'scribe', 'programming', 'literacy', 'strategist', 'mindfulness', 'knowledge'];
    const aIndex = order.indexOf(a.name.toLowerCase());
    const bIndex = order.indexOf(b.name.toLowerCase());
    return (aIndex === -1 ? 999 : aIndex) - (bIndex === -1 ? 999 : bIndex);
  });

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="p-6 relative overflow-hidden"
      style={{
        backgroundColor: themeColors.backgroundColor,
        border: `2px solid ${themeColors.borderColor}`,
        borderRadius: themeColors.borderRadius,
        boxShadow: currentTheme === 'default' 
          ? `0 0 15px rgba(255, 255, 255, 0.2), 4px 4px 0px 0px rgba(0,0,0,1)` 
          : `0 0 3px ${themeColors.primary}50, 1px 1px 0px 0px rgba(0,0,0,1)`
      }}
    >
      {/* Gradient overlay */}
      <div className="absolute inset-0 pointer-events-none"
           style={{
             background: currentTheme === 'default' 
               ? 'linear-gradient(135deg, rgba(255,255,255,0.05), rgba(6,182,212,0.1))'
               : `linear-gradient(to bottom right, ${themeColors.secondary}15, ${themeColors.secondary}20)`,
             borderRadius: themeColors.borderRadius
           }} />

      <div className="relative z-10">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-2xl font-mono font-bold text-white flex items-center">
            <Star 
              size={20} 
              className="mr-2" 
              style={{ color: themeColors.secondary }}
            />
            SKILL TREE
          </h3>
          <div className="text-xs font-mono text-gray-400">
            {sortedCategories.length} ACTIVE BRANCHES
          </div>
        </div>

        {/* Skills List */}
        <div className="space-y-4">
          {sortedCategories.length === 0 ? (
            <div className="text-center py-6">
              <div className="text-gray-500 font-mono text-sm">
                Start focusing to unlock skill branches!
              </div>
            </div>
          ) : (
            sortedCategories.map((category, index) => {
              const IconComponent = getIconComponent(category.iconName);
              const currentLevel = getCurrentLevel(category.xp || 0);
              const progress = getLevelProgress(category.xp || 0);
              const branchColor = getBranchColor(category.name, category.customColor);
              const isMaxLevel = currentLevel === 10;

              return (
                <motion.div
                  key={category.id}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: index * 0.1 }}
                  className="relative"
                  onMouseEnter={() => setHoveredSkill(category.id)}
                  onMouseLeave={() => setHoveredSkill(null)}
                >
                  {/* Skill Row */}
                  <div className="flex items-center gap-3 mb-2">
                    {/* Icon */}
                    <div 
                      className="w-8 h-8 border border-gray-600 rounded flex items-center justify-center bg-black"
                      style={{ 
                        borderColor: branchColor,
                        boxShadow: hoveredSkill === category.id ? `0 0 8px ${branchColor}60` : undefined
                      }}
                    >
                      <IconComponent size={14} style={{ color: branchColor }} />
                    </div>

                    {/* Skill Name */}
                    <div className="flex-1 min-w-0">
                      <div className="text-sm font-mono font-bold text-white truncate">
                        {category.name.toUpperCase()}
                      </div>
                    </div>

                    {/* Level Display */}
                    <div 
                      className="px-2 py-1 text-xs font-mono font-bold border"
                      style={{ 
                        borderColor: isMaxLevel ? '#FFD700' : branchColor,
                        color: isMaxLevel ? '#FFD700' : branchColor,
                        backgroundColor: 'rgba(0,0,0,0.8)'
                      }}
                    >
                      LVL {currentLevel}
                    </div>
                  </div>

                  {/* Progress Bar - Video Game Style */}
                  <div className="ml-11 mb-1">
                    {/* 10 Segmented Bars */}
                    <div className="flex gap-0.5">
                      {Array.from({ length: 10 }, (_, segmentIndex) => {
                        const segmentLevel = segmentIndex + 1;
                        const isCompleted = currentLevel > segmentLevel;
                        const isCurrent = currentLevel === segmentLevel;
                        const isLocked = currentLevel < segmentLevel;

                        let fillPercentage = 0;
                        if (isCompleted) {
                          fillPercentage = 100;
                        } else if (isCurrent) {
                          fillPercentage = progress.percentage;
                        }

                        return (
                          <div
                            key={segmentIndex}
                            className="h-3 flex-1 border border-gray-700 relative overflow-hidden"
                            style={{
                              backgroundColor: 'rgba(0,0,0,0.8)',
                              borderColor: isLocked ? '#374151' : branchColor,
                              minWidth: '8px'
                            }}
                          >
                            {/* Fill */}
                            {fillPercentage > 0 && (
                              <div
                                className="h-full transition-all duration-500"
                                style={{
                                  width: `${fillPercentage}%`,
                                  background: isCompleted 
                                    ? (segmentLevel === 10 ? '#FFD700' : branchColor)
                                    : `linear-gradient(90deg, ${branchColor}, ${branchColor}CC)`,
                                  boxShadow: isCurrent ? `0 0 4px ${branchColor}` : undefined
                                }}
                              />
                            )}

                            {/* Milestone markers */}
                            {(segmentLevel === 5 || segmentLevel === 10) && isCompleted && (
                              <div 
                                className="absolute inset-0 flex items-center justify-center"
                                style={{ color: '#000000' }}
                              >
                                <div className="text-xs">
                                  {segmentLevel === 10 ? '★' : '♦'}
                                </div>
                              </div>
                            )}
                          </div>
                        );
                      })}
                    </div>
                  </div>

                  {/* XP Info */}
                  <div className="ml-11 flex justify-between text-xs font-mono text-gray-400">
                    <span>{category.xp || 0} XP</span>
                    <span>
                      {currentLevel < 10 ? `${progress.current}/${progress.max}` : 'MASTERED'}
                    </span>
                  </div>

                  {/* Hover Tooltip */}
                  {hoveredSkill === category.id && (
                    <motion.div
                      initial={{ opacity: 0, scale: 0.8 }}
                      animate={{ opacity: 1, scale: 1 }}
                      className="absolute right-0 top-0 bg-black border-2 p-3 rounded z-50 whitespace-nowrap"
                      style={{ 
                        borderColor: branchColor,
                        boxShadow: `0 0 15px ${branchColor}60`
                      }}
                    >
                      <div 
                        className="text-sm font-mono font-bold"
                        style={{ color: branchColor }}
                      >
                        {category.name.toUpperCase()} - LEVEL {currentLevel}
                      </div>
                      <div className="text-xs text-gray-300 mt-1">
                        {Math.floor((category.xp || 0) / 60)}h {(category.xp || 0) % 60}m logged
                      </div>
                      {currentLevel < 10 && (
                        <div className="text-xs text-gray-400 mt-1">
                          Next: {progress.max - progress.current} XP to Level {currentLevel + 1}
                        </div>
                      )}
                      {isMaxLevel && (
                        <div className="text-xs text-yellow-400 mt-1">
                          ⭐ BRANCH MASTERED ⭐
                        </div>
                      )}
                    </motion.div>
                  )}
                </motion.div>
              );
            })
          )}
        </div>

        {/* Footer Stats */}
        {sortedCategories.length > 0 && (
          <div className="mt-6 pt-4 border-t border-gray-700">
            <div className="flex justify-between text-xs font-mono text-gray-400">
              <span>
                TOTAL XP: {sortedCategories.reduce((sum, cat) => sum + (cat.xp || 0), 0)}
              </span>
              <span>
                MASTERED: {sortedCategories.filter(cat => getCurrentLevel(cat.xp || 0) === 10).length}
              </span>
            </div>
          </div>
        )}
      </div>
    </motion.div>
  );
};

export default SkillTreeWidget;
