import React from 'react';
import { motion } from 'framer-motion';
import { Crown, Zap, Target, Star } from 'lucide-react';

const HeroCard = ({ player, notes = [] }) => {
  // Calculate XP and level (matching your GameUtilities.java logic)
  const calculateTotalXP = (notes) => {
    let totalXP = 0;

    for (const note of notes) {
      // Base XP per note
      totalXP += 10;

      // Bonus XP for content length
      const wordCount = note.content ? note.content.split(/\s+/).length : 0;
      totalXP += Math.min(Math.floor(wordCount / 10), 50);

      // Bonus XP for using tags
      const tags = note.tagsString ? note.tagsString.split(',').filter(tag => tag.trim()) : [];
      totalXP += tags.length * 5;

      // Bonus XP for longer titles
      if (note.title && note.title.length > 20) {
        totalXP += 5;
      }
    }

    return totalXP;
  };

  const calculateLevelFromXP = (totalXP) => {
    return Math.floor(Math.sqrt(totalXP / 50.0)) + 1;
  };

  const getRankNameForLevel = (level) => {
    switch (level) {
      case 1: return "Novice Scribe";
      case 2: return "Apprentice Writer";
      case 3: return "Skilled Chronicler";
      case 4: return "Expert Documentarian";
      case 5: return "Master Archivist";
      case 6: return "Elite Wordsmith";
      case 7: return "Distinguished Author";
      case 8: return "Legendary Scribe";
      case 9: return "Mythical Chronicler";
      case 10: return "Grandmaster of Words";
      default: return level < 15 ? `Ascended Writer` : `Transcendent Scribe`;
    }
  };

  // Calculate player stats
  const totalXP = calculateTotalXP(notes);
  const currentLevel = calculateLevelFromXP(totalXP);
  const currentLevelBaseXP = Math.pow(currentLevel - 1, 2) * 50;
  const nextLevelXP = Math.pow(currentLevel, 2) * 50;
  const xpInCurrentLevel = totalXP - currentLevelBaseXP;
  const xpNeededForNextLevel = nextLevelXP - currentLevelBaseXP;
  const progressPercentage = Math.floor((xpInCurrentLevel / xpNeededForNextLevel) * 100);

  const playerTitle = getRankNameForLevel(currentLevel);
  const nextLevelTitle = getRankNameForLevel(currentLevel + 1);

  // Generate pixel avatar based on username
  const generatePixelAvatar = (username) => {
    const colors = [
      '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FECA57',
      '#FF9FF3', '#54A0FF', '#5F27CD', '#00D2D3', '#FF9F43'
    ];

    const initial = username ? username.charAt(0).toUpperCase() : 'P';
    const colorIndex = username ? username.length % colors.length : 0;
    const bgColor = colors[colorIndex];

    return { initial, bgColor };
  };

  // Mock player data if no player provided
  const mockPlayer = {
    username: "Jroc_182",
    level: 1,
    experience: 0
  };

  const currentPlayer = player || mockPlayer;
  const avatar = generatePixelAvatar(currentPlayer.username);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-gray-800 border-2 border-gray-600 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-8"
    >
      <div className="flex items-start gap-8 lg:gap-12">
        {/* LARGER Pixel Art Avatar */}
        <div className="flex-shrink-0">
          <div 
            className="w-32 h-32 lg:w-40 lg:h-40 border-4 border-gray-400 shadow-[6px_6px_0px_0px_rgba(0,0,0,1)] flex items-center justify-center relative overflow-hidden"
            style={{ 
              backgroundColor: avatar.bgColor,
              imageRendering: 'pixelated',
              background: `
                linear-gradient(45deg, ${avatar.bgColor} 25%, transparent 25%),
                linear-gradient(-45deg, ${avatar.bgColor} 25%, transparent 25%),
                linear-gradient(45deg, transparent 75%, ${avatar.bgColor} 75%),
                linear-gradient(-45deg, transparent 75%, ${avatar.bgColor} 75%)
              `,
              backgroundSize: '12px 12px',
              backgroundPosition: '0 0, 0 6px, 6px -6px, -6px 0px'
            }}
          >
            {/* Larger pixel character */}
            <div className="relative">
              <span className="text-4xl lg:text-6xl font-bold font-mono text-white drop-shadow-lg">
                {avatar.initial}
              </span>
              {/* Crown for high levels */}
              {currentLevel >= 8 && (
                <Crown size={20} className="absolute -top-4 -right-3 text-yellow-400" />
              )}
              {/* Star effects for legendary levels */}
              {currentLevel >= 10 && (
                <>
                  <Star size={16} className="absolute -top-2 -left-4 text-yellow-300 animate-pulse" />
                  <Star size={14} className="absolute -bottom-2 -right-4 text-yellow-300 animate-pulse" style={{ animationDelay: '0.5s' }} />
                </>
              )}
            </div>

            {/* Level ring around avatar for high levels */}
            {currentLevel >= 5 && (
              <div className="absolute inset-0 border-2 border-yellow-400 opacity-60 animate-pulse" />
            )}
          </div>

          {/* Level Badge - PURPLE instead of yellow */}
          <div className="mt-4 bg-purple-500 text-white px-4 py-2 border-2 border-gray-600 shadow-[4px_4px_0px_0px_rgba(0,0,0,1)] text-center">
            <span className="font-mono font-bold text-lg">LEVEL {currentLevel}</span>
          </div>
        </div>

        {/* Player Info - More spacious */}
        <div className="flex-1 min-w-0">
          <div className="mb-6">
            {/* Player Name - Using mock data */}
            <h2 className="text-3xl lg:text-4xl font-bold font-mono text-white mb-2 break-words">
              {currentPlayer.username}
            </h2>

            {/* Player Title - More prominent */}
            <div className="flex flex-wrap items-center gap-3 mb-4">
              <div className="bg-gradient-to-r from-purple-500 to-blue-500 text-white px-4 py-2 border-2 border-gray-600 shadow-[4px_4px_0px_0px_rgba(0,0,0,1)]">
                <p className="text-lg lg:text-xl font-mono font-bold">
                  {playerTitle}
                </p>
              </div>
              {currentLevel >= 8 && (
                <div className="bg-yellow-400 text-black px-3 py-1 border-2 border-gray-600 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)]">
                  <span className="font-mono font-bold text-sm">ELITE</span>
                </div>
              )}
            </div>
          </div>

          {/* XP Progress Bar - Keep as is */}
          <div className="space-y-3">
            <div className="flex justify-between items-center">
              <span className="text-base font-mono text-gray-300">
                <Zap size={18} className="inline mr-2 text-yellow-400" />
                Experience Points
              </span>
              <span className="text-base font-mono text-white bg-gray-700 px-3 py-1 border border-gray-600">
                {xpInCurrentLevel}/{xpNeededForNextLevel} XP
              </span>
            </div>

            {/* Larger Pixel Art Progress Bar */}
            <div className="relative">
              <div className="w-full h-8 bg-gray-700 border-4 border-gray-500 shadow-[4px_4px_0px_0px_rgba(0,0,0,1)]">
                <motion.div
                  initial={{ width: 0 }}
                  animate={{ width: `${progressPercentage}%` }}
                  transition={{ duration: 1.5, ease: "easeOut" }}
                  className="h-full bg-gradient-to-r from-green-400 to-green-500 relative overflow-hidden"
                  style={{
                    imageRendering: 'pixelated',
                    background: `
                      repeating-linear-gradient(
                        45deg,
                        #4ADE80,
                        #4ADE80 3px,
                        #22C55E 3px,
                        #22C55E 6px
                      )
                    `
                  }}
                >
                  {/* Enhanced pixel texture overlay */}
                  <div className="absolute inset-0 opacity-30"
                    style={{
                      background: `
                        repeating-linear-gradient(
                          0deg,
                          transparent,
                          transparent 2px,
                          rgba(255,255,255,0.1) 2px,
                          rgba(255,255,255,0.1) 4px
                        )
                      `
                    }}
                  />
                  {/* Shine effect */}
                  <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-10 animate-pulse" />
                </motion.div>
              </div>

              {/* Progress percentage - Pixel art style */}
              <div className="absolute inset-0 flex items-center justify-center">
                <div className="bg-gray-900 border-2 border-gray-400 px-3 py-1 shadow-[2px_2px_0px_0px_rgba(0,0,0,1)]">
                  <span className="text-sm font-mono font-bold text-green-400">
                    {progressPercentage}%
                  </span>
                </div>
              </div>
            </div>

            {/* Next Level Info - More detailed */}
            <div className="flex flex-wrap items-center justify-between text-sm gap-2">
              <span className="font-mono text-gray-400 flex items-center">
                <Target size={14} className="inline mr-1" />
                Next Rank: <span className="text-purple-400 ml-1">{nextLevelTitle}</span>
              </span>
              <span className="font-mono text-green-400 bg-gray-800 px-2 py-1 border border-gray-600">
                +{nextLevelXP - totalXP} XP needed
              </span>
            </div>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default HeroCard;
