import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Crown, Star, Trophy, X, ChevronDown, Palette, Award, Camera, Code, Zap, Monitor, 
  Rocket, Users, PenTool, BookOpen, GraduationCap, Microscope, Dumbbell, Music, 
  Briefcase, TrendingUp, Shield, Target, Timer, CheckSquare, Tag, Calendar, 
  FileText, Lightbulb, Coffee, Brain, Heart, Flame, Diamond, Sparkles, Plus, Settings
} from 'lucide-react';
import PixelPageJenn from '../../assets/icons/PixelPageJenn.PNG';
import TaskStats from './TaskStats';
import ProfilePictureSection from './ProfilePictureSection';
import FavoriteBadgesSection from './FavoriteBadgesSection';
import BadgeSelectorModal from './BadgeSelectorModal';
import ProfessionBadge from './ProfessionBadge';
import LogStatsSection from './LogStatsSection';
import { getRankByXP, getNextRank } from '../../data/ranks';
import { useTheme } from '../../contexts/ThemeContext';
import { defaultProfilePics, getSelectedProfilePic, saveProfilePicSelection, getProfilePicById } from '../../data/profilePics';
import { useProfileBadges } from '../../hooks/useProfileBadges';
import { calculateSkillsAndProfession } from '../../services/professionService';

const HeroCard = ({ player, notes = [], tasks = [], taskLists = [] }) => {
  const [showEasterEgg, setShowEasterEgg] = useState(false);
  const [showStyleDropdown, setShowStyleDropdown] = useState(false);
  const [showBadgeSelector, setShowBadgeSelector] = useState(false);
  const [showProfilePicEditor, setShowProfilePicEditor] = useState(false);
  const [selectedBadges, setSelectedBadges] = useState(() => {
    const saved = localStorage.getItem('selectedProfileBadges');
    return saved ? JSON.parse(saved) : [];
  });
  const [selectedProfilePic, setSelectedProfilePic] = useState(() => getSelectedProfilePic());
  const { currentTheme, themes, switchTheme, getThemeColors } = useTheme();

  const themeColors = getThemeColors();

  // Convert icon string to actual Lucide icon component
  const getIconComponent = (iconString) => {
    const iconMap = {
      'BookOpen': BookOpen,
      'PenTool': PenTool,
      'CheckSquare': CheckSquare,
      'Target': Target,
      'Timer': Timer,
      'Tag': Tag,
      'Calendar': Calendar,
      'FileText': FileText,
      'Code': Code,
      'Monitor': Monitor,
      'Lightbulb': Lightbulb,
      'Coffee': Coffee,
      'Brain': Brain,
      'Heart': Heart,
      'Flame': Flame,
      'Star': Star,
      'Award': Award,
      'Trophy': Trophy,
      'Crown': Crown,
      'Shield': Shield,
      'Diamond': Diamond,
      'Sparkles': Sparkles,
      'Zap': Zap,
      'Rocket': Rocket
    };
    return iconMap[iconString] || Star; // Default to Star if icon not found
  };

  // Badge management functions
  const getUnlockedAchievements = () => {
    const unlockedAchievements = backendAchievementService.getUnlockedAchievements();

    // Debug log to see what's happening with achievements
    console.log('ðŸ† Badge Selector Debug (PROFILE):', {
      unlockedAchievements: unlockedAchievements.map(a => ({ id: a.id, name: a.name, tier: a.tier })),
      unlockedCount: unlockedAchievements.length,
      allAchievementsCount: allAchievements.length,
      backendServiceStats: backendAchievementService.getStats()
    });

    // Return the unlocked achievements directly (they're already full objects now)
    return unlockedAchievements;
  };

  const toggleBadgeSelection = (achievementId) => {
    setSelectedBadges(prev => {
      let newSelection;
      if (prev.includes(achievementId)) {
        // Remove badge
        newSelection = prev.filter(id => id !== achievementId);
      } else if (prev.length < 3) {
        // Add badge (max 3)
        newSelection = [...prev, achievementId];
      } else {
        // Replace oldest badge
        newSelection = [...prev.slice(1), achievementId];
      }
      localStorage.setItem('selectedProfileBadges', JSON.stringify(newSelection));
      return newSelection;
    });
  };

  // Profile pic management
  const changeProfilePic = (picId) => {
    setSelectedProfilePic(picId);
    saveProfilePicSelection(picId);
    setShowProfilePicEditor(false);
  };

  const getCurrentProfilePicData = () => {
    return getProfilePicById(selectedProfilePic);
  };

  // Get highest skill from tracker data
  const getHighestSkill = () => {
    try {
      const categories = JSON.parse(localStorage.getItem('focusCategories') || '[]');
      if (categories.length === 0) {
        console.log('ðŸ” No focus categories found in localStorage');
        return null;
      }

      // Debug log the raw categories data
      console.log('ðŸ” Raw focus categories from localStorage (DETAILED):', {
        categories,
        categoriesStringified: JSON.stringify(categories, null, 2),
        categoriesWithXP: categories.map(c => ({ name: c.name, xp: c.xp || 0 }))
      });

      // XP calculation for levels (EXACT same as HexagonalSkillTree and TimeManagementMatrix)
      const calculateXPRequired = (level) => {
        const base = 60; // 1 hour for level 1
        return Math.floor(base * Math.pow(1.4, level - 1));
      };

      // Get current level from total XP (EXACT same as HexagonalSkillTree and TimeManagementMatrix)
      const getCurrentLevel = (totalXP) => {
        let level = 1;
        let xpNeeded = 0;

        while (level <= 10) {
          xpNeeded += calculateXPRequired(level);
          if (totalXP < xpNeeded) break;
          level++;
        }

        return Math.min(level, 10);
      };

      // Get level title (same as TimeManagementMatrix)
      const getLevelTitle = (categoryName, level) => {
        const titles = {
          scholar: ['STUDENT', 'LEARNER', 'THINKER', 'SCHOLAR', 'RESEARCHER', 'EXPERT', 'SAGE', 'PROFESSOR', 'GENIUS', 'MASTERMIND'],
          profession: ['INTERN', 'TRAINEE', 'WORKER', 'SPECIALIST', 'VETERAN', 'ELITE', 'MANAGER', 'DIRECTOR', 'EXECUTIVE', 'LEGEND'],
          artisan: ['DREAMER', 'ARTIST', 'MAKER', 'CREATOR', 'DESIGNER', 'INNOVATOR', 'VISIONARY', 'PIONEER', 'VIRTUOSO', 'GODLIKE'],
          scribe: ['NOVICE', 'WRITER', 'AUTHOR', 'STORYTELLER', 'WORDSMITH', 'POET', 'NOVELIST', 'MASTER', 'LAUREATE', 'IMMORTAL'],
          programming: ['NOOB', 'CODER', 'PROGRAMMER', 'DEVELOPER', 'ENGINEER', 'ARCHITECT', 'GURU', 'NINJA', 'WIZARD', 'HACKER'],
          literacy: ['READER', 'BROWSER', 'BOOKWORM', 'SCHOLAR', 'CRITIC', 'ANALYST', 'EXPERT', 'CURATOR', 'SAGE', 'ORACLE'],
          strategist: ['PLANNER', 'ORGANIZER', 'TACTICIAN', 'STRATEGIST', 'COORDINATOR', 'MASTERMIND', 'ARCHITECT', 'VISIONARY', 'COMMANDER', 'GRANDMASTER'],
          mindfulness: ['SEEKER', 'RELAXER', 'MEDITATOR', 'PEACEFUL', 'CENTERED', 'BALANCED', 'SERENE', 'ENLIGHTENED', 'TRANSCENDENT', 'ZEN MASTER'],
          knowledge: ['CURIOUS', 'SEARCHER', 'INVESTIGATOR', 'RESEARCHER', 'EXPLORER', 'DISCOVERER', 'SCHOLAR', 'EXPERT', 'AUTHORITY', 'OMNISCIENT']
        };

        const categoryKey = categoryName.toLowerCase();
        return titles[categoryKey]?.[level - 1] || `Level ${level}`;
      };

      // Define skill professions based on level ranges - using Lucide React components
      const getProfessionForSkill = (skillName, level) => {
        const skillProfessions = {
          'Programming': {
            icon: Code,
            name: getLevelTitle('programming', level) // Use lowercase key for title lookup
          },
          'Scribe': {
            icon: PenTool,
            name: getLevelTitle('scribe', level)
          },
          'Artisan': {
            icon: Palette,
            name: getLevelTitle('artisan', level)
          },
          'Scholar': {
            icon: BookOpen,
            name: getLevelTitle('scholar', level)
          },
          'Profession': {
            icon: Briefcase,
            name: getLevelTitle('profession', level)
          },
          'Literacy': {
            icon: BookOpen,
            name: getLevelTitle('literacy', level)
          },
          'Strategist': {
            icon: Target,
            name: getLevelTitle('strategist', level)
          },
          'Mindfulness': {
            icon: Heart,
            name: getLevelTitle('mindfulness', level)
          },
          'Knowledge': {
            icon: Lightbulb,
            name: getLevelTitle('knowledge', level)
          }
        };

        // Find the appropriate profession for the skill with case-insensitive matching
        let profession = skillProfessions[skillName];

        // If not found, try case-insensitive lookup
        if (!profession) {
          const skillKey = Object.keys(skillProfessions).find(key => 
            key.toLowerCase() === skillName.toLowerCase()
          );
          profession = skillKey ? skillProfessions[skillKey] : null;
        }

        // Fallback to Scholar if still not found
        if (!profession) {
          profession = skillProfessions['Scholar'];
        }

        return profession;
      };

      // Calculate level for each skill using the official system
      const skillsWithLevels = categories.map(category => {
        const currentXP = category.xp || 0;
        const level = getCurrentLevel(currentXP);
        const profession = getProfessionForSkill(category.name, level);

        // Debug log the skill calculation
        console.log(`ðŸŽ¯ Skill Debug - ${category.name}: XP=${currentXP}, Level=${level}, Profession=${profession.name}`);

        return {
          ...category,
          level,
          profession: {
            name: profession.name,
            icon: profession.icon
          }
        };
      });

      // Find the highest level skill
      const highestSkill = skillsWithLevels.reduce((highest, current) => 
        current.level > highest.level ? current : highest
      );

      console.log('ðŸ† Highest skill determined:', {
        name: highestSkill.name,
        level: highestSkill.level,
        profession: highestSkill.profession.name,
        xp: highestSkill.xp
      });

      return highestSkill.level > 1 ? highestSkill : null;
    } catch (error) {
      console.error('Error getting highest skill:', error);
      return null;
    }
  };

  // Calculate total XP from all sources
  const calculateTotalXP = () => {
    let totalXP = 0;
    let breakdown = { notes: 0, achievements: 0, focus: 0, missions: 0 };

    // 1. Achievement XP (from unlocked achievements)
    const achievementXP = achievementStats.totalXP || 0;
    totalXP += achievementXP;
    breakdown.achievements = achievementXP;

    // 2. Notes XP
    let notesXP = 0;
    for (const note of notes) {
      // Base XP per note
      notesXP += 10;

      // Bonus XP for content length
      const wordCount = note.content ? note.content.split(/\s+/).length : 0;
      notesXP += Math.min(Math.floor(wordCount / 10), 50);

      // Bonus XP for using tags
      const tags = note.tagsString ? note.tagsString.split(',').filter(tag => tag.trim()) : [];
      notesXP += tags.length * 5;

      // Bonus XP for longer titles
      if (note.title && note.title.length > 20) {
        notesXP += 5;
      }
    }
    totalXP += notesXP;
    breakdown.notes = notesXP;

    // 3. Focus Timer XP (from tracking sessions)
    let focusXP = 0;
    try {
      const categories = JSON.parse(localStorage.getItem('focusCategories') || '[]');
      focusXP = categories.reduce((sum, category) => sum + (category.xp || 0), 0);

      // Debug check for focus XP inflation
      if (focusXP > 200) {
        console.warn('ðŸš¨ FOCUS XP IS TOO HIGH!', focusXP, 'Categories:', categories);
        console.warn('âš ï¸ TEMPORARILY CAPPING FOCUS XP FROM', focusXP, 'TO 100');
        focusXP = 100; // Temporary cap until we can fix the focus timer XP system
      }

      totalXP += focusXP;
    } catch (error) {
      console.error('Error calculating focus XP:', error);
    }
    breakdown.focus = focusXP;

    // 4. Mission Completion XP (2 XP per completed task)
    const completedTasks = tasks.filter(task => task.completed);
    const missionXP = completedTasks.length * 2;
    totalXP += missionXP;
    breakdown.missions = missionXP;

    // DEBUGGING: Log detailed breakdown
    console.log('ðŸ› DETAILED XP BREAKDOWN:');
    console.log('Notes XP:', breakdown.notes, '(from', notes.length, 'notes)');
