                              )}

                              {/* Shine effect on hover */}
                              <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/10 to-transparent opacity-0 group-hover:opacity-100 -skew-x-12 translate-x-[-100%] group-hover:translate-x-[200%] transition-all duration-700" />

                              <div className="relative z-10 flex items-center gap-3">
                                <div className="relative">
                                  {/* Use actual icon component instead of emoji string */}
                                  {React.createElement(getIconComponent(achievement.icon), { 
                                    size: 28, 
                                    className: "drop-shadow-lg",
                                    style: { color: 'white' }
                                  })}
                                  {/* Badge glow around icon */}
                                  {selectedBadges.includes(achievement.id) && (
                                    <div 
                                      className="absolute inset-0 rounded-full blur-sm opacity-60"
                                      style={{
                                        background: `radial-gradient(circle, ${tier.color}80 0%, transparent 60%)`
                                      }}
                                    />
                                  )}
                                </div>
                                <div className="flex-1">
                                  <div className="font-mono font-bold text-white text-sm mb-1">
                                    {achievement.name}
                                  </div>
                                  <div className="font-mono text-xs text-gray-400 mb-1">
                                    {achievement.description}
                                  </div>
                                  <div className="flex items-center gap-2">
                                    <div 
                                      className="px-2 py-1 text-xs font-mono font-bold rounded"
                                      style={{ 
                                        backgroundColor: `${tier.color}20`,
                                        color: tier.color,
                                        border: `1px solid ${tier.color}40`
                                      }}
                                    >
                                      {tier.name.toUpperCase()}
                                    </div>
                                    <div className="text-xs font-mono text-gray-500">
                                      +{achievement.xpReward || 0} XP
                                    </div>
                                  </div>
                                </div>
                                {selectedBadges.includes(achievement.id) && (
                                  <div className="relative">
                                    <Star size={20} style={{ color: tier.color }} className="drop-shadow-lg" />
                                    <div 
                                      className="absolute inset-0 rounded-full blur-sm opacity-60 animate-pulse"
                                      style={{
                                        background: `radial-gradient(circle, ${tier.color}80 0%, transparent 60%)`
                                      }}
                                    />
                                  </div>
                                )}
                              </div>

                              {/* Cool corner accent for selected badges */}
                              {selectedBadges.includes(achievement.id) && (
                                <div 
                                  className="absolute top-0 right-0 w-0 h-0 border-l-8 border-b-8 border-l-transparent"
                                  style={{ borderBottomColor: tier.color }}
                                />
                              )}
                            </motion.button>
                          ))}
                        </div>
                      </div>
                    );
                  })}

                  {getUnlockedAchievements().length === 0 && (
                    <div className="text-center py-8">
                      <div className="relative mb-4">
                        <Trophy size={64} className="text-gray-500 mx-auto" />
                        <div className="absolute inset-0 flex items-center justify-center">
                          <div className="w-16 h-16 border-4 border-gray-600 border-t-transparent rounded-full animate-spin opacity-30" />
                        </div>
                      </div>
                      <div className="text-gray-400 font-mono text-xl font-bold mb-2">
                        NO ACHIEVEMENTS UNLOCKED
                      </div>
                      <div className="text-gray-500 font-mono text-sm">
                        Complete tasks, write notes, and use the focus timer to unlock epic achievement badges!
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Profile Pic Editor Modal */}
      <AnimatePresence>
        {showProfilePicEditor && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
            onClick={() => setShowProfilePicEditor(false)}
          >
            <motion.div
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              className="p-6 max-w-xl mx-4 relative"
              style={{
                backgroundColor: themeColors.backgroundColor,
                border: `2px solid ${themeColors.controlColor}`,
                borderRadius: themeColors.borderRadius,
                boxShadow: currentTheme === 'default' 
                  ? '0 0 30px rgba(255, 255, 255, 0.4), 8px 8px 0px 0px rgba(0,0,0,1)' 
                  : `0 0 20px ${themeColors.controlColor}30, 8px 8px 0px 0px rgba(0,0,0,1)`
              }}
              onClick={(e) => e.stopPropagation()}
            >
              {/* Animated border */}
              <div className="absolute inset-0 opacity-50 animate-pulse pointer-events-none" 
                   style={{ 
                     border: `2px solid ${themeColors.controlColor}`,
                     borderRadius: themeColors.borderRadius
                   }} />

              {/* Close button */}
              <button
                onClick={() => setShowProfilePicEditor(false)}
                className="absolute top-4 right-4 text-gray-400 hover:text-white transition-colors"
              >
                <X size={20} />
              </button>

              {/* Content */}
              <div>
                <h2 className="text-2xl font-mono font-bold text-white mb-2">
                  CHOOSE PROFILE PICTURE
                </h2>

                <p className="font-mono text-sm mb-6" style={{ color: themeColors.controlColor }}>
                  Select a pixel art avatar to represent your character
                </p>

                <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                  {defaultProfilePics.map(pic => (
                    <button
                      key={pic.id}
                      onClick={() => changeProfilePic(pic.id)}
                      className={`p-4 transition-all duration-200 text-center ${
                        selectedProfilePic === pic.id
                          ? 'bg-gray-700 border-opacity-100'
                          : 'hover:bg-gray-800'
                      }`}
                      style={{
                        backgroundColor: selectedProfilePic === pic.id ? themeColors.backgroundColor : 'transparent',
                        border: `1px solid ${selectedProfilePic === pic.id ? themeColors.controlColor : '#4B5563'}`,
                        borderRadius: themeColors.borderRadius
                      }}
                    >
                      <div className="w-16 h-16 bg-gray-600 border border-gray-500 mx-auto mb-2 flex items-center justify-center"
                           style={{ borderRadius: themeColors.borderRadius }}>
                        {/* Placeholder for now - in a real implementation, this would show the actual image */}
                        <span className="text-2xl">ðŸŽ®</span>
                      </div>
                      <div className="font-mono text-xs font-bold text-white">
                        {pic.name}
                      </div>
                      <div className="font-mono text-xs text-gray-400">
                        {pic.description}
                      </div>
                      {selectedProfilePic === pic.id && (
                        <Star size={16} className="mx-auto mt-1" style={{ color: themeColors.controlColor }} />
                      )}
                    </button>
                  ))}
                </div>

                <div className="mt-6 p-4"
                     style={{
                       backgroundColor: themeColors.backgroundColor,
                       border: `1px solid ${themeColors.borderColor}`,
                       borderRadius: themeColors.borderRadius
                     }}>
                  <div className="font-mono text-xs text-gray-400 mb-2">PREVIEW:</div>
                  <div className="flex items-center gap-3">
                    <div className="w-12 h-12 bg-gray-600 border border-gray-500 flex items-center justify-center"
                         style={{ borderRadius: themeColors.borderRadius }}>
                      <span className="text-lg">ðŸŽ®</span>
                    </div>
                    <div>
                      <div className="font-mono text-sm font-bold text-white">
                        {getCurrentProfilePicData().name}
                      </div>
                      <div className="font-mono text-xs text-gray-400">
                        {getCurrentProfilePicData().description}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Add this style block to your component */}
      <style jsx>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @keyframes shimmer-slide {
          0% {
            background-position: -200% 0%;
          }
          100% {
            background-position: 200% 0%;
          }
        }

        @keyframes shimmer-sweep {
          0% {
            transform: translateX(-100%);
          }
          100% {
            transform: translateX(200%);
          }
        }

        .hover\\:scale-102:hover {
          transform: scale(1.02);
        }

        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-10px); }
        }

        .animate-float {
          animation: float 3s ease-in-out infinite;
        }

        @keyframes glow-pulse {
          0%, 100% { 
            box-shadow: 0 0 15px rgba(168, 85, 247, 0.5), 4px 4px 0px 0px rgba(0,0,0,1);
            transform: scale(1);
          }
          50% { 
            box-shadow: 0 0 25px rgba(168, 85, 247, 0.8), 4px 4px 0px 0px rgba(0,0,0,1);
            transform: scale(1.05);
          }
        }

        .animate-glow-pulse {
          animation: glow-pulse 2s ease-in-out infinite;
        }

        @keyframes badge-entrance {
          0% {
            transform: scale(0) rotateY(180deg);
            opacity: 0;
          }
          50% {
            transform: scale(1.1) rotateY(90deg);
            opacity: 0.8;
          }
          100% {
            transform: scale(1) rotateY(0deg);
            opacity: 1;
          }
        }

        .animate-badge-entrance {
          animation: badge-entrance 0.8s cubic-bezier(0.34, 1.56, 0.64, 1);
        }
      `}</style>
    </div>
  );
};

export default HeroCard;
