// components/ui/GlitterFillBar.jsx
import React, { useMemo } from "react";
import { motion } from "framer-motion";

export default function GlitterFillBar({
  progress = 66,         // 0-100
  height = 30,           // px
  radius = 9999,         // pill by default
  glitterCount = 60,     // number of sparkles
  className = "",
}) {
  const clamped = Math.max(0, Math.min(100, progress));

  // Stable-ish positions per render (not re-randomized unless count changes)
  const sparkles = useMemo(
    () =>
      Array.from({ length: glitterCount }, (_, i) => {
        // spread sparkles across 0..100% width & 0..100% height
        const left = Math.random() * 100;
        const top = Math.random() * 100;
        // random size & slight color shift
        const size = 2 + Math.random() * 3; // 2-5px
        const hue = 320 + Math.random() * 30; // pink-ish range
        const alpha = 0.7 + Math.random() * 0.3;
        const delay = Math.random() * 1.2;
        const dur = 1.2 + Math.random() * 1.6;
        return { id: i, left, top, size, hue, alpha, delay, dur };
      }),
    [glitterCount]
  );

  return (
    <div
      className={`relative w-full overflow-hidden border border-pink-300/40 bg-pink-200/10 ${className}`}
      style={{ height, borderRadius: radius, boxShadow: "inset 0 0 10px rgba(255, 182, 193, .15)" }}
    >
      {/* Background candy gradient for the unfilled track */}
      <div
        className="absolute inset-0"
        aria-hidden
        style={{
          background:
            "linear-gradient(90deg, rgba(255,255,255,.08), rgba(255,192,203,.15))",
        }}
      />

      {/* Filled area */}
      <div
        className="h-full relative overflow-hidden"
        style={{
          width: `${clamped}%`,
          borderRadius: radius,
          transition: "width 300ms ease",
          backgroundImage:
            "linear-gradient(90deg, #fbcfe8 0%, #f472b6 35%, #e879f9 70%, #d946ef 100%)",
          boxShadow:
            "inset 0 0 18px rgba(255,255,255,.25), inset 0 -8px 14px rgba(236,72,153,.28)",
        }}
      >
        {/* Subtle moving shimmer in the fill */}
        <motion.div
          className="absolute inset-0 opacity-30 mix-blend-screen"
          style={{
            backgroundImage:
              "repeating-linear-gradient(100deg, rgba(255,255,255,.25) 0 2px, transparent 2px 8px)",
            backgroundSize: "200% 100%",
          }}
          animate={{ backgroundPositionX: ["0%", "100%"] }}
          transition={{ duration: 3, repeat: Infinity, ease: "linear" }}
        />

        {/* Glitter particles */}
        {sparkles.map((s) => (
          <motion.span
            key={s.id}
            className="absolute rounded-full pointer-events-none"
            style={{
              left: `${s.left}%`,
              top: `${s.top}%`,
              width: s.size,
              height: s.size,
              background: `radial-gradient(circle, hsla(${s.hue}, 95%, 80%, ${s.alpha}) 0%, hsla(${s.hue}, 95%, 70%, 0) 70%)`,
              filter: "brightness(1.3)",
            }}
            initial={{ opacity: 0, y: 0 }}
            animate={{ opacity: [0, 1, 0], y: [-0.5, 0.5, -0.5] }}
            transition={{ duration: s.dur, delay: s.delay, repeat: Infinity, ease: "easeInOut" }}
          />
        ))}
      </div>
    </div>
  );
}
