import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { BarChart3, TrendingUp, Clock, AlertTriangle, Target, Calendar, Shield } from 'lucide-react';

const TaskInsights = ({ tasks, taskLists }) => {
  const [insights, setInsights] = useState({
    totalTasks: 0,
    completedTasks: 0,
    overdueTasks: 0,
    dueSoonTasks: 0,
    completionRate: 0,
    productivityTrend: 'stable',
    topPriorities: [],
    projectProgress: []
  });

  useEffect(() => {
    calculateInsights();
  }, [tasks, taskLists]);

  const calculateInsights = () => {
    if (!tasks || tasks.length === 0) {
      setInsights({
        totalTasks: 0,
        completedTasks: 0,
        overdueTasks: 0,
        dueSoonTasks: 0,
        completionRate: 0,
        productivityTrend: 'stable',
        topPriorities: [],
        projectProgress: []
      });
      return;
    }

    const now = new Date();
    const oneWeekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);

    // Basic stats
    const totalTasks = tasks.length;
    const completedTasks = tasks.filter(t => t.completed).length;
    const overdueTasks = tasks.filter(t => t.overdue && !t.completed).length;
    const dueSoonTasks = tasks.filter(t => t.dueSoon && !t.completed).length;
    const completionRate = totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;

    // Productivity trend (completed tasks this week vs last week)
    const thisWeekCompleted = tasks.filter(t => 
      t.completed && t.updatedAt && new Date(t.updatedAt) >= oneWeekAgo
    ).length;

    const lastWeekCompleted = tasks.filter(t => 
      t.completed && t.updatedAt && 
      new Date(t.updatedAt) >= new Date(oneWeekAgo.getTime() - 7 * 24 * 60 * 60 * 1000) &&
      new Date(t.updatedAt) < oneWeekAgo
    ).length;

    let productivityTrend = 'stable';
    if (thisWeekCompleted > lastWeekCompleted) productivityTrend = 'up';
    else if (thisWeekCompleted < lastWeekCompleted) productivityTrend = 'down';

    // Top priorities (incomplete high priority tasks)
    const topPriorities = tasks
      .filter(t => !t.completed && t.priority === 'high')
      .slice(0, 3);

    // Project progress
    const projectProgress = [];

    if (taskLists && taskLists.length > 0) {
      taskLists.forEach(list => {
        const listTasks = tasks.filter(t => t.taskListId === list.id);
        const completed = listTasks.filter(t => t.completed).length;
        const total = listTasks.length;
        const progress = total > 0 ? Math.round((completed / total) * 100) : 0;

        if (total > 0) {
          projectProgress.push({
            name: list.name,
            color: list.color || '#6366F1',
            completed,
            total,
            progress
          });
        }
      });
    }

    // General tasks progress
    const generalTasks = tasks.filter(t => !t.taskListId);
    const generalCompleted = generalTasks.filter(t => t.completed).length;
    if (generalTasks.length > 0) {
      projectProgress.unshift({
        name: 'General Missions',
        color: '#6366F1',
        completed: generalCompleted,
        total: generalTasks.length,
        progress: Math.round((generalCompleted / generalTasks.length) * 100)
      });
    }

    setInsights({
      totalTasks,
      completedTasks,
      overdueTasks,
      dueSoonTasks,
      completionRate,
      productivityTrend,
      topPriorities,
      projectProgress
    });
  };

  const getTrendIcon = (trend) => {
    switch (trend) {
      case 'up': return <TrendingUp className="text-green-400" size={16} />;
      case 'down': return <TrendingUp className="text-red-400 rotate-180" size={16} />;
      default: return <TrendingUp className="text-gray-400" size={16} />;
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-gray-800 border-2 border-indigo-500 shadow-[8px_8px_0px_0px_rgba(0,0,0,1)] p-6 relative space-y-6"
      style={{
        boxShadow: '0 0 20px rgba(59, 130, 246, 0.3), 8px 8px 0px 0px rgba(0,0,0,1)'
      }}
    >
      {/* Animated Border Overlay */}
      <div className="absolute inset-0 border-2 border-indigo-500 opacity-30 animate-pulse pointer-events-none" />

      {/* Header */}
      <h3 className="text-xl font-mono font-bold text-white flex items-center relative z-10">
        <div className="w-4 h-4 bg-indigo-500 mr-3" />
        MISSION INSIGHT DASHBOARD
      </h3>

      {/* Key Metrics Grid */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 relative z-10">
        {/* Total Missions - Subtle Blue to Indigo */}
        <div className="bg-gray-900 border-2 border-indigo-400 p-4 relative overflow-hidden" 
             style={{ boxShadow: '0 0 10px rgba(96, 165, 250, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)' }}>
          {/* Subtle gradient overlay */}
          <div className="absolute inset-0 bg-gradient-to-br from-blue-500/20 to-indigo-500/30 pointer-events-none" />
          <div className="relative z-10">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-xs font-mono text-indigo-300 font-bold">TOTAL MISSIONS</div>
                <div className="text-2xl font-mono font-bold text-white">{insights.totalTasks}</div>
              </div>
              <Target className="text-indigo-400" size={20} />
            </div>
          </div>
        </div>

        {/* Mission Success Rate - Subtle Indigo to Purple */}
        <div className="bg-gray-900 border-2 border-indigo-300 p-4 relative overflow-hidden" 
             style={{ boxShadow: '0 0 10px rgba(129, 140, 248, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)' }}>
          <div className="absolute inset-0 bg-gradient-to-br from-indigo-500/20 to-purple-500/25 pointer-events-none" />
          <div className="relative z-10">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-xs font-mono text-indigo-300 font-bold">SUCCESS RATE</div>
                <div className="text-2xl font-mono font-bold text-white">{insights.completionRate}%</div>
              </div>
              {getTrendIcon(insights.productivityTrend)}
            </div>
          </div>
        </div>

        {/* Critical Alerts - Subtle Purple to Dark Indigo */}
        <div className="bg-gray-900 border-2 border-purple-400 p-4 relative overflow-hidden" 
             style={{ boxShadow: '0 0 10px rgba(147, 51, 234, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)' }}>
          <div className="absolute inset-0 bg-gradient-to-br from-purple-600/25 to-indigo-700/30 pointer-events-none" />
          <div className="relative z-10">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-xs font-mono text-purple-300 font-bold">OVERDUE</div>
                <div className="text-2xl font-mono font-bold text-white">{insights.overdueTasks}</div>
              </div>
              <AlertTriangle className="text-purple-300" size={20} />
            </div>
          </div>
        </div>

        {/* Urgent Missions - Subtle Deep Indigo */}
        <div className="bg-gray-900 border-2 border-indigo-500 p-4 relative overflow-hidden" 
             style={{ boxShadow: '0 0 10px rgba(99, 102, 241, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)' }}>
          <div className="absolute inset-0 bg-gradient-to-br from-indigo-500/20 to-indigo-800/25 pointer-events-none" />
          <div className="relative z-10">
            <div className="flex items-center justify-between">
              <div>
                <div className="text-xs font-mono text-indigo-300 font-bold">URGENT</div>
                <div className="text-2xl font-mono font-bold text-white">{insights.dueSoonTasks}</div>
              </div>
              <Clock className="text-indigo-300" size={20} />
            </div>
          </div>
        </div>
      </div>

      {/* Critical Priority Missions - Subtle Gradient */}
      {insights.topPriorities.length > 0 && (
        <div className="bg-gray-900 border-2 border-purple-400 p-4 relative z-10 overflow-hidden" 
             style={{ boxShadow: '0 0 15px rgba(147, 51, 234, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)' }}>
          <div className="absolute inset-0 bg-gradient-to-r from-purple-600/15 to-indigo-700/20 pointer-events-none" />
          <div className="relative z-10">
            <h4 className="text-sm font-mono font-bold text-purple-300 mb-3 flex items-center">
              <AlertTriangle className="mr-2" size={16} />
              CRITICAL PRIORITY MISSIONS
            </h4>
            <div className="space-y-2">
              {insights.topPriorities.map((task, index) => (
                <div key={task.id} className="flex items-center gap-3 text-sm p-2 bg-gray-800 border border-purple-400/50">
                  <AlertTriangle className="text-purple-300" size={12} />
                  <span className="font-mono text-white truncate flex-1">{task.title}</span>
                  {task.dueDate && (
                    <span className="text-purple-300 font-mono text-xs">
                      <Calendar size={10} className="inline mr-1" />
                      {new Date(task.dueDate).toLocaleDateString()}
                    </span>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Operation Progress - Subtle Gradient */}
      {insights.projectProgress.length > 0 && (
        <div className="bg-gray-900 border-2 border-indigo-400 p-4 relative z-10 overflow-hidden" 
             style={{ boxShadow: '0 0 15px rgba(99, 102, 241, 0.2), 2px 2px 0px 0px rgba(0,0,0,1)' }}>
          <div className="absolute inset-0 bg-gradient-to-r from-indigo-500/15 to-blue-600/20 pointer-events-none" />
          <div className="relative z-10">
            <h4 className="text-sm font-mono font-bold text-indigo-400 mb-4 flex items-center">
              <Shield className="mr-2" size={16} />
              OPERATION PROGRESS STATUS
            </h4>
            <div className="space-y-3">
              {insights.projectProgress.slice(0, 4).map((project, index) => (
                <div key={index} className="space-y-2">
                  <div className="flex items-center justify-between text-sm">
                    <div className="flex items-center gap-3">
                      <div 
                        className="w-3 h-3 border-2 border-gray-600"
                        style={{ backgroundColor: project.color }}
                      />
                      <span className="font-mono text-white truncate">{project.name}</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <span className="font-mono text-indigo-400 text-xs">
                        {project.completed}/{project.total} COMPLETE
                      </span>
                      <span className="font-mono text-indigo-400 text-xs font-bold">
                        [{project.progress}%]
                      </span>
                    </div>
                  </div>
                  <div className="w-full bg-gray-700 h-2 border border-gray-600 shadow-inner">
                    <div 
                      className="h-full transition-all duration-700 ease-out border-r"
                      style={{ 
                        width: `${project.progress}%`, 
                        backgroundColor: project.color,
                        borderRightColor: project.color,
                        boxShadow: `0 0 5px ${project.color}50`
                      }}
                    />
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </motion.div>
  );
};

export default TaskInsights;
